import React, { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Separator } from "@/components/ui/separator";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { PaymentProcessor } from "@/components/PaymentProcessor";
import { supabase } from "@/integrations/supabase/client";
import { 
  CheckCircle, 
  AlertCircle, 
  User, 
  Mail, 
  Phone, 
  CreditCard,
  Loader2,
  Shield,
  Lock
} from "lucide-react";

interface PurchaseFlowProps {
  planId: string;
  planData: {
    title: string;
    description: string;
    entry_value: number;
    contemplation_value: number;
  };
  influencerCode?: string;
}

interface FormData {
  full_name: string;
  email: string;
  phone: string;
  cpf: string;
}

interface ValidationErrors {
  [key: string]: string;
}

export function EnhancedPurchaseFlow({ planId, planData, influencerCode }: PurchaseFlowProps) {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState<FormData>({
    full_name: "",
    email: "",
    phone: "",
    cpf: "",
  });
  const [errors, setErrors] = useState<ValidationErrors>({});
  const [loading, setLoading] = useState(false);
  const [userId, setUserId] = useState<string | null>(null);
  const { toast } = useToast();

  const validateField = (name: string, value: string): string => {
    switch (name) {
      case "full_name":
        if (value.length < 2) return "Nome deve ter pelo menos 2 caracteres";
        if (!/^[a-zA-Z√Ä-√ø\s]+$/.test(value)) return "Nome deve conter apenas letras";
        return "";

      case "email":
        if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value)) return "Email inv√°lido";
        return "";

      case "phone":
        const phoneRegex = /^\(\d{2}\)\s\d{4,5}-\d{4}$/;
        if (!phoneRegex.test(value)) return "Formato: (11) 99999-9999";
        return "";

      case "cpf":
        const cpfRegex = /^\d{3}\.\d{3}\.\d{3}-\d{2}$/;
        if (!cpfRegex.test(value)) return "Formato: 000.000.000-00";
        if (!validateCPF(value)) return "CPF inv√°lido";
        return "";

      default:
        return "";
    }
  };

  const validateCPF = (cpf: string): boolean => {
    const numbers = cpf.replace(/[^\d]/g, "");
    if (numbers.length !== 11 || /^(\d)\1{10}$/.test(numbers)) return false;

    const digits = numbers.split("").map(Number);
    const checkDigit = (slice: number) => {
      const sum = digits.slice(0, slice).reduce((acc, digit, i) => acc + digit * (slice + 1 - i), 0);
      const remainder = sum % 11;
      return remainder < 2 ? 0 : 11 - remainder;
    };

    return checkDigit(9) === digits[9] && checkDigit(10) === digits[10];
  };

  const formatField = (name: string, value: string): string => {
    switch (name) {
      case "phone":
        const phoneNumbers = value.replace(/\D/g, "");
        if (phoneNumbers.length <= 10) {
          return phoneNumbers.replace(/(\d{2})(\d{4})(\d{4})/, "($1) $2-$3");
        }
        return phoneNumbers.replace(/(\d{2})(\d{5})(\d{4})/, "($1) $2-$3");

      case "cpf":
        const cpfNumbers = value.replace(/\D/g, "");
        return cpfNumbers.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, "$1.$2.$3-$4");

      default:
        return value;
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    const formattedValue = formatField(name, value);

    setFormData(prev => ({ ...prev, [name]: formattedValue }));

    // Real-time validation
    const error = validateField(name, formattedValue);
    setErrors(prev => ({ ...prev, [name]: error }));
  };

  const validateForm = (): boolean => {
    const newErrors: ValidationErrors = {};

    Object.keys(formData).forEach(key => {
      const error = validateField(key, formData[key as keyof FormData]);
      if (error) newErrors[key] = error;
    });

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmitUserData = async () => {
    if (!validateForm()) {
      toast({
        title: "Dados inv√°lidos",
        description: "Por favor, corrija os erros nos campos destacados",
        variant: "destructive",
      });
      return;
    }

    setLoading(true);
    try {
      // Register user and proceed to payment
      const { data, error } = await supabase.auth.signUp({
        email: formData.email,
        password: Math.random().toString(36).slice(-8), // Temporary password
        options: {
          data: {
            full_name: formData.full_name,
            phone: formData.phone,
            cpf: formData.cpf,
          }
        }
      });

      if (error) throw error;

      if (data.user) {
        setUserId(data.user.id);
        setStep(2);

        toast({
          title: "Dados confirmados!",
          description: "Agora escolha a forma de pagamento",
        });
      }
    } catch (error) {
      console.error("Registration error:", error);
      toast({
        title: "Erro no cadastro",
        description: error instanceof Error ? error.message : "Erro desconhecido",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handlePaymentSuccess = (paymentData: any) => {
    setStep(3);
    toast({
      title: "Pagamento processado!",
      description: "Redirecionando para confirma√ß√£o...",
    });
  };

  const getFieldIcon = (field: string) => {
    switch (field) {
      case "full_name": return <User className="h-4 w-4" />;
      case "email": return <Mail className="h-4 w-4" />;
      case "phone": return <Phone className="h-4 w-4" />;
      case "cpf": return <CreditCard className="h-4 w-4" />;
      default: return null;
    }
  };

  const getStepIcon = (stepNumber: number) => {
    if (step > stepNumber) return <CheckCircle className="h-5 w-5 text-green-600" />;
    if (step === stepNumber) return <div className="h-5 w-5 bg-primary rounded-full" />;
    return <div className="h-5 w-5 bg-gray-300 rounded-full" />;
  };

  return (
    <div className="max-w-2xl mx-auto p-4 space-y-6">
      {/* Progress Steps */}
      <div className="flex items-center justify-center space-x-4 mb-8">
        <div className="flex items-center space-x-2">
          {getStepIcon(1)}
          <span className={`text-sm ${step >= 1 ? 'text-primary font-medium' : 'text-gray-400'}`}>
            Dados Pessoais
          </span>
        </div>
        <div className="h-px w-8 bg-gray-300" />
        <div className="flex items-center space-x-2">
          {getStepIcon(2)}
          <span className={`text-sm ${step >= 2 ? 'text-primary font-medium' : 'text-gray-400'}`}>
            Pagamento
          </span>
        </div>
        <div className="h-px w-8 bg-gray-300" />
        <div className="flex items-center space-x-2">
          {getStepIcon(3)}
          <span className={`text-sm ${step >= 3 ? 'text-primary font-medium' : 'text-gray-400'}`}>
            Confirma√ß√£o
          </span>
        </div>
      </div>

      {/* Plan Summary */}
      <Card className="border-primary/20">
        <CardContent className="p-4">
          <div className="flex justify-between items-center">
            <div>
              <h3 className="font-semibold">{planData.title}</h3>
              <p className="text-sm text-muted-foreground">{planData.description}</p>
            </div>
            <div className="text-right">
              <div className="text-2xl font-bold text-primary">
                R$ {planData.entry_value.toFixed(2)}
              </div>
              <div className="text-sm text-muted-foreground">
                Meta: R$ {planData.contemplation_value.toFixed(2)}
              </div>
            </div>
          </div>
          {influencerCode && (
            <Badge variant="secondary" className="mt-2">
              C√≥digo de indica√ß√£o: {influencerCode}
            </Badge>
          )}
        </CardContent>
      </Card>

      {/* Step 1: Personal Data */}
      {step === 1 && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Shield className="h-5 w-5" />
              <span>Seus Dados Pessoais</span>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <Alert>
              <Lock className="h-4 w-4" />
              <AlertDescription>
                Seus dados est√£o protegidos e ser√£o usados apenas para participa√ß√£o no plano
              </AlertDescription>
            </Alert>

            {Object.entries(formData).map(([key, value]) => (
              <div key={key} className="space-y-2">
                <Label htmlFor={key} className="flex items-center space-x-2">
                  {getFieldIcon(key)}
                  <span className="capitalize">
                    {key.replace('_', ' ')} {key === 'cpf' ? '(CPF)' : ''}
                  </span>
                </Label>
                <Input
                  id={key}
                  name={key}
                  value={value}
                  onChange={handleInputChange}
                  className={errors[key] ? "border-red-500" : ""}
                  placeholder={
                    key === 'phone' ? "(11) 99999-9999" :
                    key === 'cpf' ? "000.000.000-00" :
                    key === 'email' ? "seu@email.com" :
                    "Digite seu " + key.replace('_', ' ')
                  }
                />
                {errors[key] && (
                  <div className="flex items-center space-x-2 text-red-500 text-sm">
                    <AlertCircle className="h-4 w-4" />
                    <span>{errors[key]}</span>
                  </div>
                )}
              </div>
            ))}

            <Button 
              onClick={handleSubmitUserData}
              disabled={loading || Object.keys(errors).some(key => errors[key])}
              className="w-full"
            >
              {loading ? (
                <>
                  <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                  Validando dados...
                </>
              ) : (
                "Continuar para pagamento"
              )}
            </Button>
          </CardContent>
        </Card>
      )}

      {/* Step 2: Payment */}
      {step === 2 && userId && (
        <PaymentProcessor
          planId={planId}
          userId={userId}
          influencerCode={influencerCode}
          onPaymentSuccess={handlePaymentSuccess}
        />
      )}

      {/* Step 3: Confirmation */}
      {step === 3 && (
        <Card className="text-center">
          <CardContent className="p-8">
            <CheckCircle className="h-16 w-16 text-green-600 mx-auto mb-4" />
            <h2 className="text-2xl font-bold mb-2">Parab√©ns!</h2>
            <p className="text-muted-foreground mb-6">
              Sua participa√ß√£o no plano foi confirmada. Em breve voc√™ receber√° um email com todos os detalhes.
            </p>

            <div className="space-y-4">
              <Button className="w-full" onClick={() => window.location.href = "/usuario/dashboard"}>
                Acessar meu painel
              </Button>
              <Button variant="outline" className="w-full">
                Compartilhar link de indica√ß√£o
              </Button>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

import React, { Suspense, lazy } from 'react';
import { Skeleton } from '@/components/ui/skeleton';

// Componente para loading skeleton customiz√°vel
export const LoadingSkeleton = ({ 
  type = 'card',
  count = 1 
}: { 
  type?: 'card' | 'list' | 'table' | 'dashboard';
  count?: number;
}) => {
  const renderSkeleton = () => {
    switch (type) {
      case 'card':
        return (
          <div className="space-y-4">
            {Array.from({ length: count }).map((_, i) => (
              <div key={i} className="border rounded-lg p-4 space-y-3">
                <Skeleton className="h-4 w-[250px]" />
                <Skeleton className="h-4 w-[200px]" />
                <div className="space-y-2">
                  <Skeleton className="h-4 w-full" />
                  <Skeleton className="h-4 w-[80%]" />
                </div>
              </div>
            ))}
          </div>
        );

      case 'list':
        return (
          <div className="space-y-2">
            {Array.from({ length: count }).map((_, i) => (
              <div key={i} className="flex items-center space-x-4 py-2">
                <Skeleton className="h-12 w-12 rounded-full" />
                <div className="space-y-2">
                  <Skeleton className="h-4 w-[200px]" />
                  <Skeleton className="h-4 w-[150px]" />
                </div>
              </div>
            ))}
          </div>
        );

      case 'table':
        return (
          <div className="space-y-3">
            {Array.from({ length: count }).map((_, i) => (
              <div key={i} className="flex space-x-4">
                <Skeleton className="h-4 flex-1" />
                <Skeleton className="h-4 flex-1" />
                <Skeleton className="h-4 flex-1" />
                <Skeleton className="h-4 w-20" />
              </div>
            ))}
          </div>
        );

      case 'dashboard':
        return (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            {Array.from({ length: 4 }).map((_, i) => (
              <div key={i} className="border rounded-lg p-4 space-y-3">
                <Skeleton className="h-4 w-[100px]" />
                <Skeleton className="h-8 w-[60px]" />
                <Skeleton className="h-3 w-[80px]" />
              </div>
            ))}
          </div>
        );

      default:
        return <Skeleton className="h-20 w-full" />;
    }
  };

  return renderSkeleton();
};

// HOC para lazy loading com skeleton personalizado
export const withLazyLoading = <P extends object>(
  Component: React.ComponentType<P>,
  fallback?: React.ReactNode
) => {
  const LazyComponent = lazy(() => Promise.resolve({ default: Component }));

  return React.forwardRef<any, P>((props, ref) => (
    <Suspense fallback={fallback || <LoadingSkeleton type="card" count={3} />}>
      <LazyComponent {...props} ref={ref} />
    </Suspense>
  ));
};

// Componente para lazy loading de imagens
export const LazyImage = ({ 
  src, 
  alt, 
  className,
  fallback 
}: {
  src: string;
  alt: string;
  className?: string;
  fallback?: string;
}) => {
  const [loaded, setLoaded] = React.useState(false);
  const [error, setError] = React.useState(false);

  return (
    <div className={`relative ${className}`}>
      {!loaded && !error && (
        <Skeleton className="absolute inset-0 w-full h-full" />
      )}
      <img
        src={error ? fallback : src}
        alt={alt}
        className={`${className} ${loaded ? 'opacity-100' : 'opacity-0'} transition-opacity duration-300`}
        onLoad={() => setLoaded(true)}
        onError={() => {
          setError(true);
          setLoaded(true);
        }}
        loading="lazy"
      />
    </div>
  );
};

// Hook para intersection observer (lazy loading ao entrar na viewport)
export const useIntersectionObserver = (
  ref: React.RefObject<Element>,
  options: IntersectionObserverInit = {}
) => {
  const [isIntersecting, setIsIntersecting] = React.useState(false);

  React.useEffect(() => {
    const element = ref.current;
    if (!element) return;

    const observer = new IntersectionObserver(
      ([entry]) => {
        setIsIntersecting(entry.isIntersecting);
      },
      {
        threshold: 0.1,
        ...options
      }
    );

    observer.observe(element);

    return () => {
      observer.disconnect();
    };
  }, [ref, options]);

  return isIntersecting;
};

// Componente para carregar conte√∫do s√≥ quando vis√≠vel
export const LazySection = ({ 
  children, 
  fallback,
  className = ""
}: {
  children: React.ReactNode;
  fallback?: React.ReactNode;
  className?: string;
}) => {
  const ref = React.useRef<HTMLDivElement>(null);
  const isVisible = useIntersectionObserver(ref, { rootMargin: '100px' });

  return (
    <div ref={ref} className={className}>
      {isVisible ? children : (fallback || <LoadingSkeleton type="card" />)}
    </div>
  );
};

import React from "react";
import { Skeleton } from "@/components/ui/skeleton";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Loader2, RefreshCw } from "lucide-react";

interface LoadingSpinnerProps {
  size?: "sm" | "md" | "lg";
  text?: string;
  fullScreen?: boolean;
  className?: string;
  variant?: "default" | "dots" | "bars" | "pulse";
}

export function LoadingSpinner({ 
  size = "md", 
  text, 
  fullScreen = false, 
  className = "",
  variant = "default"
}: LoadingSpinnerProps) {
  const sizeClasses = {
    sm: "w-4 h-4",
    md: "w-8 h-8", 
    lg: "w-12 h-12"
  };

  const containerClasses = fullScreen 
    ? "fixed inset-0 bg-background/80 backdrop-blur-sm z-50 flex items-center justify-center"
    : "flex items-center justify-center";

  const renderSpinner = () => {
    switch (variant) {
      case "dots":
        return (
          <div className="flex space-x-1">
            <div className="w-2 h-2 bg-primary rounded-full animate-bounce [animation-delay:-0.3s]" />
            <div className="w-2 h-2 bg-primary rounded-full animate-bounce [animation-delay:-0.15s]" />
            <div className="w-2 h-2 bg-primary rounded-full animate-bounce" />
          </div>
        );
      case "bars":
        return (
          <div className="flex space-x-1">
            <div className="w-1 h-6 bg-primary animate-pulse [animation-delay:-0.4s]" />
            <div className="w-1 h-6 bg-primary animate-pulse [animation-delay:-0.2s]" />
            <div className="w-1 h-6 bg-primary animate-pulse" />
          </div>
        );
      case "pulse":
        return <div className={`${sizeClasses[size]} bg-primary rounded-full animate-pulse`} />;
      default:
        return <Loader2 className={`${sizeClasses[size]} animate-spin text-primary`} />;
    }
  };

  const SpinnerComponent = (
    <div className={`flex items-center justify-center space-x-3 ${className}`}>
      {renderSpinner()}
      {text && (
        <span className="text-sm text-muted-foreground animate-fade-in">{text}</span>
      )}
    </div>
  );

  if (fullScreen) {
    return (
      <div className={containerClasses}>
        <div className="bg-background border rounded-lg p-6 shadow-lg">
          {SpinnerComponent}
        </div>
      </div>
    );
  }

  return SpinnerComponent;
}

export function LoadingButton({ children, loading, loadingText = "Carregando...", variant = "default", size = "default", ...props }: any) {
  return (
    <Button {...props} disabled={loading || props.disabled} variant={variant} size={size}>
      {loading ? (
        <div className="flex items-center space-x-2">
          <Loader2 className="w-4 h-4 animate-spin" />
          <span>{loadingText}</span>
        </div>
      ) : (
        children
      )}
    </Button>
  );
}

export function SkeletonCard({ animate = true }: { animate?: boolean }) {
  return (
    <Card className={animate ? "animate-pulse" : ""}>
      <CardHeader>
        <Skeleton className="h-4 w-3/4" />
        <Skeleton className="h-3 w-1/2" />
      </CardHeader>
      <CardContent className="space-y-3">
        <Skeleton className="h-4 w-full" />
        <Skeleton className="h-4 w-4/5" />
        <Skeleton className="h-4 w-2/3" />
        <div className="flex space-x-2 pt-2">
          <Skeleton className="h-8 w-20" />
          <Skeleton className="h-8 w-16" />
        </div>
      </CardContent>
    </Card>
  );
}

export function SkeletonTable({ rows = 5, columns = 4 }: { rows?: number; columns?: number }) {
  return (
    <div className="space-y-3">
      {/* Header */}
      <div className="flex space-x-4 pb-2 border-b">
        {Array.from({ length: columns }).map((_, i) => (
          <Skeleton key={`header-${i}`} className="h-4 w-24" />
        ))}
      </div>
      {/* Rows */}
      {Array.from({ length: rows }).map((_, i) => (
        <div key={`row-${i}`} className="flex space-x-4">
          {Array.from({ length: columns }).map((_, j) => (
            <Skeleton 
              key={`cell-${i}-${j}`} 
              className={`h-4 ${j === 0 ? 'w-32' : j === columns - 1 ? 'w-16' : 'w-24'}`} 
            />
          ))}
        </div>
      ))}
    </div>
  );
}

export function SkeletonText({ lines = 3, className = "" }: { lines?: number; className?: string }) {
  return (
    <div className={`space-y-2 ${className}`}>
      {Array.from({ length: lines }).map((_, i) => (
        <Skeleton 
          key={i} 
          className={`h-4 ${
            i === lines - 1 ? 'w-2/3' : i === 0 ? 'w-full' : 'w-5/6'
          }`} 
        />
      ))}
    </div>
  );
}

export function SkeletonStats() {
  return (
    <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
      {Array.from({ length: 4 }).map((_, i) => (
        <Card key={i} className="animate-pulse">
          <CardContent className="p-6">
