import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { useFileUpload } from "@/hooks/useFileUpload";
import { Upload, File, Image, Video, X, Check } from "lucide-react";

interface SecureFileUploadProps {
  type: 'image' | 'document' | 'video';
  category?: string;
  onUploadComplete?: (result: any) => void;
  accept?: string;
  maxSize?: string;
  label?: string;
  description?: string;
  className?: string;
}

export function SecureFileUpload({
  type,
  category = 'general',
  onUploadComplete,
  accept,
  maxSize,
  label,
  description,
  className = "",
}: SecureFileUploadProps) {
  const [dragActive, setDragActive] = useState(false);
  const [uploadedFile, setUploadedFile] = useState<any>(null);
  const { uploading, progress, uploadFile } = useFileUpload();

  const getAcceptTypes = () => {
    if (accept) return accept;
    switch (type) {
      case 'image': return '.jpg,.jpeg,.png,.webp';
      case 'document': return '.pdf,.jpg,.jpeg,.png';
      case 'video': return '.mp4,.mov,.avi';
      default: return '*';
    }
  };

  const getMaxSizeText = () => {
    if (maxSize) return maxSize;
    switch (type) {
      case 'image': return '5MB';
      case 'document': return '10MB';
      case 'video': return '100MB';
      default: return '10MB';
    }
  };

  const getIcon = () => {
    switch (type) {
      case 'image': return <Image className="h-8 w-8" />;
      case 'video': return <Video className="h-8 w-8" />;
      default: return <File className="h-8 w-8" />;
    }
  };

  const handleDrag = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleFile(e.dataTransfer.files[0]);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    e.preventDefault();
    if (e.target.files && e.target.files[0]) {
      handleFile(e.target.files[0]);
    }
  };

  const handleFile = async (file: File) => {
    const result = await uploadFile(file, { type, category });
    if (result) {
      setUploadedFile(result);
      onUploadComplete?.(result);
    }
  };

  const removeFile = () => {
    setUploadedFile(null);
  };

  if (uploadedFile) {
    return (
      <Card className={className}>
        <CardContent className="p-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="p-2 bg-green-100 rounded-lg">
                <Check className="h-5 w-5 text-green-600" />
              </div>
              <div>
                <p className="font-medium">{uploadedFile.file_name}</p>
                <p className="text-sm text-gray-500">
                  {(uploadedFile.file_size / 1024 / 1024).toFixed(2)} MB
                </p>
              </div>
            </div>
            <Button variant="ghost" size="sm" onClick={removeFile}>
              <X className="h-4 w-4" />
            </Button>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className={className}>
      <CardHeader>
        <CardTitle className="flex items-center space-x-2">
          {getIcon()}
          <span>{label || `Upload de ${type}`}</span>
        </CardTitle>
        {description && (
          <CardDescription>{description}</CardDescription>
        )}
      </CardHeader>
      <CardContent>
        <div
          className={`relative border-2 border-dashed rounded-lg p-6 transition-colors ${
            dragActive
              ? "border-primary bg-primary/5"
              : "border-gray-300 hover:border-gray-400"
          }`}
          onDragEnter={handleDrag}
          onDragLeave={handleDrag}
          onDragOver={handleDrag}
          onDrop={handleDrop}
        >
          <input
            type="file"
            accept={getAcceptTypes()}
            onChange={handleChange}
            disabled={uploading}
            className="absolute inset-0 w-full h-full opacity-0 cursor-pointer disabled:cursor-not-allowed"
          />

          <div className="text-center">
            <Upload className="mx-auto h-12 w-12 text-gray-400" />
            <div className="mt-4">
              <p className="text-lg font-medium">
                {uploading ? "Enviando..." : "Clique ou arraste o arquivo"}
              </p>
              <p className="text-sm text-gray-500 mt-1">
                Formatos aceitos: {getAcceptTypes()} (m√°x. {getMaxSizeText()})
              </p>
            </div>
          </div>

          {uploading && (
            <div className="mt-4">
              <Progress value={progress} className="w-full" />
              <p className="text-sm text-center mt-2 text-gray-600">
                {progress}% conclu√≠do
              </p>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

import { useState, useEffect } from 'react';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Shield, AlertTriangle, Bell, X } from 'lucide-react';
import { supabase } from '@/integrations/supabase/client';
import { toast } from 'sonner';

interface SecurityEvent {
  id: string;
  event_type: string;
  created_at: string;
  details: any;
  user_id?: string | null;
  ip_address?: unknown;
  user_agent?: string | null;
}

interface SecurityAlertProps {
  enableRealTimeAlerts?: boolean;
  showCriticalOnly?: boolean;
}

const CRITICAL_EVENTS = [
  'invalid_webhook_signature',
  'payment_amount_mismatch',
  'webhook_rate_limit_exceeded',
  'transaction_limit_exceeded',
  'deprecated_webhook_accessed'
];

export const SecurityAlert = ({ enableRealTimeAlerts = true, showCriticalOnly = false }: SecurityAlertProps) => {
  const [alerts, setAlerts] = useState<SecurityEvent[]>([]);
  const [loading, setLoading] = useState(true);
  const [isMinimized, setIsMinimized] = useState(false);

  const fetchRecentAlerts = async () => {
    try {
      setLoading(true);

      // Use notification_triggers as a substitute for security events
      let query = supabase
        .from('notification_triggers')
        .select('*')
        .order('created_at', { ascending: false })
        .limit(10);

      const { data, error } = await query;

      if (error) {
        console.error('Error fetching security alerts:', error);
        return;
      }

      // Transform notification triggers to security events format
      const transformedData = (data || []).map(item => ({
        id: item.id,
        event_type: item.event_type || 'notification_trigger',
        created_at: item.created_at,
        details: item.data,
        user_id: item.user_id,
        ip_address: null,
        user_agent: null
      }));

      setAlerts(transformedData);
    } catch (error) {
      console.error('Error in fetchRecentAlerts:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchRecentAlerts();

    if (enableRealTimeAlerts) {
      const channel = supabase
        .channel('security-events')
        .on('postgres_changes', 
          { 
            event: 'INSERT', 
            schema: 'public', 
            table: 'notification_triggers' 
          },
          (payload) => {
            const newEvent = payload.new as SecurityEvent;

            // Add to alerts list
            setAlerts(prev => [newEvent, ...prev.slice(0, 9)]);

            // Show toast for critical events
            if (CRITICAL_EVENTS.includes(newEvent.event_type)) {
              toast.error(`Security Alert: ${getEventTitle(newEvent.event_type)}`, {
                description: `Critical security event detected at ${new Date(newEvent.created_at).toLocaleTimeString()}`,
                action: {
                  label: 'View Details',
                  onClick: () => setIsMinimized(false)
                }
              });
            }
          }
        )
        .subscribe();

      return () => {
        channel.unsubscribe();
      };
    }
  }, [enableRealTimeAlerts, showCriticalOnly]);

  const getEventTitle = (eventType: string): string => {
    const titles: { [key: string]: string } = {
      'invalid_webhook_signature': 'Invalid Webhook Signature',
      'payment_amount_mismatch': 'Payment Amount Mismatch',
      'webhook_rate_limit_exceeded': 'Webhook Rate Limit Exceeded',
      'transaction_limit_exceeded': 'Transaction Limit Exceeded',
      'deprecated_webhook_accessed': 'Deprecated Webhook Accessed',
      'webhook_invalid_json': 'Invalid Webhook JSON',
      'referral_limit_exceeded': 'Referral Limit Exceeded',
      'payment_processed_successfully': 'Payment Processed',
      'transaction_validation': 'Transaction Validated'
    };

    return titles[eventType] || eventType.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
  };

  const getEventSeverity = (eventType: string): 'critical' | 'warning' | 'info' => {
    if (CRITICAL_EVENTS.includes(eventType)) {
      return 'critical';
    }

    const warningEvents = [
      'webhook_invalid_json',
      'referral_limit_exceeded',
      'transaction_validation'
    ];

    return warningEvents.includes(eventType) ? 'warning' : 'info';
  };

  const getSeverityColor = (severity: string): string => {
    switch (severity) {
      case 'critical':
        return 'bg-destructive text-destructive-foreground';
      case 'warning':
        return 'bg-accent text-accent-foreground';
      default:
        return 'bg-muted text-muted-foreground';
    }
  };

  const getSeverityIcon = (severity: string) => {
    switch (severity) {
      case 'critical':
        return <AlertTriangle className="h-4 w-4" />;
      case 'warning':
        return <Bell className="h-4 w-4" />;
      default:
        return <Shield className="h-4 w-4" />;
    }
  };

  const criticalCount = alerts.filter(alert => 
    CRITICAL_EVENTS.includes(alert.event_type)
  ).length;

  if (loading) {
    return (
      <Alert className="border-muted">
        <Shield className="h-4 w-4" />
        <AlertTitle>Security Monitor</AlertTitle>
        <AlertDescription>Loading security events...</AlertDescription>
      </Alert>
    );
  }

  if (isMinimized) {
    return (
      <div className="fixed bottom-4 right-4 z-50">
        <Button
          onClick={() => setIsMinimized(false)}
          variant={criticalCount > 0 ? "destructive" : "secondary"}
          size="sm"
          className="rounded-full"
        >
          <Shield className="h-4 w-4 mr-2" />
          Security {criticalCount > 0 && `(${criticalCount})`}
        </Button>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      <Alert className={criticalCount > 0 ? "border-destructive" : "border-muted"}>
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <Shield className="h-4 w-4" />
            <AlertTitle>Security Monitor</AlertTitle>
            {criticalCount > 0 && (
              <Badge variant="destructive" className="text-xs">
                {criticalCount} Critical
              </Badge>
            )}
          </div>
          <div className="flex items-center space-x-2">
            <Button
              onClick={fetchRecentAlerts}
              variant="ghost"
              size="sm"
            >
              Refresh
            </Button>
            <Button
              onClick={() => setIsMinimized(true)}
              variant="ghost"
              size="sm"
            >
              <X className="h-4 w-4" />
            </Button>
          </div>
        </div>

        <AlertDescription className="mt-4">
          {alerts.length === 0 ? (
            <div className="text-center py-4 text-muted-foreground">
              No recent security events
            </div>
          ) : (
            <div className="space-y-2 max-h-64 overflow-y-auto">
              {alerts.map((alert) => {
                const severity = getEventSeverity(alert.event_type);
                const title = getEventTitle(alert.event_type);

                return (
                  <div
                    key={alert.id}
                    className="flex items-start justify-between p-3 rounded-lg border bg-card"
                  >
                    <div className="flex items-start space-x-3 flex-1">
                      <div className={`p-1 rounded ${getSeverityColor(severity)}`}>
                        {getSeverityIcon(severity)}
                      </div>
                      <div className="flex-1 min-w-0">
                        <div className="flex items-center space-x-2">
                          <h4 className="text-sm font-medium">{title}</h4>
                          <Badge variant="outline" className="text-xs">
                            {severity}
                          </Badge>
                        </div>
                        <p className="text-xs text-muted-foreground mt-1">
                          {new Date(alert.created_at).toLocaleString()}
                        </p>
                        {alert.ip_address && (
                          <p className="text-xs text-muted-foreground">
                            IP: {String(alert.ip_address)}
                          </p>
                        )}
                        {alert.details && (
                          <details className="mt-2">
                            <summary className="text-xs text-muted-foreground cursor-pointer hover:text-foreground">
                              View Details
                            </summary>
                            <pre className="text-xs mt-1 p-2 bg-muted rounded overflow-x-auto">
                              {JSON.stringify(alert.details, null, 2)}
                            </pre>
                          </details>
                        )}
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </AlertDescription>
      </Alert>
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { supabase } from "@/integrations/supabase/client";
import { Users, CheckCircle, XCircle, Clock, Settings, Edit, Trash2 } from 'lucide-react';

interface Participant {
  id: string;
  nome: string;
  email: string;
  telefone: string;
  serviceType: string;
  status: string;
  paymentStatus: string;
  contemplationStatus: string;
  joinDate: string;
  contemplationDate?: string | null;
}

export function AdminManagementPanel() {
  const [participants, setParticipants] = useState<Participant[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedParticipant, setSelectedParticipant] = useState<Participant | null>(null);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const { toast } = useToast();

  useEffect(() => {
    loadParticipants();
  }, []);

  const loadParticipants = async () => {
    setLoading(true);
    try {
      // Carregar participantes dos planos
      const { data: participantsData, error } = await supabase
        .from('group_participants')
        .select('*')
        .order('joined_at', { ascending: false });

      if (error) throw error;

      // Carregar dados de usu√°rios separadamente (usando profiles em vez de users)
      const userIds = participantsData?.map(p => p.user_id) || [];
      const { data: usersData } = await supabase
        .from('profiles')
        .select('user_id, full_name, email, phone')
        .in('user_id', userIds);

      // Carregar dados de grupos separadamente
      const groupIds = participantsData?.map(p => p.group_id) || [];
      const { data: groupsData } = await supabase
        .from('plan_groups')
        .select('id')
        .in('id', groupIds);

      // Transformar dados para o formato esperado
      const transformedParticipants = (participantsData || []).map(participant => {
        const user = usersData?.find(u => u.user_id === participant.user_id);
        const group = groupsData?.find(g => g.id === participant.group_id);

        return {
          id: participant.id,
          nome: user?.full_name || 'N/A',
          email: user?.email || 'N/A', 
          telefone: user?.phone || 'N/A',
          serviceType: 'Plano MLM',
          status: participant.status === 'active' ? 'Ativo' : 'Pendente',
          paymentStatus: participant.status || 'pending',
          contemplationStatus: 'aguardando',
          joinDate: participant.joined_at?.split('T')[0] || new Date().toISOString().split('T')[0],
          contemplationDate: null
        };
      });

      setParticipants(transformedParticipants);
    } catch (error) {
      console.error('Erro ao carregar participantes:', error);
      toast({
        title: "Erro",
        description: "Erro ao carregar participantes.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateContemplation = async (participantId: string, newStatus: string) => {
    try {
      // Atualizar no Supabase
      const { error } = await supabase
        .from('group_participants')
        .update({
          status: newStatus === 'contemplado' ? 'contemplated' : 'active'
        })
        .eq('id', participantId);

      if (error) throw error;

      // Atualizar estado local
      setParticipants(prev => prev.map(p => 
        p.id === participantId 
          ? { 
              ...p, 
              contemplationStatus: newStatus, 
              contemplationDate: newStatus === 'contemplado' ? new Date().toISOString().split('T')[0] : null 
            }
          : p
      ));

      // Se contemplado, criar registro na tabela contemplations
      if (newStatus === 'contemplado') {
        const participant = participants.find(p => p.id === participantId);
        if (participant) {
          // Log the contemplation (since we don't have a contemplations table)
          console.log(`Participant ${participant.nome} was contemplated`);
        }
      }

      toast({
        title: "Sucesso",
        description: "Status de contempla√ß√£o atualizado com sucesso.",
      });
    } catch (error) {
      console.error('Erro ao atualizar contempla√ß√£o:', error);
      toast({
        title: "Erro",
        description: "Erro ao atualizar status de contempla√ß√£o.",
        variant: "destructive",
      });
    }
  };

  const handleDeleteParticipant = async (participantId: string) => {
    try {
      // Remover do Supabase
      const { error } = await supabase
        .from('group_participants')
        .delete()
        .eq('id', participantId);

      if (error) throw error;

      // Atualizar estado local
      setParticipants(prev => prev.filter(p => p.id !== participantId));

      toast({
        title: "Sucesso",
        description: "Participante removido com sucesso.",
      });
    } catch (error) {
      console.error('Erro ao remover participante:', error);
      toast({
        title: "Erro",
        description: "Erro ao remover participante.",
        variant: "destructive",
      });
    }
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'paid':
        return <Badge className="bg-green-500"><CheckCircle className="w-3 h-3 mr-1" />Pago</Badge>;
      case 'pending':
        return <Badge variant="secondary"><Clock className="w-3 h-3 mr-1" />Pendente</Badge>;
      case 'contemplado':
        return <Badge className="bg-blue-500"><CheckCircle className="w-3 h-3 mr-1" />Contemplado</Badge>;
      case 'aguardando':
        return <Badge variant="outline"><Clock className="w-3 h-3 mr-1" />Aguardando</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-3xl font-bold tracking-tight">Gerenciamento de Participantes</h2>
        <Button onClick={loadParticipants}>
          <Settings className="mr-2 h-4 w-4" />
          Atualizar
        </Button>
      </div>

      <Tabs defaultValue="participants" className="space-y-4">
        <TabsList>
          <TabsTrigger value="participants">Participantes</TabsTrigger>
          <TabsTrigger value="contemplation">Contempla√ß√£o</TabsTrigger>
        </TabsList>

        <TabsContent value="participants" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Users className="mr-2 h-5 w-5" />
                Lista de Participantes
              </CardTitle>
              <CardDescription>
                Gerencie todos os participantes dos planos
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Nome</TableHead>
                    <TableHead>E-mail</TableHead>
                    <TableHead>Telefone</TableHead>
                    <TableHead>Servi√ßo</TableHead>
                    <TableHead>Pagamento</TableHead>
                    <TableHead>Contempla√ß√£o</TableHead>
                    <TableHead>Data Inscri√ß√£o</TableHead>
                    <TableHead>A√ß√µes</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {participants.map((participant) => (
                    <TableRow key={participant.id}>
                      <TableCell className="font-medium">{participant.nome}</TableCell>
                      <TableCell>{participant.email}</TableCell>
                      <TableCell>{participant.telefone}</TableCell>
                      <TableCell>{participant.serviceType}</TableCell>
                      <TableCell>{getStatusBadge(participant.paymentStatus)}</TableCell>
                      <TableCell>{getStatusBadge(participant.contemplationStatus)}</TableCell>
                      <TableCell>{new Date(participant.joinDate).toLocaleDateString('pt-BR')}</TableCell>
                      <TableCell>
                        <div className="flex space-x-2">
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => {
                              setSelectedParticipant(participant);
                              setIsEditDialogOpen(true);
                            }}
                          >
                            <Edit className="h-4 w-4" />
                          </Button>
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => handleDeleteParticipant(participant.id)}
                          >
                            <Trash2 className="h-4 w-4" />
                          </Button>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="contemplation" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Gerenciar Contempla√ß√£o</CardTitle>
              <CardDescription>
                Gerencie o status de contempla√ß√£o dos participantes
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              {participants.filter(p => p.paymentStatus === 'paid').map((participant) => (
                <div key={participant.id} className="flex items-center justify-between p-4 border rounded-lg">
                  <div>
                    <p className="font-medium">{participant.nome}</p>
                    <p className="text-sm text-muted-foreground">{participant.serviceType}</p>
                    <p className="text-sm">Status: {getStatusBadge(participant.contemplationStatus)}</p>
                  </div>
                  <div className="flex space-x-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleUpdateContemplation(participant.id, 'contemplado')}
                      disabled={participant.contemplationStatus === 'contemplado'}
                    >
                      Contemplar
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleUpdateContemplation(participant.id, 'aguardando')}
                      disabled={participant.contemplationStatus === 'aguardando'}
                    >
                      Reverter
                    </Button>
                  </div>
                </div>
              ))}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>

      {/* Dialog de Edi√ß√£o */}
      <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Editar Participante</DialogTitle>
            <DialogDescription>
              Atualize as informa√ß√µes do participante
            </DialogDescription>
          </DialogHeader>
          {selectedParticipant && (
            <div className="space-y-4">
              <div>
                <Label htmlFor="nome">Nome</Label>
                <Input id="nome" defaultValue={selectedParticipant.nome} />
              </div>
              <div>
                <Label htmlFor="email">E-mail</Label>
