import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Switch } from "@/components/ui/switch";
import { 
  Settings, 
  Check, 
  X, 
  AlertTriangle, 
  RefreshCw, 
  Link,
  Shield,
  Database,
  RefreshCcw,
  Lock,
  Eye,
  EyeOff,
  Key
} from 'lucide-react';
import { supabase } from "@/integrations/supabase/client";
import { toast } from "sonner";
import { useNavigate } from "react-router-dom";

interface AsaasIntegration {
  id: string;
  environment: string;
  status: string;
  connection_status: string;
  last_sync_at?: string;
  error_message?: string;
  created_at: string;
  updated_at: string;
}

export function AsaasIntegrationManager() {
  const navigate = useNavigate();
  const [integration, setIntegration] = useState<AsaasIntegration | null>(null);
  const [loading, setLoading] = useState(true);
  const [testing, setTesting] = useState(false);
  const [syncing, setSyncing] = useState(false);
  const [showApiKey, setShowApiKey] = useState(false);
  const [isValidApiKey, setIsValidApiKey] = useState(false);
  const [autoTestTimer, setAutoTestTimer] = useState<NodeJS.Timeout | null>(null);
  const [form, setForm] = useState({
    apiKey: '',
    environment: 'sandbox' as 'sandbox' | 'production',
    autoSync: false
  });

  useEffect(() => {
    loadIntegration();
  }, []);

  // Auto-test connection after user stops typing API key
  useEffect(() => {
    if (autoTestTimer) {
      clearTimeout(autoTestTimer);
    }

    if (form.apiKey.length > 10) { // Basic validation for API key length
      setAutoTestTimer(setTimeout(() => {
        testConnectionSilent();
      }, 2000));
    } else {
      setIsValidApiKey(false);
    }

    return () => {
      if (autoTestTimer) {
        clearTimeout(autoTestTimer);
      }
    };
  }, [form.apiKey, form.environment]);

  const loadIntegration = async () => {
    try {
      const { data, error } = await supabase
        .from('asaas_integration')
        .select('*')
        .order('created_at', { ascending: false })
        .limit(1)
        .maybeSingle();

      if (error && error.code !== 'PGRST116') {
        throw error;
      }

      if (data) {
        setIntegration(data);
        setForm(prev => ({
          ...prev,
          environment: data.environment as 'sandbox' | 'production',
          autoSync: data.status === 'active'
        }));
      }
    } catch (error) {
      console.error('Error loading integration:', error);
      toast.error('Erro ao carregar configura√ß√£o');
    } finally {
      setLoading(false);
    }
  };

  const testConnection = async () => {
    if (!form.apiKey.trim()) {
      toast.error('Informe a API Key do Asaas');
      return;
    }

    setTesting(true);
    try {
      const { data, error } = await supabase.functions.invoke('asaas-integration-manager', {
        body: {
          action: 'test_connection',
          api_key: form.apiKey,
          environment: form.environment
        }
      });

      if (error) throw error;

      if (data.success) {
        toast.success('Conex√£o testada com sucesso!');
        return true;
      } else {
        toast.error(data.error || 'Falha na conex√£o');
        return false;
      }
    } catch (error) {
      console.error('Error testing connection:', error);
      toast.error('Erro ao testar conex√£o');
      return false;
    } finally {
      setTesting(false);
    }
  };

  // Silent test for auto-validation (no toasts)
  const testConnectionSilent = async () => {
    if (!form.apiKey.trim() || form.apiKey.length < 10) {
      setIsValidApiKey(false);
      return;
    }

    try {
      const { data, error } = await supabase.functions.invoke('asaas-integration-manager', {
        body: {
          action: 'test_connection',
          api_key: form.apiKey,
          environment: form.environment
        }
      });

      if (error) throw error;

      setIsValidApiKey(data.success);
    } catch (error) {
      console.error('Silent connection test failed:', error);
      setIsValidApiKey(false);
    }
  };

  const saveConfiguration = async () => {
    const connectionTest = await testConnection();
    if (!connectionTest) return;

    try {
      const { data, error } = await supabase.functions.invoke('asaas-integration-manager', {
        body: {
          action: 'save_configuration',
          api_key: form.apiKey,
          environment: form.environment,
          auto_sync: form.autoSync
        }
      });

      if (error) throw error;

      if (data.success) {
        toast.success('Configura√ß√£o salva com sucesso!');
        await loadIntegration();
      } else {
        toast.error(data.error || 'Erro ao salvar configura√ß√£o');
      }
    } catch (error) {
      console.error('Error saving configuration:', error);
      toast.error('Erro ao salvar configura√ß√£o');
    }
  };

  const syncProducts = async () => {
    setSyncing(true);
    try {
      const { data, error } = await supabase.functions.invoke('asaas-integration-manager', {
        body: {
          action: 'sync_products'
        }
      });

      if (error) throw error;

      if (data.success) {
        toast.success(`Sincroniza√ß√£o conclu√≠da: ${data.synced} produtos sincronizados`);
        await loadIntegration();
      } else {
        toast.error(data.error || 'Erro na sincroniza√ß√£o');
      }
    } catch (error) {
      console.error('Error syncing products:', error);
      toast.error('Erro ao sincronizar produtos');
    } finally {
      setSyncing(false);
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
