                </AlertDescription>
              </Alert>

              <div className="flex gap-3">
                <Button 
                  onClick={testConnection} 
                  disabled={testing || !form.apiKey.trim()}
                  variant="outline"
                >
                  {testing ? (
                    <>
                      <RefreshCw className="h-4 w-4 mr-2 animate-spin" />
