import React, { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { supabase } from "@/integrations/supabase/client";
import { Loader2, Key, CheckCircle, XCircle, AlertCircle } from "lucide-react";

export function AsaasPaymentConfig() {
  const [apiKey, setApiKey] = useState("");
  const [environment, setEnvironment] = useState<"sandbox" | "production">("sandbox");
  const [loading, setLoading] = useState(false);
  const [testingConnection, setTestingConnection] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState<"disconnected" | "connected" | "error">("disconnected");
  const [config, setConfig] = useState<any>(null);
  const { toast } = useToast();

  useEffect(() => {
    loadConfiguration();
  }, []);

  const loadConfiguration = async () => {
    try {
      const { data, error } = await supabase
        .from('asaas_integration')
        .select('*')
        .order('created_at', { ascending: false })
        .limit(1)
        .single();

      if (data && !error) {
        setConfig(data);
        setEnvironment(data.environment as "sandbox" | "production");
        setConnectionStatus(data.connection_status as "disconnected" | "connected" | "error" || "disconnected");
      }
    } catch (error) {
      console.log("Nenhuma configura√ß√£o encontrada");
    }
  };

  const testConnection = async () => {
    if (!apiKey.trim()) {
      toast({
        title: "Erro",
        description: "Por favor, insira a API key.",
        variant: "destructive",
      });
      return;
    }

    setTestingConnection(true);
    try {
      const { data, error } = await supabase.functions.invoke('asaas-integration-manager', {
        body: {
          action: 'test_connection',
          api_key: apiKey,
          environment: environment
        }
      });

      if (error) throw error;

      if (data.success) {
        setConnectionStatus("connected");
        toast({
          title: "Conex√£o bem-sucedida!",
          description: `Conectado √† conta: ${data.account?.name || 'Asaas'}`,
        });
      } else {
        setConnectionStatus("error");
        toast({
          title: "Erro na conex√£o",
          description: data.error || "N√£o foi poss√≠vel conectar com a API.",
          variant: "destructive",
        });
      }
    } catch (error) {
      console.error("Erro ao testar conex√£o:", error);
      setConnectionStatus("error");
      toast({
        title: "Erro",
        description: "Erro ao testar a conex√£o com a API.",
        variant: "destructive",
      });
    } finally {
      setTestingConnection(false);
    }
  };

  const saveConfiguration = async () => {
    if (!apiKey.trim()) {
      toast({
        title: "Erro",
        description: "Por favor, insira a API key.",
        variant: "destructive",
      });
      return;
    }

    if (connectionStatus !== "connected") {
      toast({
        title: "Erro",
        description: "Por favor, teste a conex√£o antes de salvar.",
        variant: "destructive",
      });
      return;
    }

    setLoading(true);
    try {
      const { data, error } = await supabase.functions.invoke('asaas-integration-manager', {
        body: {
          action: 'save_configuration',
          api_key: apiKey,
          environment: environment
        }
      });

      if (error) throw error;

      if (data.success) {
        setConfig(data.config);
        toast({
          title: "Configura√ß√£o salva!",
          description: "API key configurada com sucesso.",
        });
        setApiKey(""); // Limpar o campo por seguran√ßa
        loadConfiguration(); // Recarregar configura√ß√µes
      } else {
        throw new Error(data.error || "Erro ao salvar configura√ß√£o");
      }
    } catch (error) {
      console.error("Erro ao salvar configura√ß√£o:", error);
      toast({
        title: "Erro",
        description: "Erro ao salvar a configura√ß√£o.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = () => {
    switch (connectionStatus) {
      case "connected": return "text-green-600";
      case "error": return "text-red-600";
      default: return "text-gray-500";
    }
  };

  const getStatusIcon = () => {
    switch (connectionStatus) {
      case "connected": return <CheckCircle className="h-4 w-4 text-green-600" />;
      case "error": return <XCircle className="h-4 w-4 text-red-600" />;
      default: return <AlertCircle className="h-4 w-4 text-gray-500" />;
    }
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Key className="h-5 w-5" />
          Configura√ß√£o API Asaas
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Status atual */}
        {config && (
          <div className="p-4 border rounded-lg bg-gray-50">
            <div className="flex items-center justify-between mb-2">
              <span className="font-medium">Status da integra√ß√£o:</span>
              <div className="flex items-center gap-2">
                {getStatusIcon()}
                <span className={`capitalize ${getStatusColor()}`}>
                  {config.connection_status === "connected" ? "Conectado" : "Desconectado"}
                </span>
              </div>
            </div>
            <div className="grid grid-cols-2 gap-4 text-sm">
