import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { RefreshCw, AlertTriangle, CheckCircle, XCircle } from "lucide-react";
import { supabase } from "@/integrations/supabase/client";
import { useToast } from "@/hooks/use-toast";

interface UnifiedPlan {
  id: string;
  name: string;
  price: number;
  active: boolean;
  tipo_transacao: string;
  category: string;
  table_source: string;
  created_at: string;
}

interface PlanStats {
  total_found: number;
  total_valid: number;
  total_filtered: number;
  by_category: Record<string, number>;
  by_transaction_type: Record<string, number>;
  by_table_source: Record<string, number>;
  errors: string[];
}

export const PlansMonitor = () => {
  const [plans, setPlans] = useState<UnifiedPlan[]>([]);
  const [stats, setStats] = useState<PlanStats | null>(null);
  const [loading, setLoading] = useState(false);
  const [lastRefresh, setLastRefresh] = useState<Date | null>(null);
  const { toast } = useToast();

  const loadPlansData = async (includeInactive = false) => {
    setLoading(true);
    try {
      console.log('üîç [MONITOR] Carregando dados completos dos planos...');

      const { data: response, error } = await supabase.functions.invoke('unified-plans-loader', {
        body: { 
          include_inactive: includeInactive, 
          admin_view: true 
        }
      });

      if (error) {
        console.error('‚ùå [MONITOR] Erro na edge function:', error);
        throw error;
      }

      if (!response?.success) {
        console.error('‚ùå [MONITOR] Resposta inv√°lida:', response);
        throw new Error('Falha ao carregar dados dos planos');
      }

      setPlans(response.plans || []);
      setStats(response.stats || null);
      setLastRefresh(new Date());

      console.log('‚úÖ [MONITOR] Dados carregados:', {
        total: response.plans?.length || 0,
        errors: response.errors?.length || 0
      });

      if (response.errors && response.errors.length > 0) {
        toast({
          title: "Avisos encontrados",
          description: `${response.errors.length} problemas detectados nos dados.`,
          variant: "default",
        });
      }

    } catch (error) {
      console.error('‚ùå [MONITOR] Erro cr√≠tico:', error);
      toast({
        title: "Erro no Monitor",
        description: "Falha ao carregar dados dos planos.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadPlansData();
  }, []);

  const getStatusColor = (active: boolean) => {
    return active ? "bg-green-100 text-green-800" : "bg-red-100 text-red-800";
  };

  const getCategoryColor = (category: string) => {
    const colors: Record<string, string> = {
      'tattoo': 'bg-purple-100 text-purple-800',
      'dental': 'bg-blue-100 text-blue-800',
      'service': 'bg-green-100 text-green-800',
      'product': 'bg-orange-100 text-orange-800',
    };
    return colors[category] || 'bg-gray-100 text-gray-800';
  };

  const getTableSourceColor = (source: string) => {
    const colors: Record<string, string> = {
      'planos_tatuador': 'bg-purple-50 text-purple-700',
      'planos_dentista': 'bg-blue-50 text-blue-700',
      'custom_plans': 'bg-green-50 text-green-700',
      'products': 'bg-orange-50 text-orange-700',
    };
    return colors[source] || 'bg-gray-50 text-gray-700';
  };

  return (
    <div className="space-y-6">
      {/* Header com controles */}
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold">Monitor de Planos</h2>
          <p className="text-muted-foreground">
            Monitoramento em tempo real de todos os planos e produtos
          </p>
        </div>

        <div className="flex gap-2">
          <Button
            onClick={() => loadPlansData(false)}
            disabled={loading}
            variant="outline"
          >
            <RefreshCw className={`h-4 w-4 mr-2 ${loading ? 'animate-spin' : ''}`} />
            Apenas Ativos
          </Button>

          <Button
            onClick={() => loadPlansData(true)}
            disabled={loading}
            variant="outline"
          >
            <RefreshCw className={`h-4 w-4 mr-2 ${loading ? 'animate-spin' : ''}`} />
            Todos (Incluindo Inativos)
          </Button>
        </div>
      </div>

      {/* Estat√≠sticas */}
      {stats && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium">Total Encontrado</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{stats.total_found}</div>
              <p className="text-xs text-muted-foreground">
                {stats.total_valid} v√°lidos, {stats.total_found - stats.total_valid} inv√°lidos
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium">Por Tipo</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-1">
                <div className="flex justify-between text-sm">
                  <span>Servi√ßos:</span>
                  <span className="font-medium">{stats.by_transaction_type.servico || 0}</span>
                </div>
                <div className="flex justify-between text-sm">
                  <span>Produtos:</span>
                  <span className="font-medium">{stats.by_transaction_type.produto || 0}</span>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium">Por Categoria</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-1">
                <div className="flex justify-between text-sm">
                  <span>Tatuagem:</span>
                  <span className="font-medium">{stats.by_category.tattoo || 0}</span>
                </div>
                <div className="flex justify-between text-sm">
                  <span>Dental:</span>
                  <span className="font-medium">{stats.by_category.dental || 0}</span>
                </div>
                <div className="flex justify-between text-sm">
                  <span>Produtos:</span>
                  <span className="font-medium">{stats.by_category.product || 0}</span>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium">Status</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-2">
                {stats.errors.length > 0 ? (
                  <div className="flex items-center text-red-600">
                    <AlertTriangle className="h-4 w-4 mr-1" />
                    <span className="text-sm">{stats.errors.length} problemas</span>
                  </div>
                ) : (
                  <div className="flex items-center text-green-600">
                    <CheckCircle className="h-4 w-4 mr-1" />
                    <span className="text-sm">Tudo funcionando</span>
                  </div>
                )}
                {lastRefresh && (
                  <p className="text-xs text-muted-foreground">
                    √öltima atualiza√ß√£o: {lastRefresh.toLocaleTimeString()}
                  </p>
                )}
              </div>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Lista de erros se houver */}
      {stats?.errors && stats.errors.length > 0 && (
        <Card className="border-red-200">
          <CardHeader>
            <CardTitle className="text-red-800 flex items-center">
              <AlertTriangle className="h-5 w-5 mr-2" />
              Problemas Detectados
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-2">
              {stats.errors.map((error, index) => (
                <div key={index} className="text-sm text-red-700 bg-red-50 p-2 rounded">
                  {error}
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Lista de planos */}
      <Card>
        <CardHeader>
          <CardTitle>Todos os Planos ({plans.length})</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {plans.map((plan) => (
              <div key={plan.id} className="border rounded-lg p-4">
                <div className="flex justify-between items-start mb-2">
                  <div>
                    <h4 className="font-medium">{plan.name}</h4>
                    <p className="text-sm text-muted-foreground">ID: {plan.id}</p>
                  </div>
                  <div className="text-right">
                    <div className="text-lg font-bold">R$ {plan.price}</div>
                    <div className="text-sm text-muted-foreground">
                      {new Date(plan.created_at).toLocaleDateString()}
                    </div>
                  </div>
                </div>

                <div className="flex flex-wrap gap-2">
                  <Badge className={getStatusColor(plan.active)}>
                    {plan.active ? 'Ativo' : 'Inativo'}
                  </Badge>

                  <Badge className={getCategoryColor(plan.category)}>
                    {plan.category}
                  </Badge>

                  <Badge variant="outline">
                    {plan.tipo_transacao}
                  </Badge>

                  <Badge className={getTableSourceColor(plan.table_source)}>
                    {plan.table_source}
                  </Badge>
                </div>
              </div>
            ))}
          </div>

          {plans.length === 0 && !loading && (
            <div className="text-center py-8 text-muted-foreground">
              Nenhum plano encontrado
            </div>
          )}

          {loading && (
            <div className="text-center py-8">
              <RefreshCw className="h-6 w-6 animate-spin mx-auto mb-2" />
              <div className="text-muted-foreground">Carregando dados...</div>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { useToast } from "@/hooks/use-toast";
import { supabase } from "@/integrations/supabase/client";
import { Building, Users, DollarSign, Package, UserCheck, UserX, Search, Eye } from 'lucide-react';

interface Professional {
  id: string;
  full_name: string;
  email: string;
  role: string;
  approved: boolean;
  created_at: string;
}

interface ProfessionalService {
  id: string;
  name: string;
  description: string;
  price: number;
  category: string;
  active: boolean;
  professional_id: string;
  professional_name: string;
}

export function ProfessionalMarketplaceManager() {
  const [professionals, setProfessionals] = useState<Professional[]>([]);
  const [products, setProducts] = useState<ProfessionalService[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const { toast } = useToast();

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    setLoading(true);
    try {
      // Load professionals
      const { data: professionalsData, error: profError } = await supabase
        .from('profiles')
        .select('*')
        .eq('role', 'professional')
        .order('created_at', { ascending: false });

      if (profError) throw profError;

      // Load products with professional info
      const { data: productsData, error: productsError } = await supabase
        .from('products')
        .select(`
          *,
          professional:profiles!products_professional_id_fkey(full_name)
        `)
        .order('created_at', { ascending: false });

      if (productsError) throw productsError;

      setProfessionals(professionalsData || []);

      const formattedProducts = (productsData || []).map(product => ({
        ...product,
        professional_name: product.professional?.full_name || 'N/A'
      }));

      setProducts(formattedProducts);
    } catch (error) {
      console.error('Erro ao carregar dados:', error);
      toast({
        title: "Erro",
        description: "Erro ao carregar dados dos profissionais.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleApproveProfessional = async (id: string, approved: boolean) => {
    try {
      const { error } = await supabase
        .from('profiles')
        .update({ approved })
        .eq('id', id);

      if (error) throw error;

      setProfessionals(prev => 
        prev.map(prof => 
          prof.id === id ? { ...prof, approved } : prof
        )
      );

      toast({
        title: "Sucesso",
        description: `Profissional ${approved ? 'aprovado' : 'rejeitado'} com sucesso.`,
      });
    } catch (error) {
      console.error('Erro ao atualizar profissional:', error);
      toast({
        title: "Erro",
        description: "Erro ao atualizar status do profissional.",
        variant: "destructive",
      });
    }
  };

  const handleToggleProduct = async (productId: string, currentStatus: boolean) => {
    try {
      const { error } = await supabase
        .from('products')
        .update({ active: !currentStatus })
        .eq('id', productId);

      if (error) throw error;

      setProducts(prev => 
        prev.map(product => 
          product.id === productId 
            ? { ...product, active: !currentStatus }
            : product
        )
      );

      toast({
        title: "Sucesso",
        description: `Produto ${!currentStatus ? 'ativado' : 'desativado'} com sucesso.`,
      });
    } catch (error) {
      console.error('Erro ao atualizar servi√ßo:', error);
      toast({
        title: "Erro",
        description: "Erro ao atualizar servi√ßo.",
        variant: "destructive",
      });
    }
  };

  const filteredProfessionals = professionals.filter(prof =>
    prof.full_name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
    prof.email?.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const filteredProducts = products.filter(product =>
    product.name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
    product.professional_name?.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const pendingProfessionals = professionals.filter(p => !p.approved).length;
  const totalProducts = products.length;
  const activeProducts = products.filter(p => p.active).length;
  const totalRevenue = products.reduce((sum, p) => sum + (p.price || 0), 0);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-3xl font-bold tracking-tight">Marketplace Profissional</h2>
        <div className="relative">
          <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
          <Input
            placeholder="Buscar profissionais ou servi√ßos..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-10 w-80"
          />
        </div>
      </div>

      {/* M√©tricas */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <div className="text-2xl font-bold">{professionals.length}</div>
                <p className="text-sm text-muted-foreground">Total Profissionais</p>
              </div>
              <Building className="h-8 w-8 text-muted-foreground" />
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <div className="text-2xl font-bold text-orange-600">{pendingProfessionals}</div>
                <p className="text-sm text-muted-foreground">Pendentes</p>
              </div>
              <UserCheck className="h-8 w-8 text-muted-foreground" />
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <div className="text-2xl font-bold">{activeProducts}/{totalProducts}</div>
                <p className="text-sm text-muted-foreground">Servi√ßos Ativos</p>
              </div>
              <Package className="h-8 w-8 text-muted-foreground" />
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <div className="text-2xl font-bold">R$ {totalRevenue.toFixed(2)}</div>
                <p className="text-sm text-muted-foreground">Valor Total</p>
              </div>
              <DollarSign className="h-8 w-8 text-muted-foreground" />
            </div>
          </CardContent>
        </Card>
      </div>

      <Tabs defaultValue="professionals" className="w-full">
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="professionals" className="flex items-center gap-2">
            <Users className="h-4 w-4" />
            Profissionais ({professionals.length})
          </TabsTrigger>
          <TabsTrigger value="products" className="flex items-center gap-2">
            <Package className="h-4 w-4" />
            Produtos ({products.length})
          </TabsTrigger>
        </TabsList>

        <TabsContent value="professionals" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Lista de Profissionais</CardTitle>
              <CardDescription>
                Gerencie aprova√ß√µes e monitore atividade dos profissionais
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Nome</TableHead>
                    <TableHead>Email</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>Data Cadastro</TableHead>
                    <TableHead>A√ß√µes</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {filteredProfessionals.map((professional) => (
                    <TableRow key={professional.id}>
                      <TableCell className="font-medium">
                        {professional.full_name || 'Nome n√£o informado'}
                      </TableCell>
                      <TableCell>{professional.email}</TableCell>
                      <TableCell>
                        {professional.approved ? (
                          <Badge className="bg-green-500">Aprovado</Badge>
                        ) : (
                          <Badge variant="destructive">Pendente</Badge>
                        )}
                      </TableCell>
                      <TableCell>
                        {new Date(professional.created_at).toLocaleDateString('pt-BR')}
                      </TableCell>
                      <TableCell>
                        <div className="flex space-x-2">
                          {!professional.approved ? (
                            <Button
                              variant="outline"
                              size="sm"
                              onClick={() => handleApproveProfessional(professional.id, true)}
                            >
                              <UserCheck className="h-4 w-4" />
                            </Button>
                          ) : (
                            <Button
                              variant="outline"
                              size="sm"
                              onClick={() => handleApproveProfessional(professional.id, false)}
                            >
                              <UserX className="h-4 w-4" />
                            </Button>
                          )}
                          <Button variant="outline" size="sm">
                            <Eye className="h-4 w-4" />
                          </Button>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="products" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Servi√ßos dos Profissionais</CardTitle>
              <CardDescription>
                Monitore e gerencie todos os servi√ßos oferecidos
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Servi√ßo</TableHead>
                    <TableHead>Profissional</TableHead>
                    <TableHead>Categoria</TableHead>
                    <TableHead>Pre√ßo</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>A√ß√µes</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {filteredProducts.map((product) => (
                    <TableRow key={product.id}>
                      <TableCell>
                        <div>
                          <div className="font-medium">{product.name}</div>
                          {product.description && (
                            <div className="text-sm text-muted-foreground">
                              {product.description.substring(0, 50)}...
                            </div>
                          )}
                        </div>
                      </TableCell>
                      <TableCell>{product.professional_name}</TableCell>
                      <TableCell>
                        <Badge variant="outline">{product.category || 'N/A'}</Badge>
                      </TableCell>
                      <TableCell>R$ {product.price?.toFixed(2) || '0.00'}</TableCell>
                      <TableCell>
                        {product.active ? (
                          <Badge className="bg-green-500">Ativo</Badge>
                        ) : (
                          <Badge variant="secondary">Inativo</Badge>
                        )}
                      </TableCell>
                      <TableCell>
                        <div className="flex space-x-2">
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => handleToggleProduct(product.id, product.active)}
                          >
                            {product.active ? 'Desativar' : 'Ativar'}
                          </Button>
                          <Button variant="outline" size="sm">
                            <Eye className="h-4 w-4" />
                          </Button>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

