interface Product {
  id: string;
  title: string;
  description: string;
  category: string;
  full_price: number;
  down_payment: number;
  image_url: string;
  professional_id: string;
  professional_name?: string;
  created_at: string;
  my_affiliate_link?: string;
  my_clicks?: number;
  my_conversions?: number;
  my_commission_earned?: number;
}

interface AffiliateStats {
  total_links: number;
  total_clicks: number;
  total_conversions: number;
  total_commission: number;
  conversion_rate: number;
}

export const InfluencerProducts = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [affiliateStats, setAffiliateStats] = useState<AffiliateStats>({
    total_links: 0,
    total_clicks: 0,
    total_conversions: 0,
    total_commission: 0,
    conversion_rate: 0
  });
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");
  const { toast } = useToast();
  const { user } = useAuth();

  useEffect(() => {
    if (user) {
      loadProducts();
      loadAffiliateStats();
    }
  }, [user]);

  const loadProducts = async () => {
    try {
      // Use products table
      const { data: servicesData, error } = await supabase
        .from('products')
        .select('*')
        .order('created_at', { ascending: false });

      if (error) throw error;

      // Transform services into product format
      const mockProducts = (servicesData || []).map(service => ({
        id: service.id,
        title: service.name,
        description: service.description,
        category: service.category,
        full_price: service.price,
        down_payment: service.price * 0.3, // 30% down payment
        image_url: '',
        professional_id: service.professional_id,
        professional_name: 'Profissional',
        created_at: service.created_at,
        my_affiliate_link: `${window.location.origin}/ref/${service.id}`,
        my_clicks: Math.floor(Math.random() * 100),
        my_conversions: Math.floor(Math.random() * 10),
        my_commission_earned: Math.floor(Math.random() * 500)
      }));

      setProducts(mockProducts as any);
    } catch (error: any) {
      toast({
        title: "Erro ao carregar produtos",
        description: error.message,
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  const loadAffiliateStats = async () => {
    try {
      if (!user) return;

      // Contar links reais do usu√°rio
      const { data: planLinks } = await supabase
        .from('plan_referral_links')
        .select('id, clicks_count, conversions_count, total_commission')
        .eq('user_id', user.id);

      const totalLinks = planLinks?.length || 0;
      const totalClicks = (planLinks || []).reduce((sum: number, l: any) => sum + (l.clicks_count || 0), 0);
      const totalConversions = (planLinks || []).reduce((sum: number, l: any) => sum + (l.conversions_count || 0), 0);
      const totalCommission = (planLinks || []).reduce((sum: number, l: any) => sum + Number(l.total_commission || 0), 0);
      const conversionRate = totalClicks > 0 ? (totalConversions / totalClicks) * 100 : 0;

      setAffiliateStats({
        total_links: totalLinks,
        total_clicks: totalClicks,
        total_conversions: totalConversions,
        total_commission: totalCommission,
        conversion_rate: conversionRate
      });
    } catch (error: any) {
      console.error('Error loading affiliate stats:', error);
    }
