      if (!user) throw new Error('N√£o autenticado');

      // Gerar/obter link por plano customizado, se existir
      // Caso n√£o haja plano, usar referral global do profile
      const { data: profile } = await supabase
        .from('profiles')
        .select('referral_code')
        .eq('user_id', user.id)
        .single();

      let affiliateUrl = `${window.location.origin}/inscrever?ref=${profile?.referral_code}`;

      // Update the product in state
      setProducts(prev => prev.map(product => 
        product.id === productId 
          ? { ...product, my_affiliate_link: affiliateUrl }
          : product
      ));

      toast({
        title: "Link de afiliado criado!",
        description: "Seu link √∫nico foi gerado com sucesso.",
      });

      return affiliateUrl;
    } catch (error: any) {
      toast({
        title: "Erro ao criar link",
        description: "Erro ao gerar link de afilia√ß√£o.",
        variant: "destructive"
      });
      return null;
    }
