        title: "Erro ao copiar link",
        description: "N√£o foi poss√≠vel copiar o link.",
        variant: "destructive"
      });
    }
  };

  const shareProduct = async (product: Product) => {
    let shareLink = product.my_affiliate_link;

    if (!shareLink) {
      shareLink = await createAffiliateLink(product.id);
      if (!shareLink) return;
    }

    const shareData = {
      title: product.title,
      text: `${product.description}\n\nConfira este produto incr√≠vel!`,
      url: shareLink
    };

    if (navigator.share) {
      try {
        await navigator.share(shareData);
      } catch (error) {
        copyAffiliateLink(shareLink);
      }
    } else {
      copyAffiliateLink(shareLink);
    }
  };

  const filteredProducts = products.filter(product => {
    const matchesSearch = product.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         product.description.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = selectedCategory === "all" || product.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(value);
  };

  const calculateCommission = (price: number) => {
    return price * 0.25; // 25% sobre a entrada
  };

  if (loading) {
    return (
      <div className="space-y-4">
        {[1, 2, 3].map((i) => (
          <Card key={i}>
            <CardContent className="p-6">
              <div className="animate-pulse space-y-4">
                <div className="h-4 bg-muted rounded w-1/3"></div>
                <div className="h-3 bg-muted rounded w-2/3"></div>
                <div className="h-20 bg-muted rounded"></div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold">Produtos para Afilia√ß√£o</h2>
          <p className="text-muted-foreground">
            Encontre produtos para promover e ganhe 25% de comiss√£o
          </p>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-3">
              <div className="p-2 bg-blue-100 rounded-full">
                <Link2 className="h-5 w-5 text-blue-600" />
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Links Ativos</p>
                <p className="text-2xl font-bold">{affiliateStats.total_links}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-3">
              <div className="p-2 bg-green-100 rounded-full">
                <Eye className="h-5 w-5 text-green-600" />
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Cliques</p>
                <p className="text-2xl font-bold">{affiliateStats.total_clicks}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-3">
              <div className="p-2 bg-purple-100 rounded-full">
                <Target className="h-5 w-5 text-purple-600" />
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Convers√µes</p>
                <p className="text-2xl font-bold">{affiliateStats.total_conversions}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-3">
              <div className="p-2 bg-orange-100 rounded-full">
                <BarChart3 className="h-5 w-5 text-orange-600" />
