interface Transaction {
  id: string;
  client_name: string;
  description: string;
  amount: number;
  commission: number;
  status: string;
  payment_status: string;
  created_at: string;
}

export function ProfessionalDashboard() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [stats, setStats] = useState({
    totalProducts: 0,
    totalEarnings: 0,
    pendingPayments: 0,
    completedJobs: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadDashboardData();
  }, [user]);

  const loadDashboardData = async () => {
    if (!user) return;

    setLoading(true);
    try {
      // Get professional profile
      const { data: professional } = await supabase
        .from('profiles')
        .select('id')
        .eq('user_id', user.id)
        .eq('role', 'professional')
        .single();

      if (!professional) {
        setLoading(false);
        return;
      }

      // Load products count
      const { data: products } = await supabase
        .from('products')
        .select('*')
        .eq('professional_id', professional.id);

      // Load transactions (using credit_transactions as mock)
      const { data: transactionData } = await supabase
        .from('credit_transactions')
        .select('*')
        .eq('user_id', user.id)
        .order('created_at', { ascending: false })
        .limit(10);

      const formattedTransactions: Transaction[] = (transactionData || []).map(transaction => ({
        id: transaction.id,
        client_name: 'Cliente',
        description: transaction.description || 'Servi√ßo prestado',
        amount: Number(transaction.amount),
        commission: Number(transaction.amount) * 0.7, // 70% para o profissional
        status: transaction.status || 'completed',
        payment_status: transaction.status || 'completed',
        created_at: transaction.created_at || new Date().toISOString()
      }));

      setTransactions(formattedTransactions);

      // Calculate stats
      const totalProducts = products?.length || 0;
      const totalEarnings = formattedTransactions
        .filter(t => t.status === 'completed')
        .reduce((sum, t) => sum + t.commission, 0);
      const pendingPayments = formattedTransactions
        .filter(t => t.status === 'pending')
        .reduce((sum, t) => sum + t.commission, 0);

      setStats({
        totalProducts,
        totalEarnings,
        pendingPayments,
        completedJobs: formattedTransactions.filter(t => t.status === 'completed').length
      });

    } catch (error) {
      console.error('Erro ao carregar dados:', error);
      toast({
        title: "Erro",
        description: "Erro ao carregar dados do dashboard.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(value);
  };

  const formatDate = (date: string) => {
    return new Date(date).toLocaleDateString('pt-BR');
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'pending':
        return <Badge variant="secondary">Pendente</Badge>;
      case 'completed':
        return <Badge variant="default">Conclu√≠do</Badge>;
      case 'paid':
        return <Badge>Pago</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div>
        <h2 className="text-3xl font-bold tracking-tight">Dashboard Profissional</h2>
        <p className="text-muted-foreground">
          Acompanhe seus servi√ßos e ganhos
        </p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total de Servi√ßos</CardTitle>
            <Package className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{stats.totalProducts}</div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total de Ganhos</CardTitle>
            <DollarSign className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-green-600">{formatCurrency(stats.totalEarnings)}</div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Pagamentos Pendentes</CardTitle>
            <TrendingUp className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-orange-600">{formatCurrency(stats.pendingPayments)}</div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Trabalhos Conclu√≠dos</CardTitle>
