import { useState, useEffect } from "react";
import { useAuth } from "@/contexts/AuthContext";
import { supabase } from "@/integrations/supabase/client";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { useToast } from "@/hooks/use-toast";
import { 
  Users, 
  Share2, 
  Copy, 
  CheckCircle, 
  Clock,
  ExternalLink,
  Calendar
} from "lucide-react";

interface GroupData {
  id: string;
  referral_code: string;
  status: 'forming' | 'complete' | 'cancelled';
  current_participants: number;
  max_participants: number;
  current_amount: number;
  target_amount: number;
  created_at: string;
  contemplated_at: string | null;
  service_name: string;
  service_category: string;
  user_amount_paid: number;
}

export const GroupDashboard = () => {
  const { user } = useAuth();
  const { toast } = useToast();
  const [groups, setGroups] = useState<GroupData[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (user) {
      loadUserGroups();
    }
  }, [user]);

  const loadUserGroups = async () => {
    try {
      setLoading(true);

      const { data, error } = await supabase
        .from('group_participants')
        .select(`
          amount_paid,
          joined_at,
          status,
          plan_groups!inner(
            id,
            referral_code,
            status,
            current_participants,
            max_participants,
            current_amount,
            target_amount,
            created_at,
            contemplated_at,
            service_id
          )
        `)
        .eq('user_id', user?.id)
        .eq('status', 'active')
        .order('joined_at', { ascending: false });

      if (error) throw error;

      // Para cada grupo, buscar os detalhes do plano
      const groupsWithPlans = await Promise.all(
        (data || []).map(async (item) => {
          const { data: planData } = await supabase
            .from('custom_plans')
            .select('name, category')
            .eq('id', item.plan_groups.service_id)
            .single();

          return {
            id: item.plan_groups.id,
            referral_code: item.plan_groups.referral_code,
            status: item.plan_groups.status as 'forming' | 'complete' | 'cancelled',
            current_participants: item.plan_groups.current_participants,
            max_participants: item.plan_groups.max_participants,
            current_amount: item.plan_groups.current_amount,
            target_amount: item.plan_groups.target_amount,
            created_at: item.plan_groups.created_at,
            contemplated_at: item.plan_groups.contemplated_at,
            service_name: planData?.name || 'Servi√ßo',
            service_category: planData?.category || 'service',
            user_amount_paid: item.amount_paid
          };
        })
      );

      setGroups(groupsWithPlans);
    } catch (error) {
      console.error('Erro ao carregar grupos:', error);
      toast({
        title: "Erro",
        description: "Erro ao carregar seus grupos.",
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  const copyReferralLink = async (referralCode: string) => {
    const linkToCopy = `${window.location.origin}/plano/${groups[0]?.id}?ref=${referralCode}`;

    try {
      await navigator.clipboard.writeText(linkToCopy);
      toast({
        title: "‚úÖ Link copiado!",
        description: "Link de convite copiado para a √°rea de transfer√™ncia.",
        duration: 3000
      });
    } catch (error) {
      // Fallback para dispositivos m√≥veis
      const textArea = document.createElement('textarea');
      textArea.value = linkToCopy;
      textArea.style.position = 'fixed';
      textArea.style.left = '-9999px';
      document.body.appendChild(textArea);
      textArea.select();

      try {
        document.execCommand('copy');
        document.body.removeChild(textArea);
        toast({
          title: "‚úÖ Link copiado!",
          description: "Link de convite copiado para a √°rea de transfer√™ncia.",
        });
      } catch (fallbackError) {
        document.body.removeChild(textArea);
        toast({
          title: "Erro ao copiar",
          description: "N√£o foi poss√≠vel copiar automaticamente. Tente novamente.",
          variant: "destructive"
        });
      }
    }
  };

  const shareReferralLink = (referralCode: string) => {
    const linkToShare = `${window.location.origin}/plano/${groups[0]?.id}?ref=${referralCode}`;

    if (navigator.share) {
      navigator.share({
        title: "Participe do meu grupo!",
        text: `Venha fazer parte do meu grupo para ${groups[0]?.service_name} e economizar!`,
        url: linkToShare,
      });
    } else {
      copyReferralLink(referralCode);
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'complete': return 'bg-green-100 text-green-800';
      case 'forming': return 'bg-blue-100 text-blue-800';
      case 'cancelled': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusLabel = (status: string) => {
    switch (status) {
      case 'complete': return 'Completo';
      case 'forming': return 'Formando';
      case 'cancelled': return 'Cancelado';
      default: return status;
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center py-12">
        <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-primary" />
      </div>
    );
  }

  if (groups.length === 0) {
    return (
      <Card>
        <CardContent className="p-8 text-center">
          <Users className="h-16 w-16 mx-auto mb-4 text-muted-foreground opacity-50" />
          <h3 className="text-xl font-semibold mb-2">Nenhum grupo encontrado</h3>
          <p className="text-muted-foreground mb-4">
            Voc√™ ainda n√£o est√° participando de nenhum grupo.
          </p>
          <Button onClick={() => window.location.href = '/plans'}>
            Escolher um Plano
          </Button>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-2xl font-bold">Meus Grupos</h2>
        <Badge variant="secondary">
          {groups.length} grupo{groups.length !== 1 ? 's' : ''}
        </Badge>
      </div>

      {groups.map((group) => (
        <Card key={group.id}>
          <CardHeader>
            <div className="flex items-center justify-between">
              <div>
                <CardTitle className="flex items-center gap-2">
                  {group.service_name}
                  <Badge className={getStatusColor(group.status)}>
                    {getStatusLabel(group.status)}
                  </Badge>
                </CardTitle>
                <p className="text-sm text-muted-foreground mt-1">
                  C√≥digo do grupo: <span className="font-mono font-medium">{group.referral_code}</span>
                </p>
              </div>
              {group.status === 'complete' && (
                <div className="flex items-center gap-2 text-green-600">
                  <CheckCircle className="h-5 w-5" />
                  <span className="text-sm font-medium">Grupo Completo!</span>
                </div>
              )}
            </div>
          </CardHeader>

          <CardContent className="space-y-6">
            {/* Progress */}
            <div>
              <div className="flex items-center justify-between mb-2">
                <span className="text-sm font-medium">Progresso do Grupo</span>
                <span className="text-sm text-muted-foreground">
                  {group.current_participants}/{group.max_participants} pessoas
                </span>
              </div>
              <Progress 
                value={(group.current_participants / group.max_participants) * 100} 
                className="h-2"
              />
              <div className="flex items-center justify-between mt-1 text-xs text-muted-foreground">
                <span>R$ {group.current_amount.toLocaleString()} arrecadado</span>
                <span>Meta: R$ {group.target_amount.toLocaleString()}</span>
              </div>
            </div>

            {/* Stats */}
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              <div className="text-center p-3 bg-muted/50 rounded-lg">
                <Users className="h-5 w-5 mx-auto mb-1 text-muted-foreground" />
                <div className="text-sm font-medium">{group.current_participants}</div>
                <div className="text-xs text-muted-foreground">Participantes</div>
              </div>

              <div className="text-center p-3 bg-muted/50 rounded-lg">
                <Clock className="h-5 w-5 mx-auto mb-1 text-muted-foreground" />
                <div className="text-sm font-medium">
                  {Math.max(0, group.max_participants - group.current_participants)}
                </div>
                <div className="text-xs text-muted-foreground">Restam</div>
              </div>

              <div className="text-center p-3 bg-muted/50 rounded-lg">
                <div className="text-sm font-medium">R$ {group.user_amount_paid.toLocaleString()}</div>
                <div className="text-xs text-muted-foreground">Sua contribui√ß√£o</div>
              </div>

              <div className="text-center p-3 bg-muted/50 rounded-lg">
                <Calendar className="h-5 w-5 mx-auto mb-1 text-muted-foreground" />
                <div className="text-sm font-medium">
                  {new Date(group.created_at).toLocaleDateString('pt-BR')}
                </div>
                <div className="text-xs text-muted-foreground">Criado em</div>
              </div>
            </div>

            {/* Actions */}
            {group.status === 'forming' && (
              <div className="space-y-3">
                <div className="p-4 bg-blue-50 border border-blue-200 rounded-lg">
                  <div className="flex items-center gap-2 mb-2">
                    <Share2 className="h-4 w-4 text-blue-600" />
                    <span className="font-medium text-blue-800">Compartilhe e complete seu grupo</span>
                  </div>
                  <p className="text-sm text-blue-600 mb-3">
                    Compartilhe o link abaixo para que mais {group.max_participants - group.current_participants} 
                    {group.max_participants - group.current_participants === 1 ? ' pessoa se junte' : ' pessoas se juntem'} 
                    ao seu grupo:
                  </p>

                  <div className="flex gap-2">
                    <Button 
                      variant="outline" 
                      size="sm"
                      onClick={() => copyReferralLink(group.referral_code)}
                      className="flex-1"
                    >
                      <Copy className="h-4 w-4 mr-2" />
                      Copiar Link
                    </Button>
                    <Button 
                      size="sm"
                      onClick={() => shareReferralLink(group.referral_code)}
                      className="flex-1"
                    >
                      <Share2 className="h-4 w-4 mr-2" />
                      Compartilhar
                    </Button>
                  </div>

                  <div className="mt-3 p-2 bg-white rounded border text-xs font-mono text-gray-600 break-all">
                    {`${window.location.origin}/plano/${group.id}?ref=${group.referral_code}`}
                  </div>
                </div>
              </div>
            )}

            {group.status === 'complete' && group.contemplated_at && (
              <div className="p-4 bg-green-50 border border-green-200 rounded-lg">
                <div className="flex items-center gap-2 mb-2">
                  <CheckCircle className="h-4 w-4 text-green-600" />
                  <span className="font-medium text-green-800">Parab√©ns! Grupo Completo</span>
                </div>
                <p className="text-sm text-green-600 mb-3">
                  Seu grupo foi contemplado em{' '}
                  {new Date(group.contemplated_at).toLocaleDateString('pt-BR')}. 
                  Agora voc√™ pode agendar seu servi√ßo!
                </p>
                <Button size="sm" className="w-full">
                  <Calendar className="h-4 w-4 mr-2" />
                  Agendar Servi√ßo
                </Button>
              </div>
            )}
          </CardContent>
        </Card>
      ))}
    </div>
  );
};

import { useState, useEffect } from "react";
import { useAuth } from "@/contexts/AuthContext";
import { useNavigate } from "react-router-dom";
import { supabase } from "@/integrations/supabase/client";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { 
  Users, 
  Target, 
  Clock, 
  CheckCircle, 
  AlertCircle,
  TrendingUp,
  Calendar,
  Gift,
  Copy
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { formatCurrency } from "@/lib/utils";

interface PlanParticipation {
  id: string;
  plan_id: string;
  group_id: string;
  contemplation_status: string;
  payment_status: string;
  contemplation_date?: string;
  joined_at: string;
  plan_groups: {
    current_participants: number;
    status: string;
    start_date?: string;
    end_date?: string;
  };
  custom_plans: {
    name: string;
    entry_price: number;
    total_price: number;
    max_participants: number;
  };
}

interface ReferralData {
  referralCode: string;
  totalReferrals: number;
  confirmedReferrals: number;
  pendingReferrals: number;
}

export const ParticipationDashboard = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const { toast } = useToast();
  const [participations, setParticipations] = useState<PlanParticipation[]>([]);
  const [referralData, setReferralData] = useState<ReferralData | null>(null);
  const [loading, setLoading] = useState(true);
  const [copySuccess, setCopySuccess] = useState(false);

  useEffect(() => {
    if (user) {
      loadParticipationData();
      loadReferralData();
    }
  }, [user]);

  const loadParticipationData = async () => {
    try {
      const { data, error } = await supabase
        .from('group_participants')
        .select(`
          *,
          plan_groups(*)
        `)
        .eq('user_id', user?.id)
        .order('joined_at', { ascending: false });

      if (error) throw error;

      if (data) {
        const formattedParticipations: PlanParticipation[] = data.map(participation => ({
          id: participation.id,
          plan_id: participation.group_id,
          group_id: participation.group_id,
          contemplation_status: participation.plan_groups?.status === 'complete' ? 'contemplated' : 'waiting',
          payment_status: 'paid', // Default since they're participants
          contemplation_date: participation.plan_groups?.contemplated_at,
          joined_at: participation.joined_at,
          plan_groups: {
            current_participants: participation.plan_groups?.current_participants || 0,
            status: participation.plan_groups?.status || 'forming',
            start_date: participation.plan_groups?.created_at,
            end_date: participation.plan_groups?.contemplated_at,
          },
          custom_plans: {
            name: `Grupo ${participation.plan_groups?.group_number || 'N/A'}`,
            entry_price: participation.amount_paid || 100,
            total_price: participation.plan_groups?.target_amount || 1000,
            max_participants: participation.plan_groups?.max_participants || 10,
          }
        }));
        setParticipations(formattedParticipations);
      }
    } catch (error) {
      console.error('Erro ao carregar participa√ß√µes:', error);
    }
  };

  const loadReferralData = async () => {
    try {
      // Buscar dados do perfil do usu√°rio para c√≥digo de indica√ß√£o
      const { data: profileData, error: profileError } = await supabase
        .from('profiles')
        .select('referral_code')
        .eq('user_id', user?.id)
        .single();

      // Contar refer√™ncias diretas
      const { count: totalReferrals } = await supabase
        .from('profiles')
        .select('id', { count: 'exact' })
        .eq('referred_by', user?.id);

      console.log('Profile data:', profileData);
      console.log('User ID:', user?.id);

      if (profileData?.referral_code) {
        const referralCode = profileData.referral_code;
        console.log('C√≥digo de refer√™ncia encontrado:', referralCode);

        setReferralData({
          referralCode: referralCode,
          totalReferrals: totalReferrals || 0,
          confirmedReferrals: totalReferrals || 0,
          pendingReferrals: 0
        });
      } else {
        // Fallback: usar parte do ID do usu√°rio
        const fallbackCode = user?.id?.slice(-8).toUpperCase() || 'N/A';
        console.log('Usando c√≥digo fallback:', fallbackCode);

        setReferralData({
          referralCode: fallbackCode,
          totalReferrals: totalReferrals || 0,
          confirmedReferrals: totalReferrals || 0,
          pendingReferrals: 0
        });
      }
    } catch (error) {
      console.error('Erro ao carregar dados de indica√ß√£o:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusBadge = (contemplationStatus: string, paymentStatus: string) => {
    if (contemplationStatus === 'contemplated') {
      return <Badge className="bg-green-100 text-green-800">Contemplado</Badge>;
    }
    if (contemplationStatus === 'waiting' && paymentStatus === 'pending') {
      return <Badge className="bg-blue-100 text-blue-800">Em Andamento</Badge>;
    }
    if (paymentStatus === 'paid') {
      return <Badge className="bg-purple-100 text-purple-800">Finalizado</Badge>;
    }
    return <Badge variant="secondary">Aguardando</Badge>;
  };

  const getProgressPercentage = (currentParticipants: number, maxParticipants: number) => {
    return Math.min((currentParticipants / maxParticipants) * 100, 100);
  };

  const copyReferralLink = async () => {
    if (!referralData?.referralCode) {
      return;
    }

    const link = `${window.location.origin}/register?ref=${referralData.referralCode}`;
    console.log('üöÄ Copiando link:', link);

    try {
      await navigator.clipboard.writeText(link);
      console.log('‚úÖ Link copiado!');
      setCopySuccess(true);
      setTimeout(() => setCopySuccess(false), 2000);
    } catch (error) {
      console.log('‚ö†Ô∏è Erro ao copiar, usando fallback');
      // Fallback simples
      const input = document.createElement('input');
      input.value = link;
      document.body.appendChild(input);
      input.select();
      document.execCommand('copy');
      document.body.removeChild(input);

      setCopySuccess(true);
      setTimeout(() => setCopySuccess(false), 2000);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-32">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  // Participa√ß√£o ativa mais recente
  const activeParticipation = participations.find(p => 
    p.contemplation_status === 'waiting' && p.payment_status === 'pending'
  );

  return (
    <div className="space-y-6">
      {/* Status da Participa√ß√£o Atual */}
      {activeParticipation ? (
        <Card className="border-primary/20 bg-primary/5">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Target className="h-5 w-5 text-primary" />
              Plano Atual: {activeParticipation.custom_plans.name}
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="text-center">
                <div className="text-2xl font-bold text-primary">
                  {activeParticipation.plan_groups.current_participants}
                </div>
                <div className="text-sm text-muted-foreground">Participantes Atuais</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold">
                  {activeParticipation.custom_plans.max_participants}
                </div>
                <div className="text-sm text-muted-foreground">Meta do Grupo</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-orange-600">
                  {activeParticipation.custom_plans.max_participants - activeParticipation.plan_groups.current_participants}
                </div>
                <div className="text-sm text-muted-foreground">Faltam</div>
              </div>
            </div>

            <div className="space-y-2">
              <div className="flex justify-between text-sm">
                <span>Progresso do Grupo</span>
                <span>
                  {activeParticipation.plan_groups.current_participants}/
                  {activeParticipation.custom_plans.max_participants}
                </span>
              </div>
              <Progress 
                value={getProgressPercentage(
                  activeParticipation.plan_groups.current_participants,
                  activeParticipation.custom_plans.max_participants
                )} 
                className="h-3"
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
              <div>
                <span className="text-muted-foreground">Valor da Entrada:</span>
                <span className="ml-2 font-semibold">
                  {formatCurrency(activeParticipation.custom_plans.entry_price)}
                </span>
              </div>
              <div>
                <span className="text-muted-foreground">Valor Total:</span>
                <span className="ml-2 font-semibold">
                  {formatCurrency(activeParticipation.custom_plans.total_price)}
                </span>
              </div>
            </div>

            {getStatusBadge(activeParticipation.contemplation_status, activeParticipation.payment_status)}
          </CardContent>
        </Card>
      ) : (
        <Card className="border-dashed border-2 border-muted">
          <CardContent className="p-8 text-center">
            <Gift className="h-16 w-16 mx-auto mb-4 text-muted-foreground opacity-50" />
            <h3 className="text-lg font-semibold mb-2">Nenhum plano ativo</h3>
            <p className="text-muted-foreground mb-4">
              Participe de um plano para come√ßar a formar seu grupo!
            </p>
            <Button onClick={() => navigate('/plans')}>Escolher Plano</Button>
          </CardContent>
        </Card>
      )}

      {/* Dados de Indica√ß√£o */}
      {referralData && (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {/* Status das Indica√ß√µes */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Users className="h-5 w-5" />
                Minhas Indica√ß√µes
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-3 gap-4 text-center">
                <div>
                  <div className="text-2xl font-bold text-blue-600">
                    {referralData.totalReferrals}
                  </div>
                  <div className="text-xs text-muted-foreground">Total</div>
                </div>
                <div>
                  <div className="text-2xl font-bold text-green-600">
                    {referralData.confirmedReferrals}
                  </div>
                  <div className="text-xs text-muted-foreground">Confirmadas</div>
                </div>
                <div>
                  <div className="text-2xl font-bold text-orange-600">
                    {referralData.pendingReferrals}
                  </div>
                  <div className="text-xs text-muted-foreground">Pendentes</div>
                </div>
              </div>

              <div className="space-y-2">
                <div className="flex justify-between text-sm">
                  <span>Progresso Pessoal</span>
                  <span>{referralData.confirmedReferrals}/9</span>
                </div>
                <Progress 
                  value={Math.min((referralData.confirmedReferrals / 9) * 100, 100)} 
                  className="h-2"
                />
              </div>
            </CardContent>
          </Card>

          {/* Link de Indica√ß√£o */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <TrendingUp className="h-5 w-5" />
                Meu Link de Indica√ß√£o
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="p-3 bg-muted rounded-lg">
                <div className="text-sm font-medium text-muted-foreground mb-1">
                  Seu c√≥digo:
                </div>
                <div className="font-mono text-lg font-bold">
                  {referralData.referralCode}
                </div>
              </div>

              <Button onClick={copyReferralLink} className="w-full relative">
                <Copy className="h-4 w-4 mr-2" />
                Copiar Link de Indica√ß√£o
              </Button>

              {/* Mensagem de sucesso diretamente abaixo do bot√£o */}
              {copySuccess && (
                <div className="mt-2 text-green-400 text-center font-semibold animate-in fade-in duration-300">
                  ‚úÖ Link copiado!
                </div>
              )}

              <div className="text-xs text-muted-foreground">
                Compartilhe este link para que amigos se juntem ao seu grupo
              </div>

              {/* Fallback: mostrar link para c√≥pia manual */}
              <div className="mt-2 p-2 bg-muted rounded text-xs">
                <p className="font-medium mb-1">Link completo (caso precise copiar manualmente):</p>
                <p className="font-mono text-muted-foreground break-all select-all">
                  {referralData ? `${window.location.origin}/register?ref=${referralData.referralCode}` : 'Carregando...'}
                </p>
              </div>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Hist√≥rico de Participa√ß√µes */}
      {participations.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Calendar className="h-5 w-5" />
              Hist√≥rico de Participa√ß√µes
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {participations.map((participation) => (
                <div 
                  key={participation.id}
                  className="flex items-center justify-between p-4 border rounded-lg"
                >
                  <div className="flex-1">
                    <h4 className="font-medium">{participation.custom_plans.name}</h4>
                    <p className="text-sm text-muted-foreground">
                      Participou em: {new Date(participation.joined_at).toLocaleDateString('pt-BR')}
                    </p>
                    <p className="text-sm">
                      Grupo: {participation.plan_groups.current_participants}/
                      {participation.custom_plans.max_participants} participantes
                    </p>
                  </div>

                  <div className="text-right">
                    {getStatusBadge(participation.contemplation_status, participation.payment_status)}
                    {participation.contemplation_date && (
                      <p className="text-xs text-muted-foreground mt-1">
                        Contemplado em: {new Date(participation.contemplation_date).toLocaleDateString('pt-BR')}
                      </p>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Copy, Users, Clock, DollarSign, Share, Gift, ExternalLink } from 'lucide-react';
import { useToast } from "@/hooks/use-toast";
import { supabase } from "@/integrations/supabase/client";
import { useAuth } from "@/contexts/AuthContext";

interface PlanProgress {
  participation_id: string;
  plan_code: string;
  plan_name: string;
  description: string;
  category_name: string;
  total_price: number;
  entry_amount: number;
  group_number: number;
  position_number: number;
  current_participants: number;
  max_participants: number;
  group_status: string;
  payment_status: string;
  contemplated: boolean;
  service_completed: boolean;
  professional_name: string;
  especialidade: string;
  local_atendimento: string;
  enrollment_date: string;
  contemplation_date: string;
  service_completion_date: string;
}

interface ReferralLink {
  id: string;
  referral_code: string;
  link_url: string;
  clicks_count: number;
  conversions_count: number;
  total_commission: number;
}

interface Commission {
  id: string;
  participation_amount: number;
  commission_amount: number;
  status: string;
  created_at: string;
  referrer_name: string;
}

export function PlanProgress() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [plans, setPlans] = useState<PlanProgress[]>([]);
  const [referralLinks, setReferralLinks] = useState<ReferralLink[]>([]);
  const [commissions, setCommissions] = useState<Commission[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (user) {
      loadUserData();
    }
  }, [user]);

  const loadUserData = async () => {
    setLoading(true);
    try {
      // Usar o ID do usu√°rio diretamente
      const userId = user?.id;
      if (!userId) return;

      // Carregar participa√ß√µes do usu√°rio
      const { data: plansData } = await supabase
        .from('group_participants')
        .select('*, plan_groups(*)')
        .eq('user_id', userId)
        .order('joined_at', { ascending: false });

      // Transformar dados para o formato esperado
      const formattedPlans = (plansData || []).map(participant => ({
        participation_id: participant.id,
        plan_code: `GROUP-${participant.id.slice(-4)}`,
        plan_name: `Grupo ${participant.plan_groups?.group_number || 'N/A'}`,
        description: 'Grupo de contempla√ß√£o',
        category_name: 'Servi√ßos',
        total_price: participant.plan_groups?.target_amount || 1000,
        entry_amount: participant.amount_paid || 100,
        group_number: participant.plan_groups?.group_number || 1,
        position_number: 1,
        current_participants: participant.plan_groups?.current_participants || 1,
        max_participants: participant.plan_groups?.max_participants || 10,
        group_status: participant.plan_groups?.status || 'forming',
        payment_status: 'pago',
        contemplated: participant.plan_groups?.status === 'complete',
        service_completed: false,
        professional_name: '',
        especialidade: '',
        local_atendimento: '',
        enrollment_date: participant.joined_at,
        contemplation_date: participant.plan_groups?.contemplated_at || '',
        service_completion_date: ''
      }));

      setPlans(formattedPlans);

      // Simular links de refer√™ncia vazios
      setReferralLinks([]);

      // Simular comiss√µes vazias
      setCommissions([]);


    } catch (error) {
      console.error('Erro ao carregar dados:', error);
      toast({
        title: "Erro",
        description: "Erro ao carregar seus dados. Tente novamente.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const generateReferralLink = async (planCode: string) => {
    try {
      // Simular gera√ß√£o de link
      const referralCode = `REF-${user?.id?.slice(-8)}-${Date.now()}`;
      const linkUrl = `/?ref=${referralCode}`;

      toast({
        title: "Link gerado!",
        description: "Seu link de indica√ß√£o foi criado com sucesso.",
      });

      // Adicionar √† lista de links
      const newLink: ReferralLink = {
        id: Date.now().toString(),
        referral_code: referralCode,
        link_url: linkUrl,
        clicks_count: 0,
        conversions_count: 0,
        total_commission: 0
      };

      setReferralLinks(prev => [...prev, newLink]);

    } catch (error) {
      console.error('Erro ao gerar link:', error);
      toast({
        title: "Erro",
        description: "Erro ao gerar link de indica√ß√£o.",
        variant: "destructive",
      });
    }
  };

  const copyReferralLink = (linkUrl: string) => {
    const fullUrl = `${window.location.origin}${linkUrl}`;
    navigator.clipboard.writeText(fullUrl);
    toast({
      title: "Link copiado!",
      description: "Link de indica√ß√£o copiado para a √°rea de transfer√™ncia.",
    });
  };

  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(value);
  };

  const getStatusBadge = (status: string) => {
    const statusMap = {
      'forming': { label: 'Formando', variant: 'secondary' as const },
      'full': { label: 'Completo', variant: 'default' as const },
      'contemplating': { label: 'Contemplando', variant: 'default' as const },
      'completed': { label: 'Finalizado', variant: 'default' as const }
    };

    return statusMap[status as keyof typeof statusMap] || { label: status, variant: 'outline' as const };
  };

  const getProgressPercentage = (current: number, max: number) => {
    return Math.round((current / max) * 100);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div>
        <h2 className="text-3xl font-bold tracking-tight">Meus Planos</h2>
        <p className="text-muted-foreground">
          Acompanhe o progresso dos seus grupos e suas indica√ß√µes
        </p>
      </div>

      <Tabs defaultValue="progress" className="space-y-4">
        <TabsList>
          <TabsTrigger value="progress">Progresso dos Grupos</TabsTrigger>
          <TabsTrigger value="referrals">Indica√ß√µes</TabsTrigger>
          <TabsTrigger value="commissions">Comiss√µes</TabsTrigger>
        </TabsList>

        <TabsContent value="progress" className="space-y-4">
          {plans.length === 0 ? (
            <Card>
              <CardContent className="text-center py-8">
                <Users className="mx-auto h-12 w-12 text-muted-foreground mb-4" />
                <h3 className="text-lg font-semibold mb-2">Nenhum plano encontrado</h3>
                <p className="text-muted-foreground mb-4">
                  Voc√™ ainda n√£o est√° inscrito em nenhum plano.
                </p>
                <Button onClick={() => window.location.href = '/'}>
                  Explorar Planos
                </Button>
              </CardContent>
            </Card>
          ) : (
            plans.map((plan) => (
              <Card key={plan.participation_id}>
                <CardHeader>
                  <div className="flex items-start justify-between">
                    <div>
                      <CardTitle className="flex items-center space-x-2">
                        <span>{plan.plan_name}</span>
                        <Badge variant="outline">{plan.category_name}</Badge>
                        <Badge {...getStatusBadge(plan.group_status)}>
                          {getStatusBadge(plan.group_status).label}
                        </Badge>
                      </CardTitle>
                      <CardDescription>
                        Grupo {plan.group_number} ‚Ä¢ Posi√ß√£o {plan.position_number}
                      </CardDescription>
                    </div>
                    <div className="text-right">
                      <div className="text-2xl font-bold text-primary">
                        {formatCurrency(plan.entry_amount)}
                      </div>
                      <div className="text-sm text-muted-foreground">pago</div>
                    </div>
                  </div>
                </CardHeader>

                <CardContent className="space-y-4">
                  {/* Progresso do grupo */}
                  <div>
                    <div className="flex justify-between text-sm mb-2">
                      <span>Progresso do Grupo</span>
                      <span>{plan.current_participants}/{plan.max_participants} participantes</span>
                    </div>
                    <Progress 
                      value={getProgressPercentage(plan.current_participants, plan.max_participants)} 
                      className="w-full"
                    />
                  </div>

                  {/* Status especiais */}
                  {plan.contemplated && (
                    <Alert>
                      <Gift className="h-4 w-4" />
                      <AlertDescription>
                        üéâ <strong>Parab√©ns!</strong> Voc√™ foi contemplado! 
                        {plan.contemplation_date && (
                          <span> em {new Date(plan.contemplation_date).toLocaleDateString('pt-BR')}</span>
                        )}
                      </AlertDescription>
                    </Alert>
                  )}

                  {plan.service_completed && (
                    <Alert>
                      <AlertDescription>
                        ‚úÖ Servi√ßo realizado com sucesso!
                        {plan.service_completion_date && (
                          <span> em {new Date(plan.service_completion_date).toLocaleDateString('pt-BR')}</span>
                        )}
                      </AlertDescription>
                    </Alert>
                  )}

                  {/* Informa√ß√µes do profissional */}
                  {plan.professional_name && (
                    <div className="bg-muted p-3 rounded-lg">
                      <h4 className="font-semibold">Profissional Designado</h4>
                      <p className="text-sm">
                        {plan.professional_name} - {plan.especialidade}
                      </p>
                      <p className="text-sm text-muted-foreground">
                        {plan.local_atendimento}
                      </p>
                    </div>
                  )}

                  {/* A√ß√µes */}
                  <div className="flex space-x-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => generateReferralLink(plan.plan_code)}
                    >
                      <Share className="mr-2 h-4 w-4" />
                      Gerar Link de Indica√ß√£o
                    </Button>

                    {plan.contemplated && !plan.service_completed && (
                      <Button size="sm">
                        <ExternalLink className="mr-2 h-4 w-4" />
                        Agendar Servi√ßo
                      </Button>
                    )}
                  </div>
                </CardContent>
              </Card>
            ))
          )}
        </TabsContent>

        <TabsContent value="referrals" className="space-y-4">
          <div className="grid gap-4">
            {referralLinks.map((link) => (
              <Card key={link.id}>
                <CardHeader>
                  <CardTitle className="flex items-center justify-between">
                    <span>Link de Indica√ß√£o</span>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => copyReferralLink(link.link_url)}
                    >
                      <Copy className="mr-2 h-4 w-4" />
                      Copiar
                    </Button>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="bg-muted p-3 rounded-lg mb-4">
                    <code className="text-sm break-all">
                      {window.location.origin}{link.link_url}
                    </code>
                  </div>

                  <div className="grid grid-cols-3 gap-4 text-center">
                    <div>
                      <div className="text-2xl font-bold text-blue-600">{link.clicks_count}</div>
                      <div className="text-sm text-muted-foreground">Cliques</div>
                    </div>
                    <div>
                      <div className="text-2xl font-bold text-green-600">{link.conversions_count}</div>
                      <div className="text-sm text-muted-foreground">Convers√µes</div>
                    </div>
                    <div>
                      <div className="text-2xl font-bold text-purple-600">
                        {formatCurrency(link.total_commission)}
                      </div>
                      <div className="text-sm text-muted-foreground">Comiss√£o</div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </TabsContent>

        <TabsContent value="commissions" className="space-y-4">
          {commissions.length === 0 ? (
            <Card>
              <CardContent className="text-center py-8">
                <DollarSign className="mx-auto h-12 w-12 text-muted-foreground mb-4" />
                <h3 className="text-lg font-semibold mb-2">Nenhuma comiss√£o encontrada</h3>
                <p className="text-muted-foreground">
                  Compartilhe seus links de indica√ß√£o para ganhar comiss√µes!
                </p>
              </CardContent>
            </Card>
          ) : (
            <div className="space-y-4">
              {/* Resumo das comiss√µes */}
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <Card>
                  <CardContent className="text-center p-4">
                    <div className="text-2xl font-bold text-green-600">
                      {formatCurrency(commissions.filter(c => c.status === 'paid').reduce((sum, c) => sum + c.commission_amount, 0))}
                    </div>
                    <div className="text-sm text-muted-foreground">Comiss√µes Pagas</div>
                  </CardContent>
                </Card>
                <Card>
                  <CardContent className="text-center p-4">
                    <div className="text-2xl font-bold text-orange-600">
                      {formatCurrency(commissions.filter(c => c.status === 'pending').reduce((sum, c) => sum + c.commission_amount, 0))}
                    </div>
                    <div className="text-sm text-muted-foreground">Pendentes</div>
                  </CardContent>
                </Card>
                <Card>
                  <CardContent className="text-center p-4">
                    <div className="text-2xl font-bold text-purple-600">
                      {formatCurrency(commissions.reduce((sum, c) => sum + c.commission_amount, 0))}
                    </div>
                    <div className="text-sm text-muted-foreground">Total</div>
                  </CardContent>
                </Card>
              </div>

              {/* Lista de comiss√µes */}
              {commissions.map((commission) => (
                <Card key={commission.id}>
                  <CardContent className="p-4">
                    <div className="flex items-center justify-between">
                      <div>
                        <div className="font-semibold">
                          Indica√ß√£o: {commission.referrer_name}
                        </div>
                        <div className="text-sm text-muted-foreground">
                          {new Date(commission.created_at).toLocaleDateString('pt-BR')}
                        </div>
                      </div>
                      <div className="text-right">
                        <div className="font-bold text-lg">
                          {formatCurrency(commission.commission_amount)}
                        </div>
                        <Badge 
                          variant={commission.status === 'paid' ? 'default' : 'secondary'}
                        >
                          {commission.status === 'paid' ? 'Pago' : 'Pendente'}
                        </Badge>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </TabsContent>
      </Tabs>
    </div>
  );
}

import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { supabase } from "@/integrations/supabase/client";
import { Users, DollarSign, Calendar } from "lucide-react";

interface Plan {
  id: string;
  name: string;
  description: string;
  price: number;
  max_participants: number;
  image_url?: string;
  active: boolean;
  created_at: string;
}

interface PlansDisplayProps {
  category: 'tatuador' | 'dentista';
  title: string;
  onSelectPlan?: (plan: Plan) => void;
}

export const PlansDisplay = ({ category, title, onSelectPlan }: PlansDisplayProps) => {
  const [plans, setPlans] = useState<Plan[]>([]);
  const [loading, setLoading] = useState(true);
  const { toast } = useToast();

  const tableMap = {
    tatuador: 'planos_tatuador',
    dentista: 'planos_dentista'
  };

  const loadPlans = async () => {
    try {
      setLoading(true);
      console.log('üîç Carregando planos para categoria:', category);

      // **SOLU√á√ÉO: Usar edge function unificada com filtro de categoria**
      const { data: response, error } = await supabase.functions.invoke('unified-plans-loader', {
        body: {
          category_filter: category === 'tatuador' ? 'tattoo' : 'dental'
        }
      });

      if (error) {
        console.error('‚ùå Erro na edge function unified-plans-loader:', error);
        throw error;
      }

      if (!response?.success) {
        console.error('‚ùå Resposta inv√°lida da edge function:', response);
        throw new Error(response?.error || 'Resposta inv√°lida do servidor');
      }

      const plans = response.plans || [];
      console.log('üìä Estat√≠sticas dos planos filtrados:', response.stats);
      console.log('üìù Planos encontrados para', category, ':', plans.length);

      setPlans(plans);

    } catch (error) {
      console.error('‚ùå Erro ao carregar planos:', error);
      toast({
        title: "Erro",
        description: "Erro ao carregar planos. Tente novamente.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadPlans();
  }, [category]);

  const formatPrice = (price: number) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(price);
  };

  if (loading) {
    return (
      <div className="space-y-4">
        <h3 className="text-xl font-semibold">{title}</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {[1, 2, 3].map((i) => (
            <Card key={i} className="animate-pulse">
              <CardHeader>
                <div className="h-6 bg-gray-200 rounded w-3/4"></div>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  <div className="h-20 bg-gray-200 rounded"></div>
                  <div className="h-4 bg-gray-200 rounded w-1/2"></div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  if (plans.length === 0) {
    return (
      <div className="space-y-4">
        <h3 className="text-xl font-semibold">{title}</h3>
        <Card>
          <CardContent className="py-8 text-center">
            <p className="text-muted-foreground">
              Nenhum plano de {category === 'tatuador' ? 'tatuagem' : 'odontologia'} dispon√≠vel no momento.
            </p>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <h3 className="text-xl font-semibold">{title}</h3>
        <Badge variant="outline">
          {plans.length} plano{plans.length > 1 ? 's' : ''} dispon√≠vel{plans.length > 1 ? 'is' : ''}
        </Badge>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {plans.map((plan) => (
          <Card key={plan.id} className="hover:shadow-lg transition-shadow cursor-pointer">
            <CardHeader className="pb-3">
              <CardTitle className="text-lg">{plan.name}</CardTitle>
              {plan.image_url && (
                <div className="aspect-video bg-gray-100 rounded-lg overflow-hidden">
                  <img 
                    src={plan.image_url} 
                    alt={plan.name}
                    className="w-full h-full object-cover"
                    loading="lazy"
                  />
                </div>
              )}
            </CardHeader>

            <CardContent className="space-y-4">
              {plan.description && (
                <p className="text-sm text-muted-foreground line-clamp-3">
                  {plan.description}
                </p>
              )}

              <div className="space-y-2">
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-2">
                    <DollarSign className="h-4 w-4 text-green-600" />
                    <span className="font-bold text-green-600">
                      {formatPrice(plan.price)}
                    </span>
                  </div>
                  <Badge variant="secondary">
                    Ativo
                  </Badge>
                </div>

                <div className="flex items-center gap-2 text-sm text-muted-foreground">
                  <Users className="h-4 w-4" />
                  <span>M√°ximo {plan.max_participants} participantes</span>
                </div>

                <div className="flex items-center gap-2 text-sm text-muted-foreground">
                  <Calendar className="h-4 w-4" />
                  <span>
                    Criado em {new Date(plan.created_at).toLocaleDateString('pt-BR')}
                  </span>
                </div>
              </div>

              {onSelectPlan && (
                <Button 
                  className="w-full bg-green-600 hover:bg-green-700 text-white"
                  onClick={() => onSelectPlan(plan)}
                >
                  Pagar com PIX - {formatPrice(plan.price)}
                </Button>
              )}
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
};

import { useState, useEffect } from "react";
import { useAuth } from "@/contexts/AuthContext";
import { supabase } from "@/integrations/supabase/client";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Copy, Share, Users, UserPlus, TrendingUp, ExternalLink } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface UserReferralData {
  referralCode: string;
  referralLink: string;
  totalReferrals: number;
  groupsFormed: number;
  pendingInvites: number;
}

export const ReferralSystem = () => {
  const { user } = useAuth();
  const { toast } = useToast();
  const [referralData, setReferralData] = useState<UserReferralData>({
    referralCode: "",
    referralLink: "",
    totalReferrals: 0,
    groupsFormed: 0,
    pendingInvites: 0
  });
  const [loading, setLoading] = useState(true);
  const [userGroups, setUserGroups] = useState<any[]>([]);

  useEffect(() => {
    if (user) {
      loadReferralData();
      loadUserGroups();
    }
  }, [user]);

  const loadUserGroups = async () => {
    try {
      const { data: groups } = await supabase
        .from('group_participants')
        .select(`
          plan_groups!inner(
            id,
            referral_code,
            service_id
          )
        `)
        .eq('user_id', user?.id)
        .eq('status', 'active');

      // Para cada grupo, buscar os detalhes do plano
      const groupsWithPlans = await Promise.all(
        (groups || []).map(async (group) => {
          const { data: planData } = await supabase
            .from('custom_plans')
            .select('name')
            .eq('id', group.plan_groups.service_id)
            .single();

          return {
            plan_groups: {
              ...group.plan_groups,
              custom_plans: planData
            }
          };
        })
      );

      setUserGroups(groupsWithPlans);
    } catch (error) {
      console.error('Erro ao carregar grupos do usu√°rio:', error);
    }
  };

  const loadReferralData = async () => {
    try {
      setLoading(true);

      // Buscar dados do perfil do usu√°rio para c√≥digo de indica√ß√£o pessoal
      const { data: profileData, error: profileError } = await supabase
        .from('profiles')
        .select('referral_code')
        .eq('user_id', user?.id)
        .single();

      if (profileError) throw profileError;

      // Contar referrals diretos atrav√©s dos grupos (pessoas que usaram c√≥digos dos grupos do usu√°rio)
      const { data: groupReferrals } = await supabase
        .from('group_participants')
        .select(`
          referrer_code,
          plan_groups!inner(
            referral_code
          )
        `)
        .neq('user_id', user?.id)
        .not('referrer_code', 'is', null);

      // Buscar c√≥digos dos grupos do usu√°rio
      const { data: userGroupCodes } = await supabase
        .from('group_participants')
        .select(`
          plan_groups!inner(
            referral_code
          )
        `)
        .eq('user_id', user?.id);

      const myGroupCodes = userGroupCodes?.map(ug => ug.plan_groups.referral_code) || [];
      const totalReferrals = groupReferrals?.filter(gr => myGroupCodes.includes(gr.referrer_code)).length || 0;

      // Contar grupos formados (participa√ß√µes ativas do usu√°rio)
      const { count: activeParticipations } = await supabase
        .from('group_participants')
        .select('id', { count: 'exact' })
        .eq('user_id', user?.id)
        .eq('status', 'active');

      const referralCode = profileData?.referral_code || user?.id?.slice(-8).toUpperCase() || '';

      setReferralData({
        referralCode,
        referralLink: `${window.location.origin}/register?ref=${referralCode}`,
        totalReferrals,
        groupsFormed: activeParticipations || 0,
        pendingInvites: 0 // Ser√° calculado baseado no progresso dos grupos
      });

    } catch (error) {
