            <div className="flex-1">
              <h2 className="text-2xl font-bold">{profile.name}</h2>
              <p className="text-muted-foreground">{profile.email}</p>
              <div className="flex items-center gap-4 mt-2 text-sm text-muted-foreground">
                <div className="flex items-center gap-1">
                  <Calendar className="h-4 w-4" />
                  <span>Membro desde {profile.joinDate}</span>
                </div>
                <div className="flex items-center gap-1">
                  <MapPin className="h-4 w-4" />
                  <span>{profile.city}</span>
                </div>
              </div>
            </div>

            <Dialog open={isEditingProfile} onOpenChange={setIsEditingProfile}>
              <DialogTrigger asChild>
                <Button 
                  variant="outline"
                  onClick={() => setEditedProfile(profile)}
                >
                  <Edit className="h-4 w-4 mr-2" />
                  Editar Perfil
                </Button>
              </DialogTrigger>
              <DialogContent className="max-w-md max-h-[90vh] overflow-y-auto">
                <DialogHeader>
                  <DialogTitle>Editar Perfil</DialogTitle>
                  <DialogDescription>
                    Atualize suas informa√ß√µes pessoais.
                  </DialogDescription>
                </DialogHeader>
                <div className="space-y-4 py-4">
                  <div>
                    <Label htmlFor="name">Nome Completo</Label>
                    <Input
                      id="name"
                      value={editedProfile.name}
                      onChange={(e) => setEditedProfile(prev => ({ ...prev, name: e.target.value }))}
                    />
                  </div>
                  <div>
                    <Label htmlFor="phone">Telefone</Label>
                    <Input
                      id="phone"
                      value={editedProfile.phone}
                      onChange={(e) => setEditedProfile(prev => ({ ...prev, phone: e.target.value }))}
                      placeholder="(11) 99999-9999"
                    />
                  </div>
                  <div>
                    <Label htmlFor="cpf">CPF</Label>
                    <Input
                      id="cpf"
                      value={editedProfile.cpf}
                      onChange={(e) => {
                        const formattedCpf = formatCpf(e.target.value);
                        setEditedProfile(prev => ({ ...prev, cpf: formattedCpf }));
                      }}
                      placeholder="000.000.000-00"
                      maxLength={14}
                    />
                    {editedProfile.cpf && !validateCpf(editedProfile.cpf) && editedProfile.cpf.length === 14 && (
                      <p className="text-sm text-destructive mt-1">CPF inv√°lido</p>
                    )}
                  </div>
                  <div>
                    <Label htmlFor="address">Endere√ßo</Label>
                    <Input
                      id="address"
                      value={editedProfile.address}
                      onChange={(e) => setEditedProfile(prev => ({ ...prev, address: e.target.value }))}
                      placeholder="Rua, n√∫mero, bairro"
                    />
                  </div>
                  <div>
                    <Label htmlFor="city">Cidade/Estado</Label>
                    <Input
                      id="city"
                      value={editedProfile.city}
                      onChange={(e) => setEditedProfile(prev => ({ ...prev, city: e.target.value }))}
                      placeholder="S√£o Paulo, SP"
                    />
                  </div>
                  <div>
                    <Label htmlFor="preferences">Interesses (separados por v√≠rgula)</Label>
                    <Textarea
                      id="preferences"
                      value={editedProfile.preferences.join(', ')}
                      onChange={(e) => setEditedProfile(prev => ({ 
                        ...prev, 
                        preferences: e.target.value.split(',').map(p => p.trim()).filter(p => p)
                      }))}
                      placeholder="Procedimentos est√©ticos, Promo√ß√µes especiais..."
                      rows={3}
