import { QueryClient } from '@tanstack/react-query';

export const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      // Stale time: How long data stays fresh
      staleTime: 5 * 60 * 1000, // 5 minutes default

      // GC time: How long data stays in cache after components unmount
      gcTime: 10 * 60 * 1000, // 10 minutes default

      // Retry configuration
      retry: (failureCount, error: any) => {
        // Don't retry 4xx errors except 408 (timeout)
        if (error?.status >= 400 && error?.status < 500 && error?.status !== 408) {
          return false;
        }
        // Retry up to 3 times for other errors
        return failureCount < 3;
      },

      // Retry delay with exponential backoff
      retryDelay: (attemptIndex) => Math.min(1000 * 2 ** attemptIndex, 30000),

      // Background refetch configuration
      refetchOnWindowFocus: false, // Disable refetch on window focus
      refetchOnReconnect: true,    // Refetch when reconnecting
      refetchOnMount: true,        // Refetch when component mounts

      // Network mode
      networkMode: 'online',
    },
    mutations: {
      // Retry mutations only once
      retry: 1,

      // Network mode for mutations
      networkMode: 'online',
    },
  },
});

// Performance monitoring for React Query
if (process.env.NODE_ENV === 'development') {
  queryClient.getQueryCache().subscribe((event) => {
    if (event?.type === 'observerResultsUpdated') {
      const queryKey = event.query.queryKey;
      const dataUpdateTime = event.query.state.dataUpdateCount;

      // Log slow queries
      if (dataUpdateTime > 1000) {
        console.warn(`üêå Slow query detected:`, {
          queryKey,
          dataUpdateTime: `${dataUpdateTime}ms`,
          state: event.query.state.status
        });
      }
    }
  });
}

// Clear all queries on auth state change
export const clearAllQueries = () => {
  queryClient.clear();
};

// Invalidate specific query patterns
export const invalidateQueriesByPattern = (pattern: string[]) => {
  queryClient.invalidateQueries({ queryKey: pattern });
};

// Prefetch commonly used queries
export const prefetchCommonQueries = async () => {
  // Prefetch plans for faster loading
  queryClient.prefetchQuery({
    queryKey: ['plans'],
    queryFn: async () => {
      const { supabase } = await import('@/integrations/supabase/client');
      const { data } = await supabase.functions.invoke('unified-plans-loader');
      return data?.plans || [];
    },
    staleTime: 5 * 60 * 1000,
  });
};

import React, { createContext, useContext, useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { supabase } from "@/integrations/supabase/client";
import type { User as SupabaseUser, Session } from "@supabase/supabase-js";
import { getDashboardRoute, shouldRedirectUser, ROUTES } from "@/lib/routes";

// Types
export type UserRole = 'admin' | 'professional' | 'influencer' | 'user';

interface User {
  id: string;
  email: string;
  full_name?: string;
  name?: string; // Compatibility with existing code
  phone?: string;
  role: UserRole;
  avatar_url?: string;
  category?: string; // For professionals
  approved?: boolean; // For professionals/influencers
}

interface AuthContextType {
  user: User | null;
  supabaseUser: SupabaseUser | null;
  session: Session | null;
  loading: boolean;
  login: (email: string, password: string, role?: UserRole) => Promise<{ error: Error | null }>;
  register: (email: string, password: string, userData: any, role?: UserRole) => Promise<{ error: Error | null }>;
  adminLogin: (email: string, password: string) => Promise<{ error: Error | null }>;
  logout: () => Promise<void>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const AuthProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);
  const [supabaseUser, setSupabaseUser] = useState<SupabaseUser | null>(null);
  const [session, setSession] = useState<Session | null>(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    // Set up auth state listener FIRST
    const { data: { subscription } } = supabase.auth.onAuthStateChange(
      async (event, session) => {
        console.log('üîÑ Auth state changed:', { event, userId: session?.user?.id });

        try {
          setSession(session);
          setSupabaseUser(session?.user ?? null);

          // Handle OAuth sign-in events
          if (event === 'SIGNED_IN' && session?.user) {
            console.log('üöÄ User signed in successfully');

            // Check if this is an OAuth login (Google, etc.)
            const isOAuthLogin = session.user.app_metadata?.providers?.includes('google');

            if (isOAuthLogin) {
              console.log('üîç OAuth login detected, processing profile...');

              try {
                // Check if profile exists
                const { data: existingProfile, error: profileError } = await supabase
                  .from('profiles')
                  .select('*')
                  .eq('user_id', session.user.id)
                  .single();

                if (profileError && profileError.code !== 'PGRST116') {
                  console.error('‚ùå Error checking profile:', profileError);
                }

                if (!existingProfile) {
                  console.log('‚ú® Creating new profile for OAuth user...');

                  // Create profile automatically for OAuth users as regular users
                  const { error: insertError } = await supabase
                    .from('profiles')
                    .insert({
                      user_id: session.user.id,
                      email: session.user.email,
                      full_name: session.user.user_metadata?.full_name || 
                                session.user.user_metadata?.name || 
                                session.user.email?.split('@')[0] || '',
                      role: 'user', // Default role for OAuth signups
                    });

                  if (insertError) {
                    console.error('‚ùå Error creating profile:', insertError);
                  } else {
                    console.log('‚úÖ Profile created successfully for OAuth user');
                  }
                } else {
                  console.log('‚úÖ Existing profile found for OAuth user');
                }
              } catch (profileErr) {
                console.error('‚ùå Profile processing error:', profileErr);
              }
            }

            // Load profile immediately after OAuth processing
            loadUserProfile(session.user.id);
          } else if (!session?.user) {
            console.log('üëã User signed out');
            setUser(null);
          }
        } catch (error) {
          console.error('‚ùå Auth state change error:', error);
        }

        setLoading(false);
      }
    );

    // THEN check for existing session
    supabase.auth.getSession().then(({ data: { session } }) => {
      setSession(session);
      setSupabaseUser(session?.user ?? null);

      if (session?.user) {
        loadUserProfile(session.user.id);
      }
      setLoading(false);
    });

    return () => subscription.unsubscribe();
  }, []);

  const loadUserProfile = async (userId: string) => {
    try {
      console.log('üîç Loading user profile for:', userId);

      // Buscar perfil na nova tabela centralizada
      const { data: profileData, error } = await supabase
        .from('profiles')
        .select('*')
        .eq('user_id', userId)
        .single();

      if (error) {
        console.error('‚ùå Error loading profile:', error);

        // If profile doesn't exist and user is authenticated, create a basic one
        if (error.code === 'PGRST116' && supabaseUser) {
          console.log('üîß Profile not found, creating basic profile...');

          const { error: createError } = await supabase
            .from('profiles')
            .insert({
              user_id: userId,
              email: supabaseUser.email || '',
              full_name: supabaseUser.user_metadata?.full_name || 
                        supabaseUser.user_metadata?.name || 
                        supabaseUser.email?.split('@')[0] || '',
              role: 'user',
            });

          if (createError) {
            console.error('‚ùå Error creating basic profile:', createError);
            return;
          }

          // Retry loading after creation
          return loadUserProfile(userId);
        }
        return;
      }

      if (profileData) {
        console.log('‚úÖ Profile loaded successfully:', profileData);

        // Para profissionais, buscar dados adicionais
        let additionalData = {};
        if (profileData.role === 'professional') {
          // Get additional professional data from profiles table
          additionalData = { 
            category: 'professional', 
            approved: profileData.approved 
          };
        }

        setUser({
          id: userId,
          email: profileData.email || supabaseUser?.email || '',
          full_name: profileData.full_name,
          name: profileData.full_name, // Compatibility
          phone: profileData.phone,
          role: profileData.role as UserRole,
          avatar_url: profileData.avatar_url, // Now available
          ...additionalData
        });

        // Handle automatic redirection with plan verification for users
        const currentPath = location.pathname;
        const shouldRedirect = currentPath === '/' || currentPath === '/auth' || currentPath.includes('login');

        if (shouldRedirect) {
          // Para usu√°rios comuns, verificar plano ativo antes de redirecionar
          if (profileData.role === 'user') {
            console.log('üë§ Usu√°rio comum detectado, verificando planos ativos...');

            // Usar setTimeout para evitar problemas de concorr√™ncia
            setTimeout(async () => {
              const { checkUserActivePlan, redirectBasedOnPlanStatus } = await import('@/lib/planUtils');

              const planStatus = await checkUserActivePlan(userId);
              console.log('üìã Status do plano:', planStatus);

              redirectBasedOnPlanStatus(planStatus.hasActivePlan, profileData.role, navigate);
            }, 100);

          } else {
            // Outros tipos de usu√°rio seguem fluxo normal
            const targetRoute = profileData.role === 'admin' ? '/admin/dashboard' :
                               profileData.role === 'professional' ? '/profissional/dashboard' :
                               profileData.role === 'influencer' ? '/influenciador/dashboard' :
                               '/usuario/dashboard';

            console.log('üéØ Redirecting user to:', targetRoute);
            navigate(targetRoute, { replace: true });
          }
        }
      }
    } catch (error) {
      console.error('‚ùå Unexpected error loading profile:', error);
    }
  };

  // Handle user redirection based on role and current location
  const handleUserRedirection = (userRole: UserRole) => {
    const currentPath = location.pathname;

    // Skip redirection if user is already on the correct dashboard or public pages
    if (!shouldRedirectUser(currentPath, userRole)) {
      return;
    }

    const dashboardRoute = getDashboardRoute(userRole);
    console.log(`üîÑ Redirecting ${userRole} user to:`, dashboardRoute);

    navigate(dashboardRoute, { replace: true });
  };

  const login = async (email: string, password: string, role?: UserRole) => {
    try {
      setLoading(true);

      const { data, error } = await supabase.auth.signInWithPassword({
        email,
        password,
      });

      if (error) {
        // Tratamento especial para erro de email n√£o confirmado
        if (error.message.includes('Email not confirmed')) {
          throw new Error('Email n√£o confirmado. Verifique sua caixa de entrada ou entre em contato com o suporte para ativar sua conta.');
        }
        throw error;
      }

      // Success - redirection will be handled by loadUserProfile
      console.log('‚úÖ Login successful, user profile will be loaded and redirected');

      return { error: null };
    } catch (error: any) {
      console.error('Erro no login:', error);
      return { error };
    } finally {
      setLoading(false);
    }
  };

  // Google OAuth removido - sistema simplificado

  const register = async (email: string, password: string, userData: any, role: UserRole = 'user') => {
    try {
      setLoading(true);

      // Cadastro com configura√ß√£o personalizada
      const { data, error } = await supabase.auth.signUp({
        email,
        password,
        options: {
          emailRedirectTo: `${window.location.origin}/`,
          data: {
            full_name: userData.fullName || userData.nome || 'Usu√°rio',
            phone: userData.phone || userData.telefone || '',
            role: role
          }
        }
      });

      if (error) throw error;

      // Se o usu√°rio foi criado, confirmar email automaticamente
      if (data.user) {
        console.log('‚úÖ User registered, confirming email automatically...');

        try {
          // Chamar fun√ß√£o para confirmar email automaticamente
          const { data: confirmData, error: confirmError } = await supabase.functions.invoke('auto-confirm-email', {
            body: { email }
          });

          if (confirmError) {
            console.warn('‚ö†Ô∏è Email confirmation failed:', confirmError);
          } else {
            console.log('‚úÖ Email confirmed automatically');
          }
        } catch (confirmError) {
          console.warn('‚ö†Ô∏è Email confirmation failed:', confirmError);
        }

        // Se n√£o h√° sess√£o ainda, tentar fazer login
        if (!data.session) {
          console.log('üîÑ No session after signup, attempting automatic login...');

          // Aguardar um momento para o email ser confirmado
          await new Promise(resolve => setTimeout(resolve, 1000));

          // Tentar fazer login automaticamente
          const loginResult = await login(email, password);
          if (loginResult.error) {
            console.warn('‚ö†Ô∏è Auto-login failed, user will need to login manually');
            navigate('/confirmacao-email');
          }
        } else {
          console.log('‚úÖ User registered and logged in successfully');
        }
      }

      return { error: null };
    } catch (error: any) {
      console.error('Erro durante cadastro:', error);
      return { error };
    } finally {
      setLoading(false);
    }
  };

  const adminLogin = async (email: string, password: string) => {
    try {
      setLoading(true);
      console.log('üîê Starting admin login process for:', email);

      // First, validate admin credentials using our custom function
      const { data: adminData, error: validationError } = await supabase
        .rpc('admin_login_validation', {
          login_email: email,
          login_password: password
        });

      console.log('üîç Admin validation result:', { adminData, validationError });

      if (validationError) {
        console.error('‚ùå Admin validation error:', validationError);
        throw new Error('Erro na valida√ß√£o: ' + validationError.message);
      }

      if (!adminData || adminData.length === 0) {
        console.log('‚ùå No admin data returned - invalid credentials');
        throw new Error('Email ou senha incorretos para administrador');
      }

      const adminProfile = adminData[0];
      console.log('‚úÖ Admin validated successfully:', adminProfile);

      // Now perform actual Supabase authentication to create a valid session
      console.log('üîë Performing Supabase authentication...');
      const { data: authData, error: authError } = await supabase.auth.signInWithPassword({
        email,
        password,
      });

      if (authError) {
        console.error('‚ùå Supabase auth error:', authError);
        throw new Error('Erro na autentica√ß√£o: ' + authError.message);
      }

      console.log('üéØ Authentication successful, setting session and user data');

      // Set session and user state
      setSession(authData.session);
      setSupabaseUser(authData.user);

      // Set user state with admin profile data
      const adminUser: User = {
        id: adminProfile.profile_id,
        email: adminProfile.profile_email,
        full_name: adminProfile.profile_name,
        name: adminProfile.profile_name,
        phone: '',
        role: adminProfile.profile_role as UserRole,
        avatar_url: null,
      };

      setUser(adminUser);


      // Use setTimeout to ensure state is updated before navigation
      setTimeout(() => {
        navigate('/admin/dashboard', { replace: true });
      }, 200);

      return { error: null };
    } catch (error: any) {
      console.error('‚ùå Erro no login admin:', error);
      return { error };
    } finally {
      setLoading(false);
    }
  };

  const logout = async () => {
    try {
      // Clean up auth state
      setUser(null);
      setSupabaseUser(null);
      setSession(null);

      // Sign out from Supabase
      await supabase.auth.signOut({ scope: 'global' });

      // Force page reload for clean state
      window.location.href = ROUTES.HOME;
    } catch (error) {
      console.error('Erro no logout:', error);
    }
  };

  return (
    <AuthContext.Provider 
      value={{ 
        user, 
        supabaseUser, 
        session, 
        login,
        register,
        adminLogin, 
        logout, 
        loading 
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth deve ser usado dentro de um AuthProvider');
  }
  return context;
};

