import React, { createContext, useContext, useEffect } from 'react';
import { applyGlobalTheme } from '@/lib/theme';

interface ThemeContextType {
  theme: 'dark';
  isDarkMode: true;
}

const ThemeContext = createContext<ThemeContextType>({
  theme: 'dark',
  isDarkMode: true,
});

export function ThemeProvider({ children }: { children: React.ReactNode }) {
  useEffect(() => {
    // Apply theme once on mount - NO aggressive polling
    applyGlobalTheme();
  }, []);

  const contextValue: ThemeContextType = {
    theme: 'dark',
    isDarkMode: true,
  };

  return (
    <ThemeContext.Provider value={contextValue}>
      {children}
    </ThemeContext.Provider>
  );
}

export function useTheme() {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
}

import { useState, useEffect } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { useAuth } from '@/contexts/AuthContext';
import { useToast } from '@/hooks/use-toast';

export interface CreditTransaction {
  id: string;
  userId: string;
  amount: number;
  type: 'credit' | 'debit';
  source: 'initial_payment' | 'referral_bonus' | 'marketplace_purchase' | 'withdrawal' | 'admin_adjustment';
  description: string;
  relatedOrderId?: string;
  createdAt: string;
}

export interface CreditBalance {
  userId: string;
  totalCredits: number;
  availableCredits: number;
  pendingWithdrawal: number;
  lastUpdated: string;
}

export const useCredits = () => {
  const [balance, setBalance] = useState<CreditBalance | null>(null);
  const [transactions, setTransactions] = useState<CreditTransaction[]>([]);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();
  const { toast } = useToast();

  // Carregar saldo e transa√ß√µes do usu√°rio
  const loadUserCredits = async () => {
    if (!user) return;

    try {
      setLoading(true);

      // Carregar saldo atual
      const { data: balanceData, error: balanceError } = await supabase
        .from('user_credits')
        .select('*')
        .eq('user_id', user.id)
        .single();

      if (balanceError && balanceError.code !== 'PGRST116') {
        console.error('Erro ao carregar saldo:', balanceError);
      } else if (balanceData) {
        setBalance({
          userId: balanceData.user_id,
          totalCredits: balanceData.total_credits,
          availableCredits: balanceData.available_credits,
          pendingWithdrawal: balanceData.pending_credits,
          lastUpdated: balanceData.updated_at
        });
      } else {
        // Criar registro inicial de cr√©ditos
        const { data: newBalance, error: createError } = await supabase
          .from('user_credits')
          .insert({
            user_id: user.id,
            total_credits: 0,
            available_credits: 0,
            pending_credits: 0
          })
          .select()
          .single();

        if (createError) {
          console.error('Erro ao criar saldo inicial:', createError);
        } else if (newBalance) {
          setBalance({
            userId: newBalance.user_id,
            totalCredits: newBalance.total_credits,
            availableCredits: newBalance.available_credits,
            pendingWithdrawal: newBalance.pending_credits,
            lastUpdated: newBalance.updated_at
          });
        }
      }

      // Carregar hist√≥rico de transa√ß√µes
      const { data: transactionsData, error: transactionsError } = await supabase
        .from('credit_transactions')
        .select('*')
        .eq('user_id', user.id)
        .order('created_at', { ascending: false })
        .limit(50);

      if (transactionsError) {
        console.error('Erro ao carregar transa√ß√µes:', transactionsError);
      } else if (transactionsData) {
        const formattedTransactions: CreditTransaction[] = transactionsData.map(t => ({
          id: t.id,
          userId: t.user_id,
          amount: t.amount,
          type: (t.type as 'credit' | 'debit'),
          source: (t.source_type as 'initial_payment' | 'referral_bonus' | 'marketplace_purchase' | 'withdrawal' | 'admin_adjustment'),
          description: t.description,
          relatedOrderId: t.reference_id || '',
          createdAt: t.created_at
        }));
        setTransactions(formattedTransactions);
      }

    } catch (error) {
      console.error('Erro ao carregar dados de cr√©ditos:', error);
    } finally {
      setLoading(false);
    }
  };

  // Adicionar cr√©ditos (para pagamentos, b√¥nus, etc.)
  const addCredits = async (amount: number, source: CreditTransaction['source'], description: string, relatedOrderId?: string) => {
    if (!user) return false;

    try {
      const { data, error } = await supabase.functions.invoke('add-user-credits', {
        body: {
          userId: user.id,
          amount,
          source,
          description,
          relatedOrderId
        }
      });

      if (error) {
        console.error('Erro ao adicionar cr√©ditos:', error);
        toast({
          title: "Erro ao adicionar cr√©ditos",
          description: "Tente novamente em alguns instantes.",
          variant: "destructive"
        });
        return false;
      }

      // Recarregar dados
      await loadUserCredits();

      toast({
        title: "Cr√©ditos adicionados!",
        description: `R$ ${amount.toFixed(2)} foi adicionado ao seu saldo.`,
      });

      return true;
    } catch (error) {
      console.error('Erro ao adicionar cr√©ditos:', error);
      return false;
    }
  };

  // Usar cr√©ditos (para marketplace, etc.)
  const useCredits = async (amount: number, source: CreditTransaction['source'], description: string, relatedOrderId?: string) => {
    if (!user || !balance) return false;

    if (balance.availableCredits < amount) {
      toast({
        title: "Saldo insuficiente",
        description: "Voc√™ n√£o tem cr√©ditos suficientes para esta transa√ß√£o.",
        variant: "destructive"
      });
      return false;
    }

    try {
      const { data, error } = await supabase.functions.invoke('use-user-credits', {
        body: {
          userId: user.id,
          amount,
          source,
          description,
          relatedOrderId
        }
      });

      if (error) {
        console.error('Erro ao usar cr√©ditos:', error);
        toast({
          title: "Erro ao processar transa√ß√£o",
          description: "Tente novamente em alguns instantes.",
          variant: "destructive"
        });
        return false;
      }

      // Recarregar dados
      await loadUserCredits();

      toast({
        title: "Cr√©ditos utilizados!",
        description: `R$ ${amount.toFixed(2)} foi debitado do seu saldo.`,
      });

      return true;
    } catch (error) {
      console.error('Erro ao usar cr√©ditos:', error);
      return false;
    }
  };

  // Solicitar saque
  const requestWithdrawal = async (amount: number) => {
    if (!user || !balance) return false;

    if (balance.availableCredits < amount) {
      toast({
        title: "Saldo insuficiente",
        description: "Voc√™ n√£o tem cr√©ditos suficientes para este saque.",
        variant: "destructive"
      });
      return false;
    }

    if (amount < 50) {
      toast({
        title: "Valor m√≠nimo",
        description: "O valor m√≠nimo para saque √© R$ 50,00.",
        variant: "destructive"
      });
      return false;
    }

    try {
      const { data, error } = await supabase.functions.invoke('request-withdrawal', {
        body: {
          userId: user.id,
          amount
        }
      });

      if (error) {
        console.error('Erro ao solicitar saque:', error);
        toast({
          title: "Erro ao solicitar saque",
          description: "Tente novamente em alguns instantes.",
          variant: "destructive"
        });
        return false;
      }

      // Recarregar dados
      await loadUserCredits();

      toast({
        title: "Saque solicitado!",
        description: `Sua solicita√ß√£o de saque de R$ ${amount.toFixed(2)} est√° em an√°lise.`,
      });

      return true;
    } catch (error) {
      console.error('Erro ao solicitar saque:', error);
      return false;
    }
  };

  // Converter valor de entrada para cr√©ditos (quando usu√°rio paga mas grupo n√£o completa)
  const convertPaymentToCredits = async (paymentAmount: number, orderId: string) => {
    return await addCredits(
      paymentAmount,
      'initial_payment',
      `Convers√£o de pagamento em cr√©ditos - Pedido #${orderId}`,
      orderId
    );
  };

  // Listener para atualiza√ß√µes em tempo real
  useEffect(() => {
    if (!user) return;

    loadUserCredits();

    // Configurar listener para mudan√ßas em tempo real
    const channel = supabase
      .channel('user-credits')
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'user_credits',
          filter: `user_id=eq.${user.id}`
        },
        () => {
          loadUserCredits();
        }
      )
      .on(
        'postgres_changes',
        {
          event: 'INSERT',
          schema: 'public',
          table: 'credit_transactions',
          filter: `user_id=eq.${user.id}`
        },
        () => {
          loadUserCredits();
        }
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [user]);

  return {
    balance,
    transactions,
    loading,
    addCredits,
    useCredits,
    requestWithdrawal,
    convertPaymentToCredits,
    refreshData: loadUserCredits
  };
};

import { useState, useEffect } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { useAuth } from '@/contexts/AuthContext';

export interface AutomatedTrigger {
  userId: string;
  triggerType: '15_days' | '30_days' | '60_days' | '90_days' | '180_days';
  executed: boolean;
  executedAt?: string;
  scheduledFor: string;
  groupId: string;
}

export const useNotificationTriggers = () => {
  const [triggers, setTriggers] = useState<AutomatedTrigger[]>([]);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();

  // Verificar e executar gatilhos pendentes
  const checkAndExecuteTriggers = async () => {
    if (!user) return;

    try {
      const { data, error } = await supabase.functions.invoke('notification-triggers', {
        body: { userId: user.id }
      });

      if (error) {
        console.error('Erro ao verificar gatilhos:', error);
      } else {
        console.log('Gatilhos verificados:', data);
      }
    } catch (error) {
      console.error('Erro ao executar gatilhos:', error);
    }
  };

  // Agendar pr√≥ximos gatilhos para um usu√°rio
  const scheduleTriggersForUser = async (userId: string, groupId: string, initialDate: Date) => {
    const triggerDays = [15, 30, 60, 90, 180];
    const triggers = triggerDays.map(days => {
      const scheduledDate = new Date(initialDate);
      scheduledDate.setDate(scheduledDate.getDate() + days);

      return {
        user_id: userId,
        group_id: groupId,
        trigger_type: `${days}_days` as AutomatedTrigger['triggerType'],
        scheduled_for: scheduledDate.toISOString(),
        executed: false
      };
    });

    try {
      const triggersToInsert = triggers.map(trigger => ({
        user_id: trigger.user_id,
        event_type: trigger.trigger_type,
        title: `Automated Trigger: ${trigger.trigger_type}`,
        message: `Trigger scheduled for ${trigger.scheduled_for}`,
        data: {
          group_id: trigger.group_id,
          trigger_type: trigger.trigger_type,
          scheduled_for: trigger.scheduled_for
        },
        sent: trigger.executed
      }));

      const { error } = await supabase
        .from('notification_triggers')
        .insert(triggersToInsert);

      if (error) {
        console.error('Erro ao agendar gatilhos:', error);
      } else {
        console.log('Gatilhos agendados para usu√°rio:', userId);
      }
    } catch (error) {
      console.error('Erro ao agendar gatilhos:', error);
    }
  };

  // Buscar gatilhos do usu√°rio atual
  const loadUserTriggers = async () => {
    if (!user) return;

    try {
      const { data, error } = await supabase
        .from('notification_triggers')
        .select('*')
        .eq('user_id', user.id)
        .order('scheduled_for', { ascending: true });

      if (error) {
        console.error('Erro ao carregar gatilhos:', error);
      } else {
        const formattedTriggers: AutomatedTrigger[] = data?.map(t => ({
          userId: t.user_id,
          triggerType: (t.event_type as '15_days' | '30_days' | '60_days' | '90_days' | '180_days'),
          executed: t.sent,
          executedAt: t.sent_at || '',
          scheduledFor: t.created_at,
          groupId: (t.data as any)?.group_id || ''
        })) || [];

        setTriggers(formattedTriggers);
      }
    } catch (error) {
      console.error('Erro ao carregar gatilhos:', error);
    } finally {
      setLoading(false);
    }
  };

  // Executar verifica√ß√£o manual de gatilhos (para admins)
  const runTriggersManually = async () => {
    try {
      const { data, error } = await supabase.functions.invoke('notification-triggers');

      if (error) {
        console.error('Erro ao executar gatilhos manualmente:', error);
        return false;
      }

      console.log('Gatilhos executados manualmente:', data);
      return true;
    } catch (error) {
      console.error('Erro ao executar gatilhos:', error);
      return false;
    }
  };

  // Estat√≠sticas de gatilhos
  const getTriggerStats = () => {
    const total = triggers.length;
    const executed = triggers.filter(t => t.executed).length;
    const pending = total - executed;
    const overdue = triggers.filter(t => 
      !t.executed && new Date(t.scheduledFor) < new Date()
    ).length;

    return { total, executed, pending, overdue };
  };

  useEffect(() => {
    if (user) {
      loadUserTriggers();

      // Verificar gatilhos a cada 5 minutos quando o usu√°rio est√° ativo
      const interval = setInterval(() => {
        checkAndExecuteTriggers();
      }, 5 * 60 * 1000);

      return () => clearInterval(interval);
    }
  }, [user]);

  return {
    triggers,
    loading,
    checkAndExecuteTriggers,
    scheduleTriggersForUser,
    runTriggersManually,
    getTriggerStats,
    refreshTriggers: loadUserTriggers
  };
};

