      toast({
        title: "Erro",
        description: "N√£o foi poss√≠vel marcar a notifica√ß√£o como n√£o lida.",
        variant: "destructive"
      });
    }
  }, [toast]);

  const markAllAsRead = useCallback(async () => {
    try {
      setNotifications(prev => 
        prev.map(n => ({ ...n, read: true }))
      );

      toast({
        title: "Sucesso",
        description: "Todas as notifica√ß√µes foram marcadas como lidas.",
      });
    } catch (error) {
      console.error('Error marking all notifications as read:', error);
      toast({
        title: "Erro",
        description: "N√£o foi poss√≠vel marcar todas as notifica√ß√µes como lidas.",
        variant: "destructive"
      });
    }
  }, [toast]);

  const performAction = useCallback(async (notificationId: string, action: string) => {
    try {
      // In a real implementation, this would handle different actions
      console.log(`Performing action "${action}" on notification ${notificationId}`);

      toast({
        title: "A√ß√£o realizada",
        description: `A√ß√£o "${action}" executada com sucesso.`,
      });

      // Mark notification as read after action
      await markAsRead(notificationId);

    } catch (error) {
      console.error('Error performing action:', error);
      toast({
        title: "Erro",
        description: "N√£o foi poss√≠vel executar a a√ß√£o.",
        variant: "destructive"
      });
    }
  }, [markAsRead, toast]);

  const refresh = useCallback(async () => {
    try {
      setLoading(true);
      // In a real implementation, this would fetch fresh data from Supabase
      // For now, we'll just simulate a refresh
      if (user) {
        const mockNotifications = createMockNotifications(user.id);
        setNotifications(mockNotifications);
      }
    } catch (error) {
      console.error('Error refreshing notifications:', error);
      setError('Erro ao carregar notifica√ß√µes');
    } finally {
      setLoading(false);
    }
  }, [user]);

  const nextPage = useCallback(() => {
    if (hasNextPage) {
      setCurrentPage(prev => prev + 1);
    }
  }, [hasNextPage]);

  const prevPage = useCallback(() => {
    if (currentPage > 1) {
      setCurrentPage(prev => prev - 1);
    }
  }, [currentPage]);

  return {
    notifications: paginatedNotifications,
    unreadCount,
    loading,
    error,
    currentPage,
    totalPages,
    hasNextPage,
    selectedType,
    markAsRead,
    markAsUnread,
    markAllAsRead,
    setSelectedType,
    nextPage,
    prevPage,
    refresh,
    performAction
  };
};

import { useState, useEffect, useCallback, useRef } from 'react';
import { supabase } from '@/integrations/supabase/client';

interface CacheEntry<T> {
  data: T;
  timestamp: number;
  ttl: number; // Time to live in milliseconds
}

// Cache global para dados reutiliz√°veis
const globalCache = new Map<string, CacheEntry<any>>();

// Hook otimizado para carregar dados com cache
export const useOptimizedData = <T>(
  key: string,
  fetcher: () => Promise<T>,
  options: {
    ttl?: number; // Cache TTL em ms (padr√£o: 5 minutos)
    immediate?: boolean; // Carregar imediatamente (padr√£o: true)
    refetchOnMount?: boolean; // Recarregar ao montar (padr√£o: false)
  } = {}
) => {
  const {
    ttl = 5 * 60 * 1000, // 5 minutos
    immediate = true,
    refetchOnMount = false
  } = options;

  const [data, setData] = useState<T | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<Error | null>(null);
  const mountedRef = useRef(true);

  // Verificar cache
  const getCachedData = useCallback(() => {
    const cached = globalCache.get(key);
    if (cached && Date.now() - cached.timestamp < cached.ttl) {
      return cached.data;
    }
    return null;
  }, [key]);

  // Salvar no cache
  const setCachedData = useCallback((newData: T) => {
    globalCache.set(key, {
      data: newData,
      timestamp: Date.now(),
      ttl
    });
  }, [key, ttl]);

  // Fun√ß√£o para buscar dados
  const fetchData = useCallback(async (force = false) => {
    // Verificar cache primeiro se n√£o for for√ßado
    if (!force) {
      const cached = getCachedData();
      if (cached) {
        setData(cached);
        return cached;
      }
    }

    setLoading(true);
    setError(null);

    try {
      const result = await fetcher();
      if (mountedRef.current) {
        setData(result);
        setCachedData(result);
      }
      return result;
    } catch (err) {
      const error = err as Error;
      if (mountedRef.current) {
        setError(error);
        console.error(`Error fetching data for key "${key}":`, error);
      }
      throw error;
    } finally {
      if (mountedRef.current) {
        setLoading(false);
      }
    }
  }, [key, fetcher, getCachedData, setCachedData]);

  // Carregar dados iniciais
  useEffect(() => {
    if (immediate || refetchOnMount) {
      const cached = getCachedData();
      if (cached && !refetchOnMount) {
        setData(cached);
      } else {
        fetchData();
      }
    }

    return () => {
      mountedRef.current = false;
    };
  }, [immediate, refetchOnMount, fetchData, getCachedData]);

  // Fun√ß√£o para invalidar cache
  const invalidateCache = useCallback(() => {
    globalCache.delete(key);
  }, [key]);

  // Fun√ß√£o para refetch
  const refetch = useCallback(() => {
    return fetchData(true);
  }, [fetchData]);

  return {
    data,
    loading,
    error,
    refetch,
    invalidateCache,
    fetchData: () => fetchData(false)
  };
};

// Hook espec√≠fico para dados de marketplace - APENAS PRODUTOS REAIS
export const useMarketplaceProducts = () => {
  return useOptimizedData(
    'marketplace-products-real',
    async () => {
      const { data, error } = await supabase
        .from('products')
        .select('*')
        .eq('active', true)
        .order('created_at', { ascending: false });

      if (error) throw error;

      // Se n√£o h√° produtos reais, retornar array vazio
      if (!data || data.length === 0) {
        return [];
      }

      return data.map(product => ({
        id: product.id,
        title: product.name,
        description: product.description || '',
        full_price: Number(product.price),
        down_payment: Number(product.price), // Pre√ßo total para marketplace
        category: product.category || 'Produtos',
        professional_name: 'Profissional Certificado',
        professional_id: product.professional_id,
        professional_avatar: null,
        image_url: product.image_url,
        external_link: product.external_link,
        visibility: 'both' as 'client' | 'professional' | 'both',
        is_active: product.active,
        created_at: product.created_at,
        total_sales: 0,
        rating: 5.0 // Produtos certificados
