import { useState, useCallback, useMemo, useRef } from 'react';

// Optimized state hook for async operations
export function useAsyncState<T>(initialValue: T) {
  const [data, setData] = useState<T>(initialValue);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const mountedRef = useRef(true);

  const setAsyncData = useCallback(async (asyncAction: () => Promise<T>) => {
    if (!mountedRef.current) return;

    setLoading(true);
    setError(null);

    try {
      const result = await asyncAction();
      if (mountedRef.current) {
        setData(result);
      }
    } catch (err) {
      if (mountedRef.current) {
        setError(err instanceof Error ? err.message : 'An error occurred');
      }
    } finally {
      if (mountedRef.current) {
        setLoading(false);
      }
    }
  }, []);

  const reset = useCallback(() => {
    if (mountedRef.current) {
      setData(initialValue);
      setError(null);
      setLoading(false);
    }
  }, [initialValue]);

  // Cleanup on unmount
  const cleanup = useCallback(() => {
    mountedRef.current = false;
  }, []);

  return useMemo(() => ({
    data,
    loading,
    error,
    setData,
    setAsyncData,
    reset,
    cleanup
  }), [data, loading, error, setAsyncData, reset, cleanup]);
}

// Optimized form state hook
export function useFormState<T extends Record<string, any>>(initialState: T) {
  const [state, setState] = useState<T>(initialState);
  const [errors, setErrors] = useState<Partial<Record<keyof T, string>>>({});
  const [touched, setTouched] = useState<Partial<Record<keyof T, boolean>>>({});

  const updateField = useCallback(<K extends keyof T>(field: K, value: T[K]) => {
    setState(prev => ({ ...prev, [field]: value }));
    setTouched(prev => ({ ...prev, [field]: true }));

    // Clear error when user starts typing
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: undefined }));
    }
  }, [errors]);

  const setFieldError = useCallback(<K extends keyof T>(field: K, error: string) => {
    setErrors(prev => ({ ...prev, [field]: error }));
  }, []);

  const reset = useCallback(() => {
    setState(initialState);
    setErrors({});
    setTouched({});
  }, [initialState]);

  const hasErrors = useMemo(() => Object.values(errors).some(Boolean), [errors]);

  return useMemo(() => ({
    state,
    errors,
    touched,
    hasErrors,
    updateField,
    setFieldError,
    setState,
    setErrors,
    reset
  }), [state, errors, touched, hasErrors, updateField, setFieldError, reset]);
}

// Optimized pagination hook
export function usePagination<T>(
  items: T[],
  itemsPerPage: number = 10
) {
  const [currentPage, setCurrentPage] = useState(1);

  const totalPages = useMemo(() => 
    Math.ceil(items.length / itemsPerPage), 
    [items.length, itemsPerPage]
  );

  const paginatedItems = useMemo(() => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    return items.slice(startIndex, startIndex + itemsPerPage);
  }, [items, currentPage, itemsPerPage]);

  const goToPage = useCallback((page: number) => {
    setCurrentPage(Math.max(1, Math.min(page, totalPages)));
  }, [totalPages]);

  const nextPage = useCallback(() => {
    goToPage(currentPage + 1);
  }, [currentPage, goToPage]);

  const prevPage = useCallback(() => {
    goToPage(currentPage - 1);
  }, [currentPage, goToPage]);

  const reset = useCallback(() => {
    setCurrentPage(1);
  }, []);

  return useMemo(() => ({
    currentPage,
    totalPages,
    paginatedItems,
    goToPage,
    nextPage,
    prevPage,
    reset,
    hasNext: currentPage < totalPages,
    hasPrev: currentPage > 1
  }), [currentPage, totalPages, paginatedItems, goToPage, nextPage, prevPage, reset]);
}

// Optimized toggle hook
export function useToggle(initialValue: boolean = false) {
  const [value, setValue] = useState(initialValue);

  const toggle = useCallback(() => setValue(prev => !prev), []);
  const setTrue = useCallback(() => setValue(true), []);
  const setFalse = useCallback(() => setValue(false), []);

  return useMemo(() => ({
    value,
    toggle,
    setTrue,
    setFalse,
    setValue
  }), [value, toggle, setTrue, setFalse]);
}

import { useEffect, useCallback, useMemo } from 'react';

// Debounce hook otimizado
export function useDebounce<T>(value: T, delay: number): T {
  const [debouncedValue, setDebouncedValue] = React.useState<T>(value);

  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);

    return () => {
      clearTimeout(handler);
    };
  }, [value, delay]);

  return debouncedValue;
}

// Throttle hook para eventos de scroll
export function useThrottle<T extends (...args: any[]) => any>(
  callback: T,
  delay: number
): T {
  const lastRun = React.useRef(Date.now());

  return useCallback(
    ((...args) => {
      if (Date.now() - lastRun.current >= delay) {
        callback(...args);
        lastRun.current = Date.now();
      }
    }) as T,
    [callback, delay]
  );
}

// Hook para lazy loading de imagens
export function useLazyImage(src: string, threshold = 0.1) {
  const [imageSrc, setImageSrc] = React.useState<string | null>(null);
  const [imageRef, setImageRef] = React.useState<HTMLImageElement | null>(null);

  useEffect(() => {
    if (!imageRef || !src) return;

    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setImageSrc(src);
          observer.disconnect();
        }
      },
      { threshold }
    );

    observer.observe(imageRef);

    return () => observer.disconnect();
  }, [imageRef, src, threshold]);

  return [imageSrc, setImageRef] as const;
}

// Hook para componentes lazy loading
export function useLazyComponent<T extends React.ComponentType<any>>(
  importFunc: () => Promise<{ default: T }>,
  deps: React.DependencyList = []
) {
  const [Component, setComponent] = React.useState<T | null>(null);
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState<Error | null>(null);

  useEffect(() => {
    setLoading(true);
    setError(null);

    importFunc()
      .then(module => {
        setComponent(() => module.default);
      })
      .catch(err => {
        console.error('Failed to load component:', err);
        setError(err);
      })
      .finally(() => {
        setLoading(false);
      });
  }, deps);

  return { Component, loading, error };
}

// Hook para cleanup autom√°tico
export function useCleanup(cleanup: () => void, deps: React.DependencyList = []) {
  useEffect(() => {
    return cleanup;
  }, deps);
}

// Hook para performance monitoring
export function usePerformanceMonitor(componentName: string, enabled = false) {
  const startTime = useMemo(() => performance.now(), []);

  useEffect(() => {
    if (!enabled) return;

    const endTime = performance.now();
    const renderTime = endTime - startTime;

    // Log apenas se for mais lento que 16ms (60fps)
    if (renderTime > 16) {
      console.warn(`Performance warning: ${componentName} took ${renderTime.toFixed(2)}ms to render`);
    }
  });

  return {
    mark: useCallback((label: string) => {
      if (enabled) {
        performance.mark(`${componentName}-${label}`);
      }
    }, [componentName, enabled]),

    measure: useCallback((startMark: string, endMark: string) => {
      if (enabled) {
        try {
          performance.measure(
            `${componentName}-duration`,
            `${componentName}-${startMark}`,
            `${componentName}-${endMark}`
          );
        } catch (error) {
          console.warn('Performance measurement failed:', error);
        }
      }
    }, [componentName, enabled])
  };
}

// Hook otimizado para API calls
export function useOptimizedFetch<T>(
  url: string | null,
  options: RequestInit = {}
) {
  const [data, setData] = React.useState<T | null>(null);
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState<Error | null>(null);

  const fetchData = useCallback(async () => {
    if (!url) return;

    setLoading(true);
    setError(null);

    try {
      const response = await fetch(url, options);

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const result = await response.json();
      setData(result);
    } catch (err) {
      console.error('Fetch error:', err);
      setError(err instanceof Error ? err : new Error('Unknown error'));
    } finally {
      setLoading(false);
    }
  }, [url, JSON.stringify(options)]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  return { data, loading, error, refetch: fetchData };
}

import React from 'react';

import { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';

interface ReferralTracking {
  referralCode: string | null;
  isReferred: boolean;
  setReferralCode: (code: string) => void;
  clearReferralCode: () => void;
  validateReferralCode: (code: string) => boolean;
}

// Enhanced security validation for referral codes
const validateReferralCodeFormat = (code: string): boolean => {
  // Check format: Must be alphanumeric, 3-16 characters
  const formatRegex = /^[A-Z0-9]{3,16}$/;
  if (!formatRegex.test(code)) {
    return false;
  }

  // Check for common patterns that might indicate malicious input
  const suspiciousPatterns = [
    /script/i,
    /javascript/i,
    /eval/i,
    /onclick/i,
    /onerror/i,
    /<|>|&lt;|&gt;/,
    /['"]/,
    /\\/
  ];

  return !suspiciousPatterns.some(pattern => pattern.test(code));
};

// Secure storage with validation and encryption-like obfuscation
const secureStorage = {
  setItem: (key: string, value: string) => {
    if (!validateReferralCodeFormat(value)) {
      console.warn('Invalid referral code format, not storing');
      return;
    }

    // Simple obfuscation (not real encryption, but adds a layer)
    const obfuscated = btoa(value + '|' + Date.now());
    localStorage.setItem(key, obfuscated);
  },

  getItem: (key: string): string | null => {
    try {
      const stored = localStorage.getItem(key);
      if (!stored) return null;

      const decoded = atob(stored);
      const [code, timestamp] = decoded.split('|');

      // Check if stored referral is not too old (24 hours)
      const maxAge = 24 * 60 * 60 * 1000; // 24 hours
      if (Date.now() - parseInt(timestamp) > maxAge) {
        localStorage.removeItem(key);
        return null;
      }

      return validateReferralCodeFormat(code) ? code : null;
    } catch {
      // Invalid stored data, remove it
      localStorage.removeItem(key);
      return null;
    }
  },

  removeItem: (key: string) => {
    localStorage.removeItem(key);
  }
};

export const useReferralTracking = (): ReferralTracking => {
  const [searchParams] = useSearchParams();
  const [referralCode, setReferralCodeState] = useState<string | null>(null);

  useEffect(() => {
    // Verifica se h√° um c√≥digo de refer√™ncia na URL
    const urlReferralCode = searchParams.get('ref');

    // Verifica se h√° um c√≥digo salvo no localStorage
    const savedReferralCode = secureStorage.getItem('pendingReferralCode');

    if (urlReferralCode && validateReferralCodeFormat(urlReferralCode)) {
      // Prioriza o c√≥digo da URL se for v√°lido
      setReferralCodeState(urlReferralCode);
      secureStorage.setItem('pendingReferralCode', urlReferralCode);

      // Disparar tracking de clique de forma best-effort
      try {
        // Using window.env fallback to avoid TS import.meta typing issues in some setups
        // @ts-ignore
        const baseUrl = (import.meta as any).env?.VITE_SUPABASE_URL || (window as any)?.ENV?.VITE_SUPABASE_URL;
        // @ts-ignore
        const anonKey = (import.meta as any).env?.VITE_SUPABASE_ANON_KEY || (window as any)?.ENV?.VITE_SUPABASE_ANON_KEY;
        fetch(`${baseUrl}/rest/v1/rpc/record_influencer_click`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'apikey': anonKey,
            'Authorization': `Bearer ${anonKey}`
          },
          body: JSON.stringify({
            p_referral_code: urlReferralCode,
            p_user_id: null,
            p_user_agent: navigator.userAgent,
            p_ip_hash: null,
            p_context: { path: window.location.pathname }
          })
        });
      } catch (e) {
        // best-effort, ignore errors
      }
    } else if (savedReferralCode) {
      // Usa o c√≥digo salvo se n√£o houver na URL
      setReferralCodeState(savedReferralCode);
    }
  }, [searchParams]);

  const setReferralCode = (code: string) => {
    if (!validateReferralCodeFormat(code)) {
      console.warn('Invalid referral code format');
      return;
    }

    setReferralCodeState(code);
    secureStorage.setItem('pendingReferralCode', code);
  };

  const clearReferralCode = () => {
    setReferralCodeState(null);
    secureStorage.removeItem('pendingReferralCode');
  };

  return {
    referralCode,
    isReferred: !!referralCode,
    setReferralCode,
    clearReferralCode,
    validateReferralCode: validateReferralCodeFormat
  };
};

