// Mutation hooks
export function useCreatePlan() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async (planData: any) => {
      const { data, error } = await supabase
        .from('custom_plans')
        .insert(planData)
        .select()
        .single();

      if (error) throw error;
      return data;
