 * Um plano √© considerado ativo se:
 * - Usu√°rio tem participa√ß√£o ativa em um grupo
 * - O grupo est√° em forma√ß√£o ou completo (n√£o cancelado)
 */
export const checkUserActivePlan = async (userId: string): Promise<{
  hasActivePlan: boolean;
  activePlans: ActivePlan[];
  error?: string;
}> => {
  try {
    console.log('üîç Verificando planos ativos para usu√°rio:', userId);

    // Usar a fun√ß√£o do banco para verificar se tem plano ativo
    const { data: hasActivePlan, error: checkError } = await supabase
      .rpc('user_has_active_plan', { user_uuid: userId });

    if (checkError) {
      console.error('‚ùå Erro ao verificar planos:', checkError);
      return {
        hasActivePlan: false,
        activePlans: [],
        error: checkError.message
      };
    }

    // Se n√£o tem plano ativo, retornar vazio
    if (!hasActivePlan) {
      console.log('üì≠ Nenhum plano ativo encontrado');
      return {
        hasActivePlan: false,
        activePlans: []
      };
    }

      // Buscar detalhes das participa√ß√µes ativas
      const { data: participations, error: participationError } = await supabase
        .from('group_participants')
        .select(`
          id,
          status,
          group_id,
          amount_paid,
          joined_at,
          plan_groups!inner(
            id,
            status,
            service_id,
            referral_code,
            current_participants,
            max_participants
          )
        `)
        .eq('user_id', userId)
        .eq('status', 'active')
        .in('plan_groups.status', ['forming', 'complete']);

      if (participationError) {
        console.error('‚ùå Erro ao buscar detalhes das participa√ß√µes:', participationError);
        return {
          hasActivePlan: true,
          activePlans: [],
          error: participationError.message
        };
      }

      // Para cada participa√ß√£o, buscar o nome do servi√ßo
      const activePlans: ActivePlan[] = await Promise.all(
        (participations || []).map(async (p) => {
          const { data: planData } = await supabase
            .from('custom_plans')
            .select('name')
            .eq('id', p.plan_groups?.service_id)
            .single();

          return {
            id: p.id,
            status: p.status,
            group_id: p.group_id,
            amount_paid: p.amount_paid,
            joined_at: p.joined_at,
            group_status: p.plan_groups?.status,
            service_name: planData?.name || 'Servi√ßo n√£o definido'
          };
        })
      );

    console.log('‚úÖ Verifica√ß√£o conclu√≠da:', {
      hasActivePlan: true,
      totalPlans: activePlans.length
    });

    return {
      hasActivePlan: true,
      activePlans
    };

  } catch (error: any) {
    console.error('‚ùå Erro inesperado ao verificar planos:', error);
    return {
      hasActivePlan: false,
      activePlans: [],
      error: error.message
    };
  }
};

/**
 * Redireciona o usu√°rio baseado no status do plano
 */
export const redirectBasedOnPlanStatus = (
  hasActivePlan: boolean, 
  userRole: string,
  navigate: (path: string, options?: any) => void
) => {
  if (userRole !== 'user') {
    // Outros tipos de usu√°rio seguem fluxo normal
    const dashboardRoute = userRole === 'admin' ? '/admin/dashboard' :
                          userRole === 'professional' ? '/profissional/dashboard' :
                          userRole === 'influencer' ? '/influenciador/dashboard' :
                          '/usuario/dashboard';

    navigate(dashboardRoute, { replace: true });
    return;
  }

  // Para usu√°rios comuns, verificar plano ativo
  if (hasActivePlan) {
    console.log('‚úÖ Usu√°rio tem plano ativo, redirecionando para dashboard');
    navigate('/usuario/dashboard', { replace: true });
  } else {
    console.log('‚ö†Ô∏è Usu√°rio sem plano ativo, redirecionando para sele√ß√£o de planos');
    navigate('/plans', { replace: true });
  }
};

import { supabase } from '@/integrations/supabase/client';

export interface UploadResult {
  url: string;
  path: string;
  error?: string;
}

class StorageService {
  // Upload document (PDF, images)
  async uploadDocument(file: File, userId: string): Promise<UploadResult> {
    try {
      const fileExt = file.name.split('.').pop();
      const fileName = `${Date.now()}-${Math.random().toString(36).substring(2)}.${fileExt}`;
      const filePath = `${userId}/${fileName}`;

      // Validate file type
      const allowedTypes = ['image/jpeg', 'image/png', 'image/jpg', 'application/pdf'];
      if (!allowedTypes.includes(file.type)) {
        return { url: '', path: '', error: 'Tipo de arquivo n√£o permitido. Use JPG, PNG ou PDF.' };
      }

      // Validate file size (5MB max)
      if (file.size > 5 * 1024 * 1024) {
        return { url: '', path: '', error: 'Arquivo muito grande. M√°ximo 5MB.' };
      }

      const { data, error } = await supabase.storage
        .from('documents')
        .upload(filePath, file, {
          cacheControl: '3600',
          upsert: false
        });

      if (error) {
        console.error('Upload error:', error);
        return { url: '', path: '', error: error.message };
      }

      // Get public URL
      const { data: urlData } = supabase.storage
        .from('documents')
        .getPublicUrl(filePath);

      return {
        url: urlData.publicUrl,
        path: filePath,
        error: undefined
      };
    } catch (error: any) {
      console.error('Upload service error:', error);
      return { url: '', path: '', error: 'Erro no upload. Tente novamente.' };
    }
  }

  // Upload video
  async uploadVideo(file: File, userId: string): Promise<UploadResult> {
    try {
      const fileExt = file.name.split('.').pop();
      const fileName = `${Date.now()}-${Math.random().toString(36).substring(2)}.${fileExt}`;
      const filePath = `${userId}/${fileName}`;

      // Validate file type
      const allowedTypes = ['video/mp4', 'video/mov', 'video/avi', 'video/quicktime'];
      if (!allowedTypes.includes(file.type)) {
        return { url: '', path: '', error: 'Tipo de v√≠deo n√£o permitido. Use MP4, MOV ou AVI.' };
      }

      // Validate file size (50MB max)
      if (file.size > 50 * 1024 * 1024) {
        return { url: '', path: '', error: 'V√≠deo muito grande. M√°ximo 50MB.' };
      }

      const { data, error } = await supabase.storage
        .from('videos')
        .upload(filePath, file, {
          cacheControl: '3600',
          upsert: false
        });

      if (error) {
        console.error('Video upload error:', error);
        return { url: '', path: '', error: error.message };
      }

      // Get public URL
      const { data: urlData } = supabase.storage
        .from('videos')
        .getPublicUrl(filePath);

      return {
        url: urlData.publicUrl,
        path: filePath,
        error: undefined
      };
    } catch (error: any) {
      console.error('Video upload service error:', error);
      return { url: '', path: '', error: 'Erro no upload do v√≠deo. Tente novamente.' };
    }
  }

  // Upload avatar
  async uploadAvatar(file: File): Promise<UploadResult> {
    try {
      const fileExt = file.name.split('.').pop();
      const fileName = `avatar-${Date.now()}-${Math.random().toString(36).substring(2)}.${fileExt}`;

      // Validate file type
      const allowedTypes = ['image/jpeg', 'image/png', 'image/jpg'];
      if (!allowedTypes.includes(file.type)) {
        return { url: '', path: '', error: 'Tipo de imagem n√£o permitido. Use JPG ou PNG.' };
      }

      // Validate file size (2MB max)
      if (file.size > 2 * 1024 * 1024) {
        return { url: '', path: '', error: 'Imagem muito grande. M√°ximo 2MB.' };
      }

      const { data, error } = await supabase.storage
        .from('avatars')
        .upload(fileName, file, {
          cacheControl: '3600',
          upsert: false
        });

      if (error) {
        console.error('Avatar upload error:', error);
        return { url: '', path: '', error: error.message };
      }

      // Get public URL
      const { data: urlData } = supabase.storage
        .from('avatars')
        .getPublicUrl(fileName);

      return {
        url: urlData.publicUrl,
        path: fileName,
        error: undefined
      };
    } catch (error: any) {
      console.error('Avatar upload service error:', error);
      return { url: '', path: '', error: 'Erro no upload da imagem. Tente novamente.' };
    }
  }

  // Delete file
  async deleteFile(bucket: string, path: string): Promise<boolean> {
    try {
      const { error } = await supabase.storage
        .from(bucket)
        .remove([path]);

      if (error) {
        console.error('Delete error:', error);
        return false;
      }

      return true;
    } catch (error) {
      console.error('Delete service error:', error);
      return false;
    }
  }

  // Get file URL
  getFileUrl(bucket: string, path: string): string {
    const { data } = supabase.storage
      .from(bucket)
      .getPublicUrl(path);

    return data.publicUrl;
  }
}

export const storageService = new StorageService();

