  const [fullName, setFullName] = useState("");
  const [phone, setPhone] = useState("");
  const { login, register } = useAuth();
  const { toast } = useToast();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
      if (isLogin) {
        const { error } = await login(email, password);
        if (error) throw error;

        toast({
          title: "Login realizado com sucesso!",
          description: "Redirecionando para seu painel...",
        });

        // N√£o fazer redirecionamento manual - deixar o AuthContext fazer automaticamente
      } else {
        const userData = {
          email,
          fullName: fullName || "Usu√°rio",
          phone: phone || ""
        };

        const { error } = await register(email, password, userData, "user");
        if (error) throw error;

        toast({
          title: "Cadastro realizado com sucesso!",
          description: "Sua conta foi criada. Redirecionando...",
        });

        // N√£o fazer redirecionamento manual - deixar o AuthContext fazer automaticamente
      }
    } catch (error: any) {
      console.error('QuickLogin error:', error);

      // Mensagens de erro mais espec√≠ficas
      let errorMessage = "Ocorreu um erro. Tente novamente.";

      if (error.message?.includes('Email not confirmed')) {
        errorMessage = "Email n√£o confirmado. Aguarde alguns minutos e tente novamente, ou entre em contato com o suporte.";
      } else if (error.message?.includes('Invalid login credentials')) {
        errorMessage = "Email ou senha incorretos. Verifique seus dados e tente novamente.";
      } else if (error.message?.includes('User not found')) {
        errorMessage = "Usu√°rio n√£o encontrado. Verifique o email digitado.";
      } else if (error.message) {
        errorMessage = error.message;
      }

      toast({
        title: isLogin ? "Erro no Login" : "Erro no Cadastro",
        description: errorMessage,
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
