            </p>
          </div>

          {/* Status Card */}
          <Card className={`mb-8 ${getStatusColor()}`}>
            <CardContent className="text-center py-8">
              <div className="flex justify-center mb-4">
                {getStatusIcon()}
              </div>
              <h2 className="text-xl font-bold mb-2">
                {voucher?.valid ? 'Voucher V√°lido' : 'Voucher Inv√°lido'}
