    const loadData = async (retryCount = 0) => {
      try {
        setLoading(true);

        // Load professionals with retry
        const loadProfessionals = async () => {
          try {
            const { data, error } = await supabase
              .from('profiles')
              .select('*')
              .eq('role', 'professional')
              .order('created_at', { ascending: false });

