import React, { useState, useEffect } from 'react';
import { useAuth } from '@/contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { supabase } from '@/integrations/supabase/client';
import { useToast } from '@/hooks/use-toast';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import SimpleImageUpload from '@/components/SimpleImageUpload';
import { Bell, Camera, Star, Calendar, DollarSign, User, MapPin, CreditCard, Phone, Mail, Instagram } from 'lucide-react';
import Header from '@/components/Header';
import Footer from '@/components/Footer';

interface Professional {
  id: string;
  name: string;
  email: string;
  specialty?: string;
  location?: string;
  phone?: string;
  instagram?: string;
  bank_data?: any;
  rating?: number;
  total_earnings?: number;
  avatar_url?: string;
}

interface Contemplation {
  id: string;
  user_id: string;
  user_name: string;
  entry_date: string;
  referral_count: number;
  service_confirmed: boolean;
  payment_status: 'pending' | 'released' | 'paid';
  before_photos?: string[];
  after_photos?: string[];
}

interface ServiceHistory {
  id: string;
  client_name: string;
  service_date: string;
  amount: number;
  payment_status: 'pending' | 'released' | 'paid';
  rating?: number;
  review?: string;
}

interface Notification {
  id: string;
  message: string;
  type: 'contemplation' | 'payment';
  created_at: string;
  read: boolean;
}

const ProDashboard: React.FC = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const { toast } = useToast();

  const [professional, setProfessional] = useState<Professional | null>(null);
  const [contemplations, setContemplations] = useState<Contemplation[]>([]);
  const [serviceHistory, setServiceHistory] = useState<ServiceHistory[]>([]);
  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedContemplation, setSelectedContemplation] = useState<string | null>(null);
  const [beforePhotos, setBeforePhotos] = useState<string[]>([]);
  const [afterPhotos, setAfterPhotos] = useState<string[]>([]);

  useEffect(() => {
    if (!user || user.role !== 'professional') {
      navigate('/profissional/login');
      return;
    }
    loadDashboardData();
  }, [user, navigate]);

  const loadDashboardData = async () => {
    try {
      setLoading(true);

      // Load professional data
      const { data: profData, error: profError } = await supabase
        .from('profiles')
        .select('*')
        .eq('user_id', user?.id)
        .eq('role', 'professional')
        .single();

      if (profError) throw profError;

      const professionalInfo: Professional = {
        id: profData.id,
        name: profData.full_name || user?.name || '',
        email: profData.email || user?.email || '',
        specialty: 'Especialidade n√£o informada',
        location: profData.phone || 'Localiza√ß√£o n√£o informada',
        phone: profData.phone || '',
        instagram: '',
        bank_data: null,
        rating: 0,
        total_earnings: 0,
        avatar_url: null
      };

      setProfessional(professionalInfo);

      // Carregar contemplations reais
      const { data: contData } = await supabase
        .from('contemplations')
        .select('id, user_id, service_confirmed, payment_status, created_at')
        .eq('professional_id', user.id)
        .order('created_at', { ascending: false });

      const formattedContemplations: Contemplation[] = (contData || []).map((c: any) => ({
        id: c.id,
        user_id: c.user_id,
        user_name: 'Cliente',
        entry_date: c.created_at,
        referral_count: 9,
        service_confirmed: !!c.service_confirmed,
        payment_status: (c.payment_status || 'pending') as any,
        before_photos: [],
        after_photos: []
      }));
      setContemplations(formattedContemplations);

      // Carregar hist√≥rico de servi√ßos
      const { data: histData } = await supabase
        .from('service_history')
        .select('*')
        .eq('professional_id', user.id)
        .order('service_date', { ascending: false });
      const formattedHistory: ServiceHistory[] = (histData || []).map((s: any) => ({
        id: s.id,
        client_name: s.client_name,
        service_date: s.service_date,
        amount: Number(s.amount),
        payment_status: (s.payment_status || 'pending') as any,
        rating: s.rating,
        review: s.review
      }));
      setServiceHistory(formattedHistory);

      // Carregar notifica√ß√µes
      const { data: notifData } = await supabase
        .from('professional_notifications')
        .select('*')
        .eq('professional_id', user.id)
        .order('created_at', { ascending: false });
      const formattedNotifications: Notification[] = (notifData || []).map((n: any) => ({
        id: n.id,
        message: n.message,
        type: n.type,
        created_at: n.created_at,
        read: n.read
      }));
      setNotifications(formattedNotifications);

    } catch (error) {
      console.error('Error loading dashboard data:', error);
      toast({
        title: "Erro ao carregar dados",
        description: "N√£o foi poss√≠vel carregar as informa√ß√µes do painel.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleConfirmService = async (contemplationId: string) => {
    try {
      // Atualiza contempla√ß√£o e cria hist√≥rico
      const { error: updateError } = await supabase
        .from('contemplations')
        .update({ service_confirmed: true, confirmation_date: new Date().toISOString(), payment_status: 'released' })
        .eq('id', contemplationId);
      if (updateError) throw updateError;

      // Buscar pagamento gerado para esta contempla√ß√£o
      const { data: paymentRow } = await supabase
        .from('pagamentos_profissionais')
        .select('id')
        .eq('contemplation_id', contemplationId)
        .order('created_at', { ascending: false })
        .limit(1)
        .single();

      // Chamar edge function para liberar pagamento (redundante, mas mantido para consist√™ncia)
      // @ts-ignore
      const baseUrl = (import.meta as any).env?.VITE_SUPABASE_URL || (window as any)?.ENV?.VITE_SUPABASE_URL;
      const response = await fetch(`${baseUrl}/functions/v1/release-professional-payment`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('sb-access-token') || ''}`
        },
        body: JSON.stringify({ payment_id: paymentRow?.id || contemplationId, admin_id: user?.id })
      });
      if (!response.ok) {
