import React from 'react';

// Performance optimization utilities

// Console log remover for production
export const createLogger = (isDevelopment: boolean) => {
  if (isDevelopment) {
    return {
      log: console.log,
      warn: console.warn,
      error: console.error,
      info: console.info,
      debug: console.debug
    };
  }

  // Production - only critical errors
  return {
    log: () => {},
    warn: () => {},
    error: console.error, // Keep critical errors
    info: () => {},
    debug: () => {}
  };
};

// Timer management for cleanup
export class TimerManager {
  private timers: Set<NodeJS.Timeout> = new Set();
  private intervals: Set<NodeJS.Timeout> = new Set();

  setTimeout(callback: () => void, delay: number): NodeJS.Timeout {
    const timer = setTimeout(() => {
      this.timers.delete(timer);
      callback();
    }, delay);

    this.timers.add(timer);
    return timer;
  }

  setInterval(callback: () => void, delay: number): NodeJS.Timeout {
    const interval = setInterval(callback, delay);
    this.intervals.add(interval);
    return interval;
  }

  clearTimeout(timer: NodeJS.Timeout): void {
    clearTimeout(timer);
    this.timers.delete(timer);
  }

  clearInterval(interval: NodeJS.Timeout): void {
    clearInterval(interval);
    this.intervals.delete(interval);
  }

  cleanup(): void {
    this.timers.forEach(timer => clearTimeout(timer));
    this.intervals.forEach(interval => clearInterval(interval));
    this.timers.clear();
    this.intervals.clear();
  }
}

// Memory leak detector
export const detectMemoryLeaks = (componentName: string) => {
  if (process.env.NODE_ENV !== 'development') return () => {};

  const initialMemory = (performance as any).memory?.usedJSHeapSize || 0;

  return () => {
    const finalMemory = (performance as any).memory?.usedJSHeapSize || 0;
    const memoryDiff = finalMemory - initialMemory;

    if (memoryDiff > 5 * 1024 * 1024) { // 5MB threshold
      console.warn(`Possible memory leak in ${componentName}: ${(memoryDiff / 1024 / 1024).toFixed(2)}MB`);
    }
  };
};

// Debounce function
export function debounce<T extends (...args: any[]) => any>(
  func: T,
  wait: number
): (...args: Parameters<T>) => void {
  let timeout: NodeJS.Timeout;

  return (...args: Parameters<T>) => {
    clearTimeout(timeout);
    timeout = setTimeout(() => func(...args), wait);
  };
}

// Throttle function
export function throttle<T extends (...args: any[]) => any>(
  func: T,
  limit: number
): (...args: Parameters<T>) => void {
  let inThrottle: boolean;

  return (...args: Parameters<T>) => {
    if (!inThrottle) {
      func(...args);
      inThrottle = true;
      setTimeout(() => inThrottle = false, limit);
    }
  };
}

// Component performance tracker
export const performanceTracker = {
  start: (componentName: string) => {
    if (process.env.NODE_ENV === 'development') {
      performance.mark(`${componentName}-start`);
    }
  },

  end: (componentName: string) => {
    if (process.env.NODE_ENV === 'development') {
      performance.mark(`${componentName}-end`);
      performance.measure(
        `${componentName}-duration`,
        `${componentName}-start`,
        `${componentName}-end`
      );

      const measure = performance.getEntriesByName(`${componentName}-duration`)[0];
      if (measure && measure.duration > 16) {
        console.warn(`Performance warning: ${componentName} took ${measure.duration.toFixed(2)}ms`);
      }
    }
  }
};

// Bundle size analyzer
export const analyzeBundleSize = () => {
  if (process.env.NODE_ENV === 'development') {
    // Analyze loaded modules
    const modules = Object.keys(window as any).filter(key => 
      key.startsWith('__webpack') || key.startsWith('webpackChunk')
    );

    console.log('Loaded modules:', modules.length);

    // Memory usage
    if ((performance as any).memory) {
      const memory = (performance as any).memory;
      console.log('Memory usage:', {
        used: `${(memory.usedJSHeapSize / 1024 / 1024).toFixed(2)}MB`,
        total: `${(memory.totalJSHeapSize / 1024 / 1024).toFixed(2)}MB`,
        limit: `${(memory.jsHeapSizeLimit / 1024 / 1024).toFixed(2)}MB`
      });
    }
  }
};

// Lazy component loader with error boundary
export const lazyLoad = <T extends React.ComponentType<any>>(
  importFunc: () => Promise<{ default: T }>,
  fallback: React.ComponentType = () => React.createElement('div', null, 'Loading...')
) => {
  return React.lazy(async () => {
    try {
      const module = await importFunc();
      return module;
    } catch (error) {
      console.error('Failed to load component:', error);
      // Return fallback component on error
      return { default: fallback as T };
    }
  });
};

import { serve } from "https://deno.land/std@0.190.0/http/server.ts";
import { createClient } from "https://esm.sh/@supabase/supabase-js@2.45.0";

const corsHeaders = {
  "Access-Control-Allow-Origin": "*",
  "Access-Control-Allow-Headers": "authorization, x-client-info, apikey, content-type",
};

const logStep = (step: string, details?: any) => {
  const detailsStr = details ? ` - ${JSON.stringify(details)}` : '';
  console.log(`[ASAAS-INTEGRATION] ${step}${detailsStr}`);
};

serve(async (req) => {
  if (req.method === "OPTIONS") {
    return new Response(null, { headers: corsHeaders });
  }

  try {
    logStep("Function started");

    const { action, ...data } = await req.json();
    logStep("Request data received", { action });

    // Initialize Supabase client
    const supabaseClient = createClient(
      Deno.env.get("SUPABASE_URL") ?? "",
      Deno.env.get("SUPABASE_SERVICE_ROLE_KEY") ?? "",
      { auth: { persistSession: false } }
    );

    let result;

    switch (action) {
      case "test_connection":
        result = await testAsaasConnection(data);
        break;

      case "save_configuration":
        result = await saveConfiguration(supabaseClient, data);
        break;

      case "sync_products":
        result = await syncProducts(supabaseClient);
        break;

      case "get_integration_status":
        result = await getIntegrationStatus(supabaseClient);
        break;

      default:
        throw new Error(`A√ß√£o n√£o reconhecida: ${action}`);
    }

    logStep("Operation completed successfully", result);

    return new Response(JSON.stringify(result), {
      headers: { ...corsHeaders, "Content-Type": "application/json" },
      status: 200,
    });

  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : String(error);
    logStep("ERROR in asaas-integration-manager", { message: errorMessage });

    return new Response(JSON.stringify({ 
      success: false, 
      error: errorMessage 
    }), {
      headers: { ...corsHeaders, "Content-Type": "application/json" },
      status: 500,
    });
  }
});

// Testar conex√£o com Asaas
async function testAsaasConnection(data: any) {
  logStep("Testing Asaas connection", { environment: data.environment });

  const baseUrl = data.environment === 'production' 
    ? 'https://www.asaas.com/api/v3'
    : 'https://sandbox.asaas.com/api/v3';

  try {
    // Teste com endpoint de contas/informa√ß√µes b√°sicas
    const response = await fetch(`${baseUrl}/myAccount`, {
      method: 'GET',
      headers: {
        'access_token': data.api_key,
        'Content-Type': 'application/json',
        'User-Agent': 'Asaas Integration Client 1.0'
      },
    });

    logStep("API Response received", { 
      status: response.status, 
      statusText: response.statusText,
      headers: Object.fromEntries(response.headers.entries())
    });

    if (response.ok) {
      const accountData = await response.json();
      logStep("Asaas connection successful", { account: accountData.name || 'Account found' });

      return {
        success: true,
        message: "Conex√£o com Asaas estabelecida com sucesso",
        account_info: {
          name: accountData.name || 'Account validated',
          email: accountData.email || 'N/A',
          environment: data.environment
        }
      };
    } else {
      // Tentar ler o erro espec√≠fico
      let errorText;
      try {
        const errorData = await response.json();
        errorText = errorData.errors ? errorData.errors.map((e: any) => e.description || e.code).join(', ') : JSON.stringify(errorData);
      } catch {
        errorText = await response.text();
      }

      logStep("API Error details", { status: response.status, error: errorText });

      // Tratamento espec√≠fico para erros de autentica√ß√£o
      if (response.status === 401) {
        throw new Error(`API Key inv√°lida ou expirada. Verifique se a chave est√° correta para o ambiente ${data.environment}`);
      } else if (response.status === 403) {
        throw new Error(`Acesso negado. Verifique as permiss√µes da API Key para o ambiente ${data.environment}`);
      } else {
        throw new Error(`Erro na API Asaas: ${response.status} - ${errorText}`);
      }
    }
  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : String(error);
    logStep("Asaas connection failed", { error: errorMessage });

    return {
      success: false,
      error: `Falha na conex√£o: ${errorMessage}`
    };
  }
}

// Salvar configura√ß√£o
async function saveConfiguration(supabaseClient: any, data: any) {
  logStep("Saving configuration", { environment: data.environment });

  try {
    // Criptografar a API key (simula√ß√£o b√°sica - em produ√ß√£o usar crypto adequado)
    const apiKeyEncrypted = btoa(data.api_key);

    // Verificar se j√° existe configura√ß√£o
    const { data: existing } = await supabaseClient
      .from('asaas_integration')
      .select('id')
      .order('created_at', { ascending: false })
      .limit(1)
      .maybeSingle();

    const configData = {
      api_key_encrypted: apiKeyEncrypted,
      environment: data.environment,
      status: 'active',
      connection_status: 'connected',
      error_message: null,
      updated_at: new Date().toISOString()
    };

    let result;
    if (existing) {
      // Atualizar configura√ß√£o existente
      result = await supabaseClient
        .from('asaas_integration')
        .update(configData)
        .eq('id', existing.id)
        .select()
        .single();
    } else {
      // Criar nova configura√ß√£o
      result = await supabaseClient
        .from('asaas_integration')
        .insert({
          ...configData,
          created_at: new Date().toISOString()
        })
        .select()
        .single();
    }

    if (result.error) {
      throw new Error(`Erro ao salvar configura√ß√£o: ${result.error.message}`);
    }

    return {
      success: true,
      message: "Configura√ß√£o salva com sucesso",
      integration_id: result.data.id
    };

  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : String(error);
    logStep("Error saving configuration", { error: errorMessage });

    return {
      success: false,
      error: errorMessage
    };
  }
}

// Sincronizar produtos
async function syncProducts(supabaseClient: any) {
  logStep("Starting product sync");

  try {
    // Buscar configura√ß√£o ativa
    const { data: integration, error: integrationError } = await supabaseClient
      .from('asaas_integration')
      .select('*')
      .eq('status', 'active')
      .eq('connection_status', 'connected')
      .order('created_at', { ascending: false })
      .limit(1)
      .maybeSingle();

    if (integrationError || !integration) {
      throw new Error("Nenhuma integra√ß√£o ativa encontrada");
    }

    // Descriptografar API key
    const apiKey = atob(integration.api_key_encrypted);
    const baseUrl = integration.environment === 'production' 
      ? 'https://www.asaas.com/api/v3'
      : 'https://sandbox.asaas.com/api/v3';

    // Buscar produtos que precisam ser sincronizados
    const { data: products } = await supabaseClient
      .from('products')
      .select('*')
      .eq('active', true)
      .is('asaas_product_id', null);

    const { data: customPlans } = await supabaseClient
      .from('custom_plans')
      .select('*')
      .eq('active', true)
      .is('asaas_product_id', null);

    let syncedCount = 0;

    // Sincronizar produtos
    if (products) {
      for (const product of products) {
        try {
          const asaasProduct = await createAsaasProduct(baseUrl, apiKey, {
            name: product.name,
            description: product.description,
            value: product.price,
            type: 'INSTALLMENT'
          });

          if (asaasProduct.id) {
            await supabaseClient
              .from('products')
              .update({ asaas_product_id: asaasProduct.id })
              .eq('id', product.id);

            syncedCount++;
            logStep("Product synced", { productId: product.id, asaasId: asaasProduct.id });
          }
        } catch (error) {
          logStep("Error syncing product", { productId: product.id, error: error.message });
        }
      }
    }

    // Sincronizar planos customizados
    if (customPlans) {
      for (const plan of customPlans) {
        try {
          const asaasPlan = await createAsaasProduct(baseUrl, apiKey, {
            name: plan.name,
            description: plan.description,
            value: plan.price,
            type: 'INSTALLMENT'
          });

          if (asaasPlan.id) {
            await supabaseClient
              .from('custom_plans')
              .update({ asaas_product_id: asaasPlan.id })
              .eq('id', plan.id);

            syncedCount++;
            logStep("Plan synced", { planId: plan.id, asaasId: asaasPlan.id });
          }
        } catch (error) {
          logStep("Error syncing plan", { planId: plan.id, error: error.message });
        }
      }
    }

    // Atualizar timestamp da √∫ltima sincroniza√ß√£o
    await supabaseClient
      .from('asaas_integration')
      .update({ 
        last_sync_at: new Date().toISOString(),
        updated_at: new Date().toISOString()
      })
      .eq('id', integration.id);

    return {
      success: true,
      message: `Sincroniza√ß√£o conclu√≠da: ${syncedCount} itens sincronizados`,
      synced: syncedCount
    };

  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : String(error);
    logStep("Error in product sync", { error: errorMessage });

    return {
      success: false,
      error: errorMessage
    };
  }
}

// Criar produto no Asaas
async function createAsaasProduct(baseUrl: string, apiKey: string, productData: any) {
  const response = await fetch(`${baseUrl}/subscriptions`, {
    method: 'POST',
    headers: {
      'access_token': apiKey,
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({
      billingType: 'BOLETO',
      cycle: 'MONTHLY',
      name: productData.name,
      description: productData.description,
      value: productData.value
    }),
  });

  if (!response.ok) {
    const errorText = await response.text();
    throw new Error(`Erro ao criar produto no Asaas: ${response.status} - ${errorText}`);
  }

  return await response.json();
}

