import { serve } from "https://deno.land/std@0.190.0/http/server.ts";
import Stripe from "https://esm.sh/stripe@14.21.0";
import { createClient } from "https://esm.sh/@supabase/supabase-js@2.45.0";

const corsHeaders = {
  "Access-Control-Allow-Origin": "*",
  "Access-Control-Allow-Headers": "authorization, x-client-info, apikey, content-type",
};

const logStep = (step: string, details?: any) => {
  const detailsStr = details ? ` - ${JSON.stringify(details)}` : '';
  console.log(`[CONFIRM-MLM-PAYMENT] ${step}${detailsStr}`);
};

serve(async (req) => {
  if (req.method === "OPTIONS") {
    return new Response(null, { headers: corsHeaders });
  }

  try {
    logStep("Iniciando confirma√ß√£o de pagamento MLM");

    const { session_id } = await req.json();
    if (!session_id) throw new Error("session_id √© obrigat√≥rio");

    logStep("Session ID recebido", { session_id });

    // Initialize clients
    const supabase = createClient(
      Deno.env.get("SUPABASE_URL") ?? "",
      Deno.env.get("SUPABASE_SERVICE_ROLE_KEY") ?? "",
      { auth: { persistSession: false } }
    );

    const stripe = new Stripe(Deno.env.get("STRIPE_SECRET_KEY") || "", {
      apiVersion: "2023-10-16",
    });

    // Get Stripe session details
    const session = await stripe.checkout.sessions.retrieve(session_id);
    logStep("Stripe session recuperada", { 
      status: session.payment_status,
      metadata: session.metadata 
    });

    if (session.payment_status !== 'paid') {
      throw new Error("Pagamento n√£o foi confirmado");
    }

    const { 
      user_id, 
      product_id, 
      group_id, 
      referral_code,
      is_group_creator 
    } = session.metadata || {};

    if (!user_id || !product_id || !group_id) {
      throw new Error("Metadados incompletos na sess√£o do Stripe");
    }

    logStep("Processando confirma√ß√£o", {
      user_id,
      product_id,
      group_id,
      is_group_creator: is_group_creator === 'true'
    });

    // Update purchase status
    const { error: purchaseUpdateError } = await supabase
      .from("user_purchases")
      .update({
        status: 'completed',
        stripe_payment_intent_id: session.payment_intent,
        updated_at: new Date().toISOString()
      })
      .eq("stripe_session_id", session_id);

    if (purchaseUpdateError) {
      logStep("Erro ao atualizar purchase", purchaseUpdateError);
      throw purchaseUpdateError;
    }

    // Get current group details
    const { data: group, error: groupError } = await supabase
      .from("groups")
      .select("*")
      .eq("id", group_id)
      .single();

    if (groupError || !group) {
      throw new Error("Grupo n√£o encontrado");
    }

    // Check if user is already a member
    const { data: existingMember } = await supabase
      .from("group_members")
      .select("*")
      .eq("group_id", group_id)
      .eq("user_id", user_id)
      .single();

    if (existingMember) {
      logStep("Usu√°rio j√° √© membro do grupo", { existingMember });
      return new Response(JSON.stringify({
        success: true,
        message: "Usu√°rio j√° √© membro do grupo",
        group_id: group_id
      }), {
        headers: { ...corsHeaders, "Content-Type": "application/json" },
        status: 200,
      });
    }

    // Add user to group
    const position = group.current_count;
    const isCreator = is_group_creator === 'true';

    const { error: memberError } = await supabase
      .from("group_members")
      .insert({
        group_id: group_id,
        user_id: user_id,
        referred_by: isCreator ? null : group.buyer_id,
        position: position,
        payment_id: session.payment_intent
      });

    if (memberError) {
      logStep("Erro ao adicionar membro", memberError);
      throw memberError;
    }

    logStep("Membro adicionado ao grupo", { 
      user_id, 
      group_id, 
      position 
    });

    // If there was a referral, confirm it and update commission
    if (referral_code && !isCreator) {
      const { error: referralUpdateError } = await supabase
        .from("referrals")
        .update({
          status: 'confirmed',
          confirmed_at: new Date().toISOString()
        })
        .eq("referral_code", referral_code)
        .eq("referred_id", user_id)
        .eq("group_id", group_id);

      if (referralUpdateError) {
        logStep("Erro ao confirmar referral", referralUpdateError);
      } else {
        logStep("Referral confirmado", { referral_code });

        // Confirmar comiss√£o do influenciador
        const { error: commissionUpdateError } = await supabase
          .from("influencer_commissions")
          .update({ status: 'confirmed' })
          .eq("referral_code", referral_code)
        // Record influencer conversion and credit transaction
        try {
          await supabase.rpc('record_influencer_conversion', {
            p_referral_code: referral_code,
            p_client_id: user.id,
            p_payment_id: purchase.id,
            p_entry_value: purchase.amount_paid,
            p_product_total_value: null
          });
        } catch (e) {
          console.log('record_influencer_conversion failed', e);
        }

          .eq("client_id", user_id);

        if (commissionUpdateError) {
          logStep("Erro ao confirmar comiss√£o", commissionUpdateError);
        } else {
          logStep("Comiss√£o confirmada");
        }
      }
    }

    // Check if group is now complete (this will be handled by database triggers)
    const { data: updatedGroup } = await supabase
      .from("groups")
      .select("current_count, status")
      .eq("id", group_id)
      .single();

    logStep("Status final do grupo", updatedGroup);

    return new Response(JSON.stringify({
      success: true,
      message: "Pagamento confirmado e usu√°rio adicionado ao grupo",
      group_id: group_id,
      group_status: updatedGroup?.status,
      current_count: updatedGroup?.current_count
    }), {
      headers: { ...corsHeaders, "Content-Type": "application/json" },
      status: 200,
    });

  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : "Erro desconhecido";
    logStep("ERRO", { message: errorMessage });

    return new Response(JSON.stringify({
      success: false,
      error: errorMessage
    }), {
      headers: { ...corsHeaders, "Content-Type": "application/json" },
      status: 400,
    });
  }
});

import { serve } from "https://deno.land/std@0.168.0/http/server.ts"
import { createClient } from 'https://esm.sh/@supabase/supabase-js@2'

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
}

serve(async (req) => {
  // Handle CORS preflight requests
  if (req.method === 'OPTIONS') {
    return new Response(null, { headers: corsHeaders });
  }

  try {
    const supabaseClient = createClient(
      Deno.env.get('SUPABASE_URL') ?? '',
      Deno.env.get('SUPABASE_SERVICE_ROLE_KEY') ?? ''
    )

    const { plan_id, plan_category, user_id, payment_method = 'pix', municipio = 'sao_paulo', intended_leader_id = null } = await req.json()

    console.log('Iniciando cria√ß√£o de pagamento:', { plan_id, plan_category, user_id, payment_method, municipio })

    // Obter configura√ß√£o ativa da integra√ß√£o Asaas
    const { data: asaasConfigs, error: configError } = await supabaseClient
      .from('asaas_integration')
      .select('*')
      .order('created_at', { ascending: false })

    if (configError) {
      console.error('‚ùå Erro ao buscar config Asaas:', configError)
      throw new Error('Erro ao carregar configura√ß√£o Asaas: ' + configError.message)
    }

    if (!asaasConfigs || asaasConfigs.length === 0) {
      throw new Error('Nenhuma configura√ß√£o Asaas encontrada')
    }

    // Buscar primeiro ativo, ou pegar o mais recente
    const asaasConfig = asaasConfigs.find(config => config.status === 'active') || asaasConfigs[0]

    if (!asaasConfig) {
      throw new Error('Configura√ß√£o Asaas n√£o encontrada')
    }

    console.log('‚úÖ Config Asaas encontrada:', {
      id: asaasConfig.id,
      status: asaasConfig.status,
      environment: asaasConfig.environment,
      connection_status: asaasConfig.connection_status
    })

    // **4. BUSCAR PLANO USANDO EDGE FUNCTION UNIFICADA**
    console.log('üîç [PLAN-SEARCH] Buscando plano via unified-plans-loader...')

    const { data: plansResponse, error: plansError } = await supabaseClient.functions.invoke('unified-plans-loader', {
      body: { include_inactive: false, admin_view: false }
    })

    if (plansError || !plansResponse?.success) {
      console.error('‚ùå [PLAN-SEARCH] Erro ao buscar planos:', plansError)
      throw new Error('Erro ao carregar planos dispon√≠veis')
    }

    const foundPlan = plansResponse.plans.find((plan: any) => plan.id === plan_id)

    if (!foundPlan) {
      console.error('‚ùå [PLAN-SEARCH] Plano n√£o encontrado:', { plan_id, plan_category })
      console.log('üìã [PLAN-SEARCH] Planos dispon√≠veis:', plansResponse.plans.map((p: any) => ({ id: p.id, name: p.name, active: p.active })))
      throw new Error(`Plano n√£o encontrado ou inativo: ${plan_id}`)
    }

    if (!foundPlan.active) {
      console.error('‚ùå [PLAN-SEARCH] Plano inativo:', foundPlan)
      throw new Error(`Plano est√° inativo: ${foundPlan.name}`)
    }

    const planData = foundPlan
    const planTableSource = foundPlan.table_source

    console.log('‚úÖ [PLAN-SEARCH] Plano encontrado:', {
      id: planData.id,
      name: planData.name,
      price: planData.price,
      source: planTableSource,
      tipo_transacao: planData.tipo_transacao
    })

    // **DEFINIR VALORES E TIPO DE TRANSA√á√ÉO**
    const entryAmount = planData.price
    const tipoTransacao = planData.tipo_transacao || 'servico'

    console.log('üí∞ [PLAN-VALUES] Valores definidos:', {
      entryAmount,
      tipoTransacao,
      planPrice: planData.price
    })

    // **5. BUSCAR E VALIDAR DADOS DO USU√ÅRIO**
    console.log('üë§ [USER-DATA] Buscando dados do usu√°rio...', user_id)
    const { data: user, error: userError } = await supabaseClient
      .from('profiles')
      .select('*')
      .eq('user_id', user_id)
      .single()

    if (userError || !user) {
      console.error('‚ùå [USER-DATA] Usu√°rio n√£o encontrado:', userError)
      throw new Error('Usu√°rio n√£o encontrado')
    }

    console.log('üîç [USER-DATA] Dados completos do usu√°rio:', user)
    console.log('üîç [CPF-DEBUG] CPF do usu√°rio:', user.cpf, 'Tipo:', typeof user.cpf, 'Existe:', !!user.cpf)

    if (!user.cpf) {
      console.error('‚ùå [USER-DATA] CPF obrigat√≥rio n√£o informado')
      throw new Error('CPF √© obrigat√≥rio para criar pagamentos')
    }

    console.log('‚úÖ [USER-DATA] Usu√°rio validado:', { id: user.id, email: user.email, cpf_provided: !!user.cpf, cpf_value: user.cpf })

    // **6. CALCULAR IMPOSTOS BASEADO NO TIPO DE TRANSA√á√ÉO**
    console.log('üí∞ [TAX-CALC] Calculando impostos para:', tipoTransacao)

    const { data: impostos, error: impostosError } = await supabaseClient
      .rpc('calcular_impostos', {
        valor_base: planData.price,
        tipo: tipoTransacao,
        municipio: 'sao_paulo',
        regime: 'simples_nacional'
      });

    if (impostosError) {
      console.error('‚ö†Ô∏è [TAX-CALC] Erro no c√°lculo de impostos:', impostosError);
      // Continuar sem impostos se c√°lculo falhar
    }

    console.log('üìä [TAX-CALC] Impostos calculados:', impostos);

    // Preparar dados para o Asaas
    const asaasBaseUrl = asaasConfig.environment === 'production' 
      ? 'https://www.asaas.com/api/v3'
      : 'https://sandbox.asaas.com/api/v3'

    const apiKey = atob(asaasConfig.api_key_encrypted) // Descriptografar a chave

    // Primeiro, criar/buscar cliente no Asaas se necess√°rio
    let customerId = null;

    // Tentar buscar cliente existente pelo email
    const customerSearchResponse = await fetch(`${asaasBaseUrl}/customers?email=${encodeURIComponent(user.email)}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'access_token': apiKey
      }
    });

    const existingCustomers = await customerSearchResponse.json();

    if (existingCustomers.data && existingCustomers.data.length > 0) {
      customerId = existingCustomers.data[0].id;
      console.log('Cliente existente encontrado:', customerId);
    } else {
      // Criar novo cliente - CORRE√á√ÉO: Incluir CPF obrigatoriamente
      if (!user.cpf) {
        console.error('‚ùå CPF obrigat√≥rio para criar novo cliente no Asaas');
        throw new Error('CPF √© obrigat√≥rio para criar clientes no Asaas');
      }

      const newCustomerData = {
        name: user.full_name || user.email,
        email: user.email,
        cpfCnpj: user.cpf, // CORRE√á√ÉO: Sempre incluir CPF
        phone: user.phone || undefined
      };

      console.log('üÜï Criando novo cliente com dados:', newCustomerData);

      const createCustomerResponse = await fetch(`${asaasBaseUrl}/customers`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'access_token': apiKey
        },
        body: JSON.stringify(newCustomerData)
      });

      const newCustomer = await createCustomerResponse.json();

      if (!createCustomerResponse.ok) {
        console.error('‚ùå Erro ao criar cliente no Asaas:', newCustomer);
        throw new Error(newCustomer.errors?.[0]?.description || 'Erro ao criar cliente no Asaas');
      }

      customerId = newCustomer.id;
      console.log('‚úÖ Novo cliente criado com sucesso:', customerId);
    }

    // Create Order before provider call to carry order_id in externalReference
    const orderId = crypto.randomUUID()
    const amountCents = Math.round(Number(entryAmount) * 100)

    // Validate intended leader (if provided) exists
    let validatedLeaderId: string | null = null
    if (intended_leader_id) {
      const { data: leaderProfile } = await supabaseClient
        .from('profiles')
        .select('id')
        .eq('id', intended_leader_id)
        .single()
      validatedLeaderId = leaderProfile?.id || null
    }

    // Insert Order with temporary provider_session_id = orderId (updated after ASAAS)
    const { data: orderRecord, error: orderError } = await supabaseClient
      .from('orders')
      .insert({
        id: orderId,
        user_id: user.id, // profiles.id
        plan_id: plan_id,
        intended_leader_id: validatedLeaderId,
        provider_session_id: orderId,
        amount_cents: amountCents,
        currency: 'BRL',
        status: 'pending',
        metadata: { plan_table_source: planTableSource }
      })
      .select()
      .single()

    if (orderError) {
      console.error('‚ùå Erro ao criar Order:', orderError)
      throw new Error('Erro ao criar pedido')
    }

    const externalRef = `order=${orderRecord.id};leader=${validatedLeaderId || ''}`

    const paymentData = {
      customer: customerId,
      billingType: payment_method === 'pix' ? 'PIX' : 'BOLETO',
      value: entryAmount,
      dueDate: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0], // 7 dias
      description: `Entrada do plano: ${planData.name} (10% do valor total)`,
      externalReference: externalRef,
      postalService: false
    }

    // Validar se CPF foi fornecido antes de criar cobran√ßa
    console.log('üîç [CPF-FINAL-CHECK] Verifica√ß√£o final do CPF antes da cobran√ßa:');
    console.log('üîç [CPF-FINAL-CHECK] user.cpf:', user.cpf);
    console.log('üîç [CPF-FINAL-CHECK] typeof user.cpf:', typeof user.cpf);
    console.log('üîç [CPF-FINAL-CHECK] !!user.cpf:', !!user.cpf);
    console.log('üîç [CPF-FINAL-CHECK] user.cpf === null:', user.cpf === null);
    console.log('üîç [CPF-FINAL-CHECK] user.cpf === undefined:', user.cpf === undefined);
    console.log('üîç [CPF-FINAL-CHECK] user.cpf === "":', user.cpf === "");

    if (!user.cpf) {
      console.error('‚ùå CPF/CNPJ n√£o fornecido para cria√ß√£o da cobran√ßa - usu√°rio:', user_id);
      console.error('‚ùå Objeto user completo:', JSON.stringify(user, null, 2));
      throw new Error('Para criar esta cobran√ßa √© necess√°rio preencher o CPF ou CNPJ do cliente.');
    }

    // Criar cobran√ßa no Asaas
    console.log('Criando cobran√ßa com dados:', paymentData);
    console.log('‚úÖ CPF do cliente fornecido:', user.cpf);
    const asaasResponse = await fetch(`${asaasBaseUrl}/payments`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'access_token': apiKey
      },
      body: JSON.stringify(paymentData)
    })

    const asaasResult = await asaasResponse.json()
    console.log('Resposta completa da API Asaas:', JSON.stringify(asaasResult, null, 2));

    if (!asaasResponse.ok) {
      console.error('Erro na API Asaas:', asaasResult)
      throw new Error(asaasResult.errors?.[0]?.description || 'Erro ao criar cobran√ßa')
    }

    console.log('Cobran√ßa criada com sucesso:', asaasResult.id)

    // **BUSCAR QR CODE PIX SE FOR PIX**
    let pixQrCode = null;
    let pixCode = null;

    if (payment_method === 'pix') {
