        const qrCodeResponse = await fetch(`${asaasBaseUrl}/payments/${asaasResult.id}/pixQrCode`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'access_token': apiKey
          }
        });

        if (qrCodeResponse.ok) {
          const qrCodeData = await qrCodeResponse.json();
          pixQrCode = qrCodeData.encodedImage;
          pixCode = qrCodeData.payload;
          console.log('‚úÖ QR Code PIX obtido com sucesso');
        } else {
          console.log('‚ö†Ô∏è QR Code PIX n√£o dispon√≠vel ainda');
        }
      } catch (error) {
        console.log('‚ö†Ô∏è Erro ao buscar QR Code PIX:', error);
      }
    }

    // Salvar transa√ß√£o no banco de dados (legado/compatibilidade)
    const { data: transacaoRecord, error: transacaoError } = await supabaseClient
      .from('transacoes')
      .insert({
        usuario_id: user_id,
        plano_id: plan_id,
        valor: entryAmount,
        tipo_transacao: tipoTransacao,
        status: 'pendente',
        asaas_payment_id: asaasResult.id,
        payment_method: payment_method,
        iss_percentual: impostos?.iss_percentual || 0,
        icms_percentual: impostos?.icms_percentual || 0,
        pis_cofins_percentual: impostos?.pis_cofins_percentual || 0,
        valor_impostos: impostos?.total_impostos || 0,
        valor_liquido: impostos?.valor_liquido || entryAmount,
        municipio_iss: municipio,
        regime_tributario: 'simples_nacional',
        observacoes: `Entrada do plano: ${planData.name} (10% do valor total) - Tipo: ${tipoTransacao}`
      })
      .select()
      .single();

    if (transacaoError) {
      console.error('‚ùå Erro ao salvar transa√ß√£o:', transacaoError);
      throw new Error('Erro ao salvar dados da transa√ß√£o');
    }

    console.log('‚úÖ Transa√ß√£o salva no banco:', transacaoRecord.id);

    // Tamb√©m salvar na tabela payments para compatibilidade
    const { data: paymentRecord, error: saveError } = await supabaseClient
      .from('payments')
      .insert({
        user_id: user_id,
        plan_id: plan_id,
        asaas_payment_id: asaasResult.id,
        amount: entryAmount,
        status: 'pending',
        payment_method: payment_method,
        payment_url: asaasResult.invoiceUrl || asaasResult.bankSlipUrl || null,
        plan_name: planData.name,
        customer_id: customerId,
        due_date: asaasResult.dueDate,
        external_reference: externalRef
      })
      .select()
      .single();

    if (saveError) {
      console.error('‚ùå Erro ao salvar pagamento:', saveError);
      // N√£o falhar se der erro no payments, pois j√° temos na transacoes
    } else {
      console.log('‚úÖ Pagamento salvo no banco:', paymentRecord.id);
    }

    // **RETORNO OTIMIZADO PARA CHECKOUT**
    let redirectUrl = null;

    // Para PIX, usar invoiceUrl se dispon√≠vel
    if (payment_method === 'pix' && asaasResult.invoiceUrl) {
      redirectUrl = asaasResult.invoiceUrl;
    }
    // Para boleto, usar bankSlipUrl
    else if (payment_method === 'boleto' && asaasResult.bankSlipUrl) {
      redirectUrl = asaasResult.bankSlipUrl;
    }
    // Fallback para invoiceUrl gen√©rica
    else if (asaasResult.invoiceUrl) {
      redirectUrl = asaasResult.invoiceUrl;
    }

    // Update order with provider session id (ASAAS payment id)
    const { error: updateOrderSessionError } = await supabaseClient
      .from('orders')
      .update({ provider_session_id: asaasResult.id, metadata: { ...orderRecord.metadata, asaas_payment_id: asaasResult.id } })
      .eq('id', orderRecord.id)

    if (updateOrderSessionError) {
      console.warn('‚ö†Ô∏è Falha ao atualizar provider_session_id do Order:', updateOrderSessionError)
    }

    // Retornar dados otimizados para checkout
    const responseData = {
      success: true,
      transacao_id: transacaoRecord.id,
      payment_id: paymentRecord?.id || null,
      asaas_payment_id: asaasResult.id,
      redirect_url: redirectUrl,
      amount: entryAmount,
      valor_liquido: impostos?.valor_liquido || entryAmount,
      valor_impostos: impostos?.total_impostos || 0,
      tipo_transacao: tipoTransacao,
      plan_name: planData.name,
      status: 'pending',
      // Dados espec√≠ficos do PIX
      qr_code: pixQrCode,
      pix_code: pixCode,
      message: payment_method === 'pix' ? 'PIX gerado com sucesso' : 'Redirecionando para pagamento...'
    };

    console.log('üì§ Retornando dados de checkout:', {
      ...responseData,
      qr_code: pixQrCode ? '[QR_CODE_DATA]' : null,
      pix_code: pixCode ? '[PIX_CODE_DATA]' : null
    });

    return new Response(JSON.stringify(responseData), {
      headers: { ...corsHeaders, 'Content-Type': 'application/json' },
      status: 200,
    });

  } catch (error) {
    console.error('Erro ao criar pagamento:', error)
    return new Response(
      JSON.stringify({ 
        success: false, 
        error: error.message || 'Erro interno do servidor' 
      }),
      {
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        status: 500,
      }
    )
  }
})

