}

async function processGroupJoin(supabase: any, userId: string, serviceId: string, amount: number, referrerId?: string) {
  // Buscar grupo ativo ou criar novo
  let { data: activeGroup } = await supabase
    .from('plan_groups')
    .select('*')
    .eq('service_id', serviceId)
    .eq('status', 'forming')
    .lt('current_participants', 10)
    .order('created_at', { ascending: true })
    .limit(1)
    .single();

  if (!activeGroup) {
    // Criar novo grupo
    const { data: newGroup } = await supabase
      .from('plan_groups')
      .insert({
        service_id: serviceId,
        group_number: Math.floor(Math.random() * 10000),
        target_amount: amount * 10,
        current_amount: 0,
        current_participants: 0,
        max_participants: 10,
        status: 'forming'
      })
      .select()
      .single();

    activeGroup = newGroup;
  }

  // Adicionar participante (ser√° confirmado via webhook)
  await supabase
    .from('group_participants')
    .insert({
      group_id: activeGroup.id,
      user_id: userId,
      amount_paid: amount,
      referrer_id: referrerId,
      status: 'pending'
    });

  return activeGroup;
}

