async function processAutoConversions(supabaseClient: any) {
  logStep("Verificando convers√µes autom√°ticas para cr√©ditos");

  // Buscar grupos com mais de 180 dias que ainda n√£o foram convertidos
  const { data: expiredGroups, error } = await supabaseClient
    .from('groups')
    .select(`
      id,
      user_id,
      paid_amount,
      created_at,
      users (email, full_name)
    `)
    .eq('status', 'forming')
    .lt('created_at', new Date(Date.now() - 180 * 24 * 60 * 60 * 1000).toISOString());

  if (error) {
    logStep("Erro ao buscar grupos expirados", { error: error.message });
    return;
  }

  if (!expiredGroups || expiredGroups.length === 0) {
    logStep("Nenhum grupo para convers√£o autom√°tica encontrado");
    return;
  }

  logStep(`Grupos para convers√£o encontrados`, { count: expiredGroups.length });

  for (const group of expiredGroups) {
    // Verificar se j√° foi convertido
    const { data: existingTransaction } = await supabaseClient
      .from('credit_transactions')
      .select('id')
      .eq('user_id', group.user_id)
      .eq('source', 'initial_payment')
      .eq('related_order_id', group.id)
      .single();

    if (existingTransaction) {
      logStep(`Grupo ${group.id} j√° foi convertido anteriormente`);
      continue;
    }

    // Adicionar cr√©ditos
    try {
      await supabaseClient.functions.invoke('add-user-credits', {
        body: {
          userId: group.user_id,
          amount: group.paid_amount,
          source: 'initial_payment',
          description: `Convers√£o autom√°tica ap√≥s 180 dias - Grupo #${group.id}`,
          relatedOrderId: group.id
        }
      });

      // Atualizar status do grupo
      await supabaseClient
        .from('groups')
        .update({ status: 'expired_converted' })
        .eq('id', group.id);

      logStep(`Convers√£o autom√°tica realizada`, { 
        groupId: group.id, 
        userId: group.user_id, 
        amount: group.paid_amount 
      });

    } catch (conversionError) {
      logStep("Erro na convers√£o autom√°tica", { 
        groupId: group.id, 
        error: conversionError.message 
      });
    }
  }
}

async function notifyAdmins(supabaseClient: any, alerts: UserAlert[]) {
  logStep("Enviando resumo para admins");

  // Buscar admins
  const { data: admins, error } = await supabaseClient
    .from('users')
    .select('id, email')
    .eq('role', 'admin');

  if (error || !admins || admins.length === 0) {
    logStep("Nenhum admin encontrado");
    return;
  }

  const alertsByType = {
    critical: alerts.filter(a => a.daysInactive >= 90 && a.referralCount === 0),
    warning: alerts.filter(a => a.daysInactive >= 30 && a.daysInactive < 90),
    info: alerts.filter(a => a.daysInactive < 30)
  };

  for (const admin of admins) {
    const { error: adminNotificationError } = await supabaseClient
      .from('notifications')
      .insert({
        user_id: admin.id,
        title: `üìä Relat√≥rio de Usu√°rios - ${alerts.length} alertas`,
        message: `Cr√≠ticos: ${alertsByType.critical.length}, Aten√ß√£o: ${alertsByType.warning.length}, Info: ${alertsByType.info.length}`,
        type: alertsByType.critical.length > 0 ? 'warning' : 'info',
        category: 'system',
        read: false,
        action_url: '/admin/relatorios',
        action_text: 'Ver Relat√≥rio'
      });

    if (adminNotificationError) {
      logStep("Erro ao notificar admin", { adminId: admin.id, error: adminNotificationError.message });
    }
  }
}

import { serve } from "https://deno.land/std@0.190.0/http/server.ts";
import Stripe from "https://esm.sh/stripe@14.21.0";
import { createClient } from "https://esm.sh/@supabase/supabase-js@2.45.0";

const corsHeaders = {
  "Access-Control-Allow-Origin": "*",
  "Access-Control-Allow-Headers": "authorization, x-client-info, apikey, content-type",
};

interface PurchaseRequest {
  product_code: string;
  referral_code?: string; // c√≥digo de indica√ß√£o (opcional)
}

const logStep = (step: string, details?: any) => {
  const detailsStr = details ? ` - ${JSON.stringify(details)}` : '';
  console.log(`[MLM-PURCHASE] ${step}${detailsStr}`);
};

serve(async (req) => {
  if (req.method === "OPTIONS") {
    return new Response(null, { headers: corsHeaders });
  }

  try {
    logStep("Iniciando processo de compra MLM");

    // Parse request body
    const { product_code, referral_code } = await req.json() as PurchaseRequest;
    logStep("Request recebido", { product_code, referral_code });

    // Initialize clients
    const supabase = createClient(
      Deno.env.get("SUPABASE_URL") ?? "",
      Deno.env.get("SUPABASE_SERVICE_ROLE_KEY") ?? "",
      { auth: { persistSession: false } }
    );

    const stripe = new Stripe(Deno.env.get("STRIPE_SECRET_KEY") || "", {
      apiVersion: "2023-10-16",
    });

    // Authenticate user
    const authHeader = req.headers.get("Authorization");
    if (!authHeader) throw new Error("Authorization header required");

    const token = authHeader.replace("Bearer ", "");
    const { data: userData, error: userError } = await supabase.auth.getUser(token);
    if (userError) throw userError;

    const user = userData.user;
    if (!user?.email) throw new Error("User not authenticated");
    logStep("Usu√°rio autenticado", { userId: user.id, email: user.email });

    // Get product details
    const { data: product, error: productError } = await supabase
      .from("products")
      .select("*")
      .eq("product_code", product_code)
      .eq("active", true)
      .single();

    if (productError || !product) {
      throw new Error(`Produto n√£o encontrado: ${product_code}`);
    }
    logStep("Produto encontrado", product);

    // Check if user already has an active group for this product
    const { data: existingGroup, error: groupError } = await supabase
      .from("groups")
      .select("*")
      .eq("buyer_id", user.id)
      .eq("product_id", product.id)
      .eq("status", "open")
      .single();

    if (existingGroup) {
      throw new Error("Voc√™ j√° possui um grupo ativo para este produto");
    }

    let groupToJoin = null;
    let isGroupCreator = false;

    // Se tem c√≥digo de indica√ß√£o, tentar entrar em grupo existente
    if (referral_code) {
      logStep("Verificando c√≥digo de indica√ß√£o", { referral_code });

      const { data: existingGroupByCode, error: codeError } = await supabase
        .from("groups")
        .select("*, products(*)")
        .eq("referral_code", referral_code)
        .eq("status", "open")
        .eq("product_id", product.id)
        .single();

      if (existingGroupByCode && existingGroupByCode.current_count < 10) {
        groupToJoin = existingGroupByCode;
        logStep("Grupo encontrado para entrada", { 
          groupId: groupToJoin.id, 
          currentCount: groupToJoin.current_count 
        });
      } else {
        logStep("C√≥digo de indica√ß√£o inv√°lido ou grupo cheio");
      }
    }

    // Se n√£o tem grupo para entrar, criar novo grupo
    if (!groupToJoin) {
      isGroupCreator = true;

      // Generate unique referral code
      let newReferralCode;
      let codeExists = true;

      while (codeExists) {
        newReferralCode = await generateReferralCode();
        const { data: existingCode } = await supabase
          .from("groups")
          .select("id")
          .eq("referral_code", newReferralCode)
          .single();

        codeExists = !!existingCode;
      }

      logStep("Criando novo grupo", { referralCode: newReferralCode });

      const { data: newGroup, error: newGroupError } = await supabase
        .from("groups")
        .insert({
          product_id: product.id,
          buyer_id: user.id,
          referral_code: newReferralCode,
          current_count: 1
        })
        .select()
        .single();

      if (newGroupError) throw newGroupError;
      groupToJoin = newGroup;
      logStep("Novo grupo criado", { groupId: newGroup.id });
    }

    // Create Stripe checkout session
    logStep("Criando sess√£o Stripe", { 
      productName: product.name, 
      entryValue: product.entry_value 
    });

    const session = await stripe.checkout.sessions.create({
      line_items: [{
        price: product.stripe_price_id,
        quantity: 1,
      }],
      mode: 'payment',
      success_url: `${req.headers.get("origin")}/mlm/success?session_id={CHECKOUT_SESSION_ID}`,
      cancel_url: `${req.headers.get("origin")}/mlm/cancel`,
      metadata: {
        user_id: user.id,
        product_id: product.id,
        group_id: groupToJoin.id,
        referral_code: referral_code || "",
        is_group_creator: isGroupCreator.toString()
      }
    });

    // Create purchase record
    const { data: purchase, error: purchaseError } = await supabase
      .from("user_purchases")
      .insert({
        user_id: user.id,
        product_id: product.id,
        group_id: groupToJoin.id,
        stripe_session_id: session.id,
        amount_paid: product.entry_value,
        status: 'pending'
      })
      .select()
      .single();

    if (purchaseError) throw purchaseError;
    logStep("Registro de compra criado", { purchaseId: purchase.id });

    // Se est√° entrando em grupo existente, criar referral record
    if (referral_code && !isGroupCreator) {
      const { error: referralError } = await supabase
        .from("referrals")
        .insert({
          referrer_id: groupToJoin.buyer_id,
          referred_id: user.id,
          group_id: groupToJoin.id,
          referral_code: referral_code,
          status: 'pending'
        });

      if (referralError) logStep("Erro ao criar referral", referralError);

      // Criar comiss√£o de influenciador com c√°lculo correto
      logStep("Criando comiss√£o de influenciador");
      try {
        await supabase.rpc('create_influencer_commission', {
          p_influencer_id: groupToJoin.buyer_id,
          p_client_id: user.id,
          p_product_id: product.id,
          p_referral_code: referral_code,
          p_product_total_value: product.total_price
        });
        logStep("Comiss√£o criada com sucesso");
      } catch (commissionError) {
        logStep("Erro ao criar comiss√£o", commissionError);
      }
    }

    logStep("Processo conclu√≠do com sucesso", { 
      sessionUrl: session.url,
      groupId: groupToJoin.id
    });

    return new Response(JSON.stringify({
      success: true,
      checkout_url: session.url,
      group_id: groupToJoin.id,
      is_group_creator: isGroupCreator
    }), {
      headers: { ...corsHeaders, "Content-Type": "application/json" },
      status: 200,
    });

  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : "Erro desconhecido";
    logStep("ERRO", { message: errorMessage });

    return new Response(JSON.stringify({
      success: false,
      error: errorMessage
    }), {
      headers: { ...corsHeaders, "Content-Type": "application/json" },
      status: 400,
    });
  }
});

async function generateReferralCode(): Promise<string> {
  const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
  let result = '';
  for (let i = 0; i < 8; i++) {
    result += characters.charAt(Math.floor(Math.random() * characters.length));
  }
  return result;
}

import { serve } from "https://deno.land/std@0.190.0/http/server.ts";
import { createClient } from "https://esm.sh/@supabase/supabase-js@2.45.0";
import { crypto } from "https://deno.land/std@0.190.0/crypto/mod.ts";

const corsHeaders = {
  "Access-Control-Allow-Origin": "*",
  "Access-Control-Allow-Headers": "authorization, x-client-info, apikey, content-type",
};

const logStep = (step: string, details?: any) => {
  const detailsStr = details ? ` - ${JSON.stringify(details)}` : '';
  console.log(`[SECURE-PIX-WEBHOOK] ${step}${detailsStr}`);
};

// SECURITY: Verify webhook signature from Asaas
const verifyWebhookSignature = async (
  rawBody: string,
  signature: string | null,
  secret: string
): Promise<boolean> => {
  if (!signature || !secret) {
    logStep("Missing signature or secret");
    return false;
  }

  try {
    const encoder = new TextEncoder();
    const key = await crypto.subtle.importKey(
      "raw",
      encoder.encode(secret),
      { name: "HMAC", hash: "SHA-256" },
      false,
      ["sign"]
    );

    const expectedSignature = await crypto.subtle.sign(
      "HMAC",
      key,
      encoder.encode(rawBody)
    );

    const expectedHex = Array.from(new Uint8Array(expectedSignature))
      .map(b => b.toString(16).padStart(2, '0'))
      .join('');

    const receivedHex = signature.replace('sha256=', '');

    logStep("Signature verification", { 
      expected: expectedHex.substring(0, 10) + "...", 
      received: receivedHex.substring(0, 10) + "..." 
    });

    return expectedHex === receivedHex;
  } catch (error) {
    logStep("Signature verification error", error);
    return false;
  }
};

serve(async (req) => {
  if (req.method === "OPTIONS") {
    return new Response(null, { headers: corsHeaders });
  }

  try {
    logStep("Secure webhook received");

    // Initialize Supabase client
    const supabaseClient = createClient(
      Deno.env.get("SUPABASE_URL") ?? "",
      Deno.env.get("SUPABASE_SERVICE_ROLE_KEY") ?? "",
      { auth: { persistSession: false } }
    );

    // SECURITY: Rate limiting check
    const clientIp = req.headers.get("x-forwarded-for") || req.headers.get("x-real-ip") || "unknown";
    const userAgent = req.headers.get("user-agent") || "unknown";

    // Check rate limit: max 10 webhook requests per minute per IP
    const rateLimitCheck = await supabaseClient.rpc('check_rate_limit', {
      identifier: clientIp,
      action_type: 'webhook_request',
      max_requests: 10,
      window_minutes: 1
    });

    if (!rateLimitCheck.data) {
      logStep("SECURITY: Rate limit exceeded", { ip: clientIp });

      await supabaseClient.from("security_events").insert({
        event_type: "webhook_rate_limit_exceeded",
        ip_address: clientIp,
        user_agent: userAgent,
        details: {
          endpoint: "secure-pix-webhook",
          timestamp: new Date().toISOString()
        }
      });

      return new Response("Rate limit exceeded", { status: 429 });
    }

    // Get raw body for signature verification
    const rawBody = await req.text();

    // SECURITY: Validate JSON format
    let webhookData;
    try {
      webhookData = JSON.parse(rawBody);
    } catch (parseError) {
      logStep("SECURITY: Invalid JSON in webhook", parseError);

      await supabaseClient.from("security_events").insert({
        event_type: "webhook_invalid_json",
        ip_address: clientIp,
        user_agent: userAgent,
        details: {
          error: parseError.message,
          body_length: rawBody.length
        }
      });

      return new Response("Invalid JSON", { status: 400 });
    }

    logStep("Webhook data received", { event: webhookData.event, paymentId: webhookData.payment?.id });

    // SECURITY: Verify webhook signature
    const asaasWebhookSecret = Deno.env.get("ASAAS_WEBHOOK_SECRET");
    const signature = req.headers.get("asaas-signature");

    const isValidSignature = await verifyWebhookSignature(
      rawBody,
      signature,
      asaasWebhookSecret || ""
    );

    if (!isValidSignature) {
      logStep("SECURITY: Invalid webhook signature");

      // Log security event
      await supabaseClient.from("security_events").insert({
        event_type: "invalid_webhook_signature",
        ip_address: req.headers.get("x-forwarded-for") || "unknown",
        user_agent: req.headers.get("user-agent"),
        details: {
          webhook_event: webhookData.event,
          payment_id: webhookData.payment?.id,
          signature_provided: !!signature,
          timestamp: new Date().toISOString()
        }
      });

      return new Response("Unauthorized", { status: 401 });
    }

    logStep("Webhook signature verified successfully");

    // Extract payment information from webhook
    const { event, payment } = webhookData;

    if (!payment || !payment.id) {
      logStep("Invalid webhook data - no payment ID");
      return new Response("Invalid webhook data", { status: 400 });
    }

    // Find payment record using Asaas payment ID
    const { data: paymentRecord, error: paymentError } = await supabaseClient
      .from("payments")
      .select("*")
      .eq("stripe_session_id", payment.id) // Using stripe_session_id field for Asaas ID
      .single();

    if (paymentError || !paymentRecord) {
      logStep("Payment not found in database", { asaasId: payment.id, error: paymentError });
      return new Response("Payment not found", { status: 404 });
    }

    logStep("Payment found", { paymentId: paymentRecord.id, currentStatus: paymentRecord.status });

    // SECURITY: Validate payment amount
    const amountValid = await supabaseClient.rpc('validate_payment_amount', {
      payment_id: paymentRecord.id,
      received_amount: payment.value || payment.amount || 0
    });

    if (!amountValid.data) {
      logStep("SECURITY: Payment amount mismatch", {
        expected: paymentRecord.amount,
        received: payment.value || payment.amount
      });

      // Log security event
      await supabaseClient.from("security_events").insert({
        event_type: "payment_amount_mismatch",
        user_id: paymentRecord.user_id,
        details: {
          payment_id: paymentRecord.id,
          expected_amount: paymentRecord.amount,
          received_amount: payment.value || payment.amount,
          asaas_payment_id: payment.id
        }
      });

      return new Response("Payment amount mismatch", { status: 400 });
    }

    // Create payment validation record
    await supabaseClient.from("payment_validations").insert({
      payment_id: paymentRecord.id,
      asaas_payment_id: payment.id,
      webhook_signature: signature,
      amount_verified: true,
      signature_verified: true
    });

    // Process payment confirmation
    if (event === "PAYMENT_RECEIVED" && payment.status === "RECEIVED") {
      logStep("Processing secure payment confirmation");

      // Update payment status
      const { error: updateError } = await supabaseClient
        .from("payments")
        .update({
          status: "paid",
          paid_at: new Date().toISOString(),
          updated_at: new Date().toISOString(),
        })
        .eq("id", paymentRecord.id);

      if (updateError) {
        logStep("Error updating payment status", updateError);
        throw new Error("Erro ao atualizar status do pagamento");
      }

      // ... rest of payment processing logic (same as before but secure)
      // Get plan details if available
      let planData = null;
      if (paymentRecord.plan_id) {
        const { data: plan, error: planError } = await supabaseClient
          .from("custom_plans")
          .select("*")
          .eq("id", paymentRecord.plan_id)
          .single();

        if (!planError && plan) {
          planData = plan;
        }
      }

      // Add user to MLM network if not already exists
      const { data: existingNetwork, error: networkError } = await supabaseClient
        .from("mlm_network")
        .select("*")
        .eq("user_id", paymentRecord.user_id)
        .single();

      if (networkError && networkError.code === "PGRST116") {
        // User not in network, add them
        const { error: insertNetworkError } = await supabaseClient
          .from("mlm_network")
          .insert({
            user_id: paymentRecord.user_id,
            referred_by_user_id: null,
            status: "active",
          });

        if (insertNetworkError) {
          logStep("Error adding user to MLM network", insertNetworkError);
        } else {
          logStep("User added to MLM network");
        }
      }

      // Process referral commission with validation
      if (paymentRecord.influencer_code) {
        logStep("Processing referral commission", { influencerCode: paymentRecord.influencer_code });

        // Find referrer with rate limiting check
        const { data: referrer, error: referrerError } = await supabaseClient
          .from("mlm_network")
          .select("user_id, total_referrals")
          .eq("referral_code", paymentRecord.influencer_code)
          .single();

        if (!referrerError && referrer) {
          // SECURITY: Validate referral limits (prevent abuse)
          if (referrer.total_referrals > 1000) {
            logStep("SECURITY: Referral limit exceeded", { referrerId: referrer.user_id });

            await supabaseClient.from("security_events").insert({
              event_type: "referral_limit_exceeded",
              user_id: referrer.user_id,
              details: { total_referrals: referrer.total_referrals }
            });
          } else {
            // Process referral normally
            const commissionAmount = paymentRecord.amount * 0.10; // 10% of entry/payment amount

            // Update referred user's network record
            await supabaseClient
              .from("mlm_network")
              .update({
                referred_by_user_id: referrer.user_id,
                updated_at: new Date().toISOString(),
              })
              .eq("user_id", paymentRecord.user_id);

            // Create referral record
            await supabaseClient
              .from("mlm_referrals")
              .upsert({
                referrer_id: referrer.user_id,
                referred_id: paymentRecord.user_id,
                referral_code_used: paymentRecord.influencer_code,
                commission_earned: commissionAmount,
                status: "confirmed",
                confirmed_at: new Date().toISOString(),
              }, {
                onConflict: "referrer_id,referred_id"
              });

            // Create commission record (MLM side)
            await supabaseClient
              .from("mlm_commissions")
              .insert({
                user_id: referrer.user_id,
                source_user_id: paymentRecord.user_id,
                level: 1,
                amount: commissionAmount,
                percentage: 10.0,
                type: "referral",
                status: "pending",
              });

            // Record influencer conversion and pending commission in dedicated tables
            try {
              await supabaseClient.rpc('record_influencer_conversion', {
                p_referral_code: paymentRecord.influencer_code,
                p_client_id: paymentRecord.user_id,
                p_payment_id: paymentRecord.id,
                p_entry_value: paymentRecord.amount,
                p_product_total_value: null
              });
            } catch (e) {
              logStep('record_influencer_conversion failed', { error: String(e) });
            }

            // Also register a credit transaction for referrer (auditable wallet)
            try {
              await supabaseClient
                .from('credit_transactions')
                .insert({
                  user_id: referrer.user_id,
                  type: 'earned',
                  amount: commissionAmount,
                  description: `Comiss√£o de indica√ß√£o (10%) sobre pagamento ${paymentRecord.id}`,
                  source_type: 'referral_bonus',
                  commission_rate: 10,
                  reference_id: paymentRecord.id
                });
            } catch (e) {
              logStep('credit_transactions insert failed', { error: String(e) });
            }

            logStep("Secure referral commission processed");
          }
        }
      }

      // Log successful payment processing
      await supabaseClient.from("security_events").insert({
        event_type: "payment_processed_successfully",
        user_id: paymentRecord.user_id,
        details: {
          payment_id: paymentRecord.id,
          amount: paymentRecord.amount,
          asaas_payment_id: payment.id,
          plan_id: paymentRecord.plan_id
        }
      });

      logStep("Secure payment confirmation completed successfully");
    }

    return new Response(JSON.stringify({ success: true }), {
      headers: { ...corsHeaders, "Content-Type": "application/json" },
      status: 200,
    });

  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : String(error);
    logStep("ERROR in secure-pix-webhook", { message: errorMessage });

    // Log error as security event
    const supabaseClient = createClient(
      Deno.env.get("SUPABASE_URL") ?? "",
      Deno.env.get("SUPABASE_SERVICE_ROLE_KEY") ?? "",
      { auth: { persistSession: false } }
    );

    await supabaseClient.from("security_events").insert({
      event_type: "webhook_processing_error",
      details: { error: errorMessage, timestamp: new Date().toISOString() }
    });

    return new Response(JSON.stringify({ 
      success: false, 
      error: errorMessage 
    }), {
      headers: { ...corsHeaders, "Content-Type": "application/json" },
      status: 500,
    });
  }
});

import { serve } from "https://deno.land/std@0.190.0/http/server.ts";
import { createClient } from "https://esm.sh/@supabase/supabase-js@2.45.0";

const corsHeaders = {
  "Access-Control-Allow-Origin": "*",
  "Access-Control-Allow-Headers": "authorization, x-client-info, apikey, content-type",
};

interface EmailNotificationRequest {
  userId: string;
  type: 'group_complete' | 'referral_success' | 'payment_processed' | 'achievement_unlocked';
  templateData: any;
}

serve(async (req) => {
  if (req.method === "OPTIONS") {
    return new Response(null, { headers: corsHeaders });
  }

  try {
    const { userId, type, templateData }: EmailNotificationRequest = await req.json();

    // Inicializar Supabase client
    const supabaseClient = createClient(
      Deno.env.get("SUPABASE_URL") ?? "",
      Deno.env.get("SUPABASE_SERVICE_ROLE_KEY") ?? ""
    );

    // Buscar dados do usu√°rio
    const { data: userData, error: userError } = await supabaseClient
      .from('users')
      .select('email, full_name')
      .eq('id', userId)
      .single();

    if (userError || !userData) {
      throw new Error("Usu√°rio n√£o encontrado");
    }

    // Preparar conte√∫do do email baseado no tipo
    let subject = "";
    let htmlContent = "";

    switch (type) {
      case 'group_complete':
        subject = "üéâ Seu grupo est√° completo!";
        htmlContent = `
          <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
            <h2 style="color: #2563eb;">Grupo Completo!</h2>
            <p>Ol√° ${userData.full_name},</p>
            <p>Temos uma √≥tima not√≠cia! O grupo <strong>${templateData.groupName}</strong> atingiu o n√∫mero m√°ximo de participantes.</p>
            <p>Em breve voc√™ receber√° mais informa√ß√µes sobre o agendamento do seu servi√ßo.</p>
            <div style="background: #f3f4f6; padding: 20px; border-radius: 8px; margin: 20px 0;">
              <h3>Detalhes do Grupo:</h3>
              <ul>
                <li><strong>Plano:</strong> ${templateData.planName}</li>
                <li><strong>Valor pago:</strong> R$ ${templateData.paidAmount}</li>
                <li><strong>Total de participantes:</strong> ${templateData.totalMembers}</li>
              </ul>
            </div>
            <p>Obrigado por fazer parte do Amigo do Peito!</p>
          </div>
        `;
        break;

      case 'referral_success':
        subject = "üí∞ Nova indica√ß√£o convertida!";
        htmlContent = `
          <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
            <h2 style="color: #059669;">Indica√ß√£o Convertida!</h2>
            <p>Ol√° ${userData.full_name},</p>
            <p>Parab√©ns! Uma das suas indica√ß√µes se converteu em venda.</p>
            <div style="background: #f0fdf4; padding: 20px; border-radius: 8px; margin: 20px 0; border-left: 4px solid #059669;">
              <h3>Detalhes da Comiss√£o:</h3>
              <ul>
                <li><strong>Indicado:</strong> ${templateData.referredName}</li>
                <li><strong>Comiss√£o:</strong> R$ ${templateData.commission}</li>
                <li><strong>Plano:</strong> ${templateData.planName}</li>
              </ul>
            </div>
            <p>Continue compartilhando seus links para ganhar ainda mais!</p>
          </div>
        `;
        break;

      case 'payment_processed':
        subject = templateData.type === 'withdrawal' ? "üí≥ Saque processado" : "üí∞ Comiss√£o recebida";
        htmlContent = `
          <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
            <h2 style="color: #059669;">${templateData.type === 'withdrawal' ? 'Saque Processado' : 'Comiss√£o Recebida'}!</h2>
            <p>Ol√° ${userData.full_name},</p>
            <p>${templateData.type === 'withdrawal' 
              ? `Seu saque de R$ ${templateData.amount} foi processado com sucesso!`
              : `Voc√™ recebeu uma nova comiss√£o de R$ ${templateData.amount}!`
            }</p>
            <div style="background: #f0fdf4; padding: 20px; border-radius: 8px; margin: 20px 0;">
              <h3>Detalhes:</h3>
              <ul>
                <li><strong>Valor:</strong> R$ ${templateData.amount}</li>
                <li><strong>Data:</strong> ${new Date().toLocaleDateString('pt-BR')}</li>
                ${templateData.method ? `<li><strong>M√©todo:</strong> ${templateData.method}</li>` : ''}
              </ul>
            </div>
          </div>
        `;
        break;

      case 'achievement_unlocked':
        subject = "üèÜ Nova conquista desbloqueada!";
        htmlContent = `
          <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
            <h2 style="color: #7c3aed;">Conquista Desbloqueada!</h2>
            <p>Ol√° ${userData.full_name},</p>
            <p>Parab√©ns! Voc√™ desbloqueou uma nova conquista: <strong>${templateData.achievementName}</strong></p>
            ${templateData.bonus ? `
              <div style="background: #faf5ff; padding: 20px; border-radius: 8px; margin: 20px 0; border-left: 4px solid #7c3aed;">
                <h3>B√¥nus Especial!</h3>
                <p>Voc√™ ganhou R$ ${templateData.bonus} de b√¥nus por esta conquista!</p>
              </div>
            ` : ''}
            <p>Continue assim e desbloqueie ainda mais conquistas!</p>
          </div>
        `;
        break;
    }

    // Aqui voc√™ integraria com seu provedor de email (SendGrid, Resend, etc.)
    // Por agora, vamos apenas logar o email que seria enviado
    console.log('Email would be sent:', {
      to: userData.email,
      subject,
      html: htmlContent
    });

    // Registrar notifica√ß√£o no banco
    const { error: notificationError } = await supabaseClient
      .from('notifications')
      .insert({
        user_id: userId,
        title: subject,
        message: `Email enviado: ${subject}`,
        type: 'info',
        read: false,
        category: 'system'
      });

    if (notificationError) {
      console.error('Erro ao criar notifica√ß√£o:', notificationError);
    }

    return new Response(
      JSON.stringify({ success: true, message: "Email notification processed" }),
      {
        headers: { ...corsHeaders, "Content-Type": "application/json" },
        status: 200,
      }
    );

  } catch (error) {
    console.error('Erro ao processar notifica√ß√£o por email:', error);
    return new Response(
      JSON.stringify({ error: error.message }),
      {
        headers: { ...corsHeaders, "Content-Type": "application/json" },
        status: 500,
      }
    );
  }
});

